css:
@namespace url(http://www.w3.org/1999/xhtml);

/* ver1.2.1 */

/*TOPページ*/
@-moz-document url-prefix("http://www.nicovideo.jp"){
#sideContents div[style]{display: none !important;}
#personalFrame,#earthquake{display: none !important;}
#nicoNews {margin: 0 0 8px 0!important;}
#content_360 .mb8p4,#content_624 .pickup_cl {display: none !important;}
}

/*  -- niconicoヘッダー -- */
@-moz-document url-prefix("http://www.nicovideo.jp"),
               url-prefix("http://nicovideo.jp"),
               url-prefix("http://live.nicovideo.jp"),
               url-prefix("http://news.nicovideo.jp"),
               url-prefix("http://seiga.nicovideo.jp"),
               url-prefix("http://jk.nicovideo.jp"),
               url-prefix("http://event.nicovideo.jp"),
               url-prefix("http://com.nicovideo.jp/"){
/*#siteHeader #siteHeaderInner ul li a span{ border-right: none!important;}
#siteHeader .siteHeaderGlovalNavigation li a{ border-right:1px solid #666666!important;}*/
#siteHeader #notify_box_count a span{ border-right: none!important;}

#siteHeader #siteHeaderInner ul li:hover{background:none!important;}
#siteHeader .siteHeaderGlovalNavigation li a span:hover {
    border-right: 1px solid #666666!important;
    text-decoration:underline!important;}
    #siteHeader #siteHeaderLeftMenu a,
#siteHeader #siteHeaderLeftMenu a span:hover{border-right:none!important;}
#siteHeader #siteHeaderInner ul li a span:hover {text-decoration:underline!important;}

#siteHeader #siteHeaderInner .siteHeaderGlovalNavigation li.siteHeaderOther a:hover,
#siteHeader #siteHeaderInner .siteHeaderGlovalNavigation li.siteHeaderOther a span:hover,
#siteHeader #siteHeaderInner .siteHeaderMenuList li.siteHeaderOther a:hover,
#siteHeader #siteHeaderInner .siteHeaderMenuList li.siteHeaderOther span:hover,
#siteHeader #siteHeaderInner .siteHeaderMenuList #siteHeaderUserContainer span:hover,
#notify_box_count span:hover
 {border-right: medium none!important;}
/*#siteHeader #siteHeaderInner .siteHeaderMenuList li.siteHeaderHorizon a span:hover,
#siteHeader #siteHeaderInner .siteHeaderMenuList li.siteHeaderHorizon a:hover,
#siteHeader #siteHeaderInner .siteHeaderMenuList li.siteHeaderHorizon a span,
#siteHeader #siteHeaderInner .siteHeaderMenuList li.siteHeaderHorizon a
{border-left: medium none!important;}*/

#siteHeader #siteHeaderInner .siteHeaderMenuList li.siteHeaderHorizon a:hover span {
    border-left: 1px solid #666666!important;}

#siteHeader .siteHeaderHorizon  span:hover,
#siteHeader a[href="uni_ranking"] span:hover,
#siteHeader .siteHeaderHorizon  span:hover,
#siteHeader a[href="http://www.upload.nicovideo.jp/upload"] span:hover,
#siteHeader a[id="menu-ranking"] span:hover,
#siteHeader a[href="/ranking"] span:hover,
#siteHeader a[href="/create/"] span:hover,
#siteHeader a[href="/my/"] span:hover
{border-right: 1px solid #666666!important;}

/*#siteHeader #notify_box_count a,*/
/*#siteHeader #notify_box_count a:hover #notify_box_count_num,*/
#siteHeader a[title="ランキングを表示します"] span:hover
{border-right: 1px solid #666666!important;}
#siteHeader a[title="ランキングを表示します"] span
{border-left: 1px solid #666666!important;}

#siteHeader #notify_box_count{padding-right:4px!important;}

#siteHeader #notify_box_count #notify_box_count_num{
    background:#666666!important;
    padding: 1px 5px!important;
    margin-bottom:1px!important;
    margin-left:4px!important;
    border: 2px solid #FFFFFF!important;
    border-radius: 50% 50% 50% 50%!important;
    display: inline-block!important;
    font-size: 77%!important;
    font-weight: bold!important;
    line-height: 14px!important;
    margin-bottom: 1px!important;
    margin-right:2px!important;
    margin-left: 4px!important;    
    text-align: center!important;
    vertical-align: middle!important;}
    
#siteHeader #notify_box_count #notify_box_count_num:hover
{text-decoration: none!important;}
#siteHeaderNotification span.siteHeaderNotNotifications{background:#666666!important;}
#siteHeader #siteHeaderInner .siteHeaderMenuList li ul {padding-bottom: 1px!important;}
#siteHeader #siteHeaderInner .siteHeaderMenuList li ul li {margin:-1px 0!important;}

#siteHeader .siteHeaderMenuList a{border-right:medium none!important;}

#siteHeaderNotification .siteHeaderPoint {display: none !important;}

#siteHeader #siteHeaderInner ul.siteHeaderGlovalNavigation li a:hover span,
#siteHeader #siteHeaderInner ul.siteHeaderMenuList li a[title*="ランキング"]:hover span,
#siteHeader #siteHeaderInner ul.siteHeaderMenuList li.siteHeaderHorizon a:hover span,
#siteHeader #siteHeaderInner ul.siteHeaderMenuList li a[href*="upload.nicovideo.jp"]:hover span,
#siteHeader #siteHeaderInner ul.siteHeaderMenuList li a[title*="ランキング"]:hover span,
#siteHeader #siteHeaderInner ul.siteHeaderMenuList li a#menu-ranking:hover span,
#siteHeader #siteHeaderInner ul.siteHeaderMenuList li a[href="uni_ranking"]:hover span,
#siteHeader #siteHeaderInner ul.siteHeaderMenuList li a[href="/create/"]:hover span,
#siteHeader #siteHeaderInner ul.siteHeaderMenuList li a[href="/my/"]:hover span
{border-right:1px solid #666666!important;}

#siteHeader #siteHeaderInner .siteHeaderGlovalNavigation li.siteHeaderOther a:hover span {
    border-right:1px solid #272727!important;}/*←上書きで色を変えてるため最後に記述*/

}

/* -- チャンネルページ -- */
@-moz-document url-prefix("http://ch.nicovideo.jp"){
/*#siteHeader #siteHeaderInner ul li, #siteHeader #siteHeaderInner ul li a {
    color:#000000!important;}*/

#channel_pc_top{display:none!important;}
ul.siteHeaderDropdown{background:none repeat scroll 0 0 #EAE8EB !important;}
#siteHeader #siteHeaderLeftMenuContainer,
#siteHeader #siteHeaderRightMenuContainer{border:1px solid #272727!important;}
#siteHeader #siteHeaderLeftMenuContainer a,
#siteHeader #siteHeaderRightMenuContainer li a,
#siteHeader #siteHeaderRightMenuContainer li dt{color:#272727 !important;}
#siteHeader #siteHeaderRightMenuContainer li p{color:#5f5f5f !important;}
#siteHeader #siteHeaderLeftMenuContainer li a:hover,
#siteHeader #siteHeaderRightMenuContainer li a:hover,
#siteHeader #siteHeaderRightMenuContainer li p:hover{color:#ffffff !important;}
#siteHeader #siteHeaderRightMenuContainer li:hover,
#siteHeader #siteHeaderRightMenuContainer a:hover,
#siteHeader #siteHeaderRightMenuContainer dd:hover,
#siteHeader #siteHeaderRightMenuContainer dt:hover{background:none !important;
                                                   text-shadow:none!important;}
                                                   
#userContainer span:hover{text-decoration: underline!important;}
#siteHeader {background: none repeat scroll 0 0 #272727!important;}

li.menuServiceList:hover{background-color:#EAE8EB !important;}

#siteHeader #siteHeaderInner ul li a.siteHeaderPoint{display: none !important;}
}

/*　マイページ　*/
@-moz-document url-prefix("http://www.nicovideo.jp/my"){
.userDetail #juminhyo{display:none!important;}

}

/*  総合  */
@-moz-document domain("nicovideo.jp"){
#zero_lead_index,#zero_lead{display:none!important;}
.siteHeaderNicopo,.siteHeaderTest2{display: none !important;}
#siteHeaderNotification span.siteHeaderNotNotifications{
background:#666666!important;
border-radius: 50%!important;
text-shadow: 1px 1px 0 #000000!important;
}
}
