@-moz-document domain("hattrick.org") {

#page
{
	margin-top: 10px !important;
}
#top
{
	height: 0px !important;
	position: relative !important;
	z-index: 50 !important;
    background: #000000 !important;
}
#footer a
{
        color: #d9291a!important;
        font-weight: bold !important;
}
#ticker
{
	/*position: absolute !important;   
	top: 30px !important;
	left: 360px !important;*/
	width: 350px !important;
	margin-top: 20px !important;
	margin-left: 20px !important;
}
#ticker div a
{
	padding-bottom: 2px !important;
	color: white !important;
	text-decoration: none !important;
	line-height: 1.1em !important;
}
#shortcuts
{
	margin-top: 10px !important;
	margin-left:0px !important;
}
#teamLinks
{
    color: white !important;
    margin-top: 100px !important;
	margin-left: 160px !important;
}
div#teamLinks a
{
     color: white !important;
}
#ft_rapidid {  margin-top:70px !important; }
#ft_rapidid a { color: white !important;}
#ft_rapidid_form {margin-top:-5px !important; }
/*--Users online - week - date - time ----------------------------*/
div[id="online"] {color: white !important; margin-top: 70px !important;}
div[id="time"] {color: white !important; margin-top: 70px !important;}

/*--Page background-----------------------------------------------*/
body 
{
background: #d9291a !important; /* Old browsers */
background: -moz-linear-gradient(top,  #d9291a 0%, #d9291a 50%, #ffffff 100%) !important; /* FF3.6+ */
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#d9291a), color-stop(50%,#d9291a), color-stop(100%,#ffffff)) !important; /* Chrome,Safari4+ */
background: -webkit-linear-gradient(top,  #d9291a 0%,#d9291a 50%,#ffffff 100%) !important; /* Chrome10+,Safari5.1+ */
background: -o-linear-gradient(top,  #d9291a 0%,#d9291a 50%,#ffffff 100%) !important; /* Opera 11.10+ */
background: -ms-linear-gradient(top,  #d9291a 0%,#d9291a 50%,#ffffff 100%) !important; /* IE10+ */
background: linear-gradient(to bottom,  #d9291a 0%,#d9291a 50%,#ffffff 100%) !important; /* W3C */
filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#d9291a', endColorstr='#ffffff',GradientType=0 ) !important; /* IE6-9 */

}

/*----centre title-------("Offizielle Hattrick-Mitteilungen")--------------*/
h1 
{
        color: #d9291a !important;
        border-bottom: solid thin #d9291a!important;
}
/*-----all boxHead--------------------*/
h2 
{
        color: #fefefe !important;
        background-color: #d9291a !important;
        border-radius: 4px 4px 0 0 !important;
}
/*-----centre news--------------------*/
.boxBody h2 
{ 
        color: #d9291a !important;
        background: none !important;
        border-bottom: solid 1px #d9291a !important;
}
/*-----------boxHeads----------------------------------------------------------------------------------------*/

.sidebarBox .boxHead .boxLeft, .subMenuBox .boxHead .boxLeft, .main .boxHead .boxLeft
{
        background: none !important;
}
.sidebarBox .boxHead, .subMenuBox .boxHead, .main .boxHead { background: none !important; border-width:0px !important;}
.sidebarBox .boxFooter .boxLeft,.subMenuBox .boxFooter .boxLeft,.main .boxFooter .boxLeft{ background: none !important; }
.sidebarBox .boxBody,.subMenuBox .boxBody,.main .boxBody{ border-left: 1px solid #d9291a !important; border-right: 1px solid #d9291a !important; }

/*Left menu*/
.subMenuBox .boxHead
{
        color: black !important;
	border-radius: 5px 5px 0 0 !important;
}
.subMenuBox .boxHead .boxLeft
{
        color: black !important;
        }
/*Centre box*/
.main .boxHead h2 
{
        color: black !important;
}
/*Right menu*/
.sidebarBox h2 
{
        color: #fefefe !important;
}

.boxFooter
{
	background: white !important;
	border-radius: 0 0 4px 4px !important;
	border-width: 0px !important;
	border-left: 1px solid #d9291a !important;
	border-right: 1px solid #d9291a !important;
	border-bottom: 1px solid #d9291a !important;
}
/*----- Press, GB, Team events ------------------------------------------------------------------------------*/
.main .mainBox h2 
{
	font-size: 14px !important;
	font-weight: bold !important;
	padding: 1px !important;
}
/*Central header paths*/
h2 a 
{
        color: #fefefe !important;
}
h2 a:hover 
{
        color: white !important;
}
/*News and team events date*/
.date
{
	color: #808080 !important;
	padding-right: 8px;
	font-weight: 400;
}
/*Left Menu titles*/
h3 
{
        color: #d9291a !important;
        border-bottom: solid thin #d9291a !important;
}
/*General Links*/
a
{
    color: #d9291a !important;
	/*text-decoration: none !important;*/
}
/*----subMenu and sidebar links------------------------------------------------------------------------------*/
.subMenuBox a, .subMenuBox a
{
    /*text-decoration: none !important;*/
	color: black !important;
}
.sidebarBox a 
{
        text-decoration: underline !important;
	color: black !important;
}
/*Bot teams link*/
div a[class="shy"] 
{
        color: gray !important;
}
/*Hide Hattrick logo*/
#logo
{
        background: none !important;
}
/*Hide ticker's stars*/
#ticker div
{
        /*background: none !important;*/
}
/* central page background */
#content
{
background: #ffffff !important; /* Old browsers */
background: -moz-linear-gradient(top,  #ffffff 0%, #ffffff 33%, #d9291a 100%) !important; /* FF3.6+ */
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#ffffff), color-stop(33%,#ffffff), color-stop(100%,#d9291a)) !important; /* Chrome,Safari4+ */
background: -webkit-linear-gradient(top,  #ffffff 0%,#ffffff 33%,#d9291a 100%) !important; /* Chrome10+,Safari5.1+ */
background: -o-linear-gradient(top,  #ffffff 0%,#ffffff 33%,#d9291a 100%) !important; /* Opera 11.10+ */
background: -ms-linear-gradient(top,  #ffffff 0%,#ffffff 33%,#d9291a 100%) !important; /* IE10+ */
background: linear-gradient(to bottom,  #ffffff 0%,#ffffff 33%,#d9291a 100%) !important; /* W3C */
filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#ffffff', endColorstr='#d9291a',GradientType=0 ) !important; /* IE6-9 */
/*padding-top:21px !important;*/


}
/*Image header (banner)*/
#header 
{
	/*height: 155px !important;*/
	/*padding-top: 20px !important;*/
	background: url("http://i586.photobucket.com/albums/ss302/SGDGizzly/HT-Skin/BayernMuenchen/header6.png") repeat scroll 0 -15px transparent  !important;
	border-radius: 20px 20px 0 0 !important;
}
/*--------Top menu settings----------------------------------------------------------------------------------*/
#menu
{
	width: 981px !important;
	margin-top: 0px !important;
	margin-left: -14px !important;
	margin-bottom: -7px !important;
	padding-left: 0px !important;
	background: #ffffff !important;
	position: absolute !important;
	border-radius: 0px !important;
}
#menu a
{
	line-height: 30px !important;  
    color: #d9291a!important;
	margin: 0px !important;
}
#menu > a.logout {margin-right:0px !important;}
div#menu a:hover 
{
        color: #ffffff!important; 
        background: #d9291a !important;
}
a:hover
{
	color: #196aab !important;
}
/*bottom white line*/
div[id="bottom"]
{
    background: #d9291a !important;
	border-radius: 0 0 20px 20px !important;
}

/*---Aufstellung---------------------------------------------------------*/
div[class="name"] a       {color: #000000 !important;}
div[class="name"] a:hover {color: #d9291a !important;}

/*---Forum---------------------------------------------------------------*/
div[class="boxBody"]   {background: white !important;}
div[class="message"] a {color: #d9291a !important;}
div[class="signature"] a {color: #d9291a !important;}

.cfHeader
{
	background: none repeat scroll 0 0 #d9291a !important;
}
#cfPollHeader
{
	background: black !important;
	padding: 5px 10px;
	font-weight: bold;
        color: #ffffff;
}
.cfWrapper
{
	border: solid 1px #d9291a !important;
	margin: 0 0 12px 0;
}
.cfWrapper a
{
        color: #fefefe !important;
}
.cfUserInfo a
{
        color: #d9291a !important;
}
.folderHeader a:link 
{
	color: #D11E1A !important;
	font-weight: bold !important;
}
.cfFooter a
{ 
        color: black !important;
}
.folderItem div.ignore
{
        cursor: pointer;
        width: 10px;
        height: 10px;
}
.folderItem div.unignore
{
        cursor: pointer;
        width: 10px;
        height: 10px;
}
.folderItem div.unignore:hover
{

}
/*Ads section*/
div[class="insertHeader"]
{

}
div[class="insertFooter"]
{
        background: #ffffff !important;
}
/*-------------Formatting buttons----------------------------------------------------------------------------*/

/*------Foxtrick popup-------*/
ul.ft-popup-list {background-color: black !important; border: solid 1px white !important; }
ul.ft-popup-list > li > a,  ul.ft-popup-list > li > span {	color: white !important; }
ul.ft-popup-list > li > a:hover {color: #d9291a !important; background-color: #fefefe !important; }
ul.ft-popup-list > li:hover {background-color: #fefefe !important;}

/*------lineup-order-----*/
#order_tabs a {color:#fefefe !important;}
#order_tabs a:hover {color:#d9291a !important;}
#orders .pos_abbr { color: #ffffff !important;}
#orders .position {border: 2px solid #FFFFFF !important;}
#orders .position .player, .player.ui-draggable-dragging {border: 2px solid #FFFFFF !important;}
/*#orders #players .player.used {background: none repeat scroll 0 0 #d9291a !important;}*/
/*#orders #players .player.bench {background: none repeat scroll 0 0 #3e8fd4 !important;}*/
/*#orders .player {background: none repeat scroll 0 0 #d9291a !important;}*/
#orders .helptext { color: #ffffff !important;}
.field
{
    background-image: url("http://i586.photobucket.com/albums/ss302/SGDGizzly/HT-Skin/field_blue.png")  !important;
}

#tab_order 
{
    background-image: url("http://i586.photobucket.com/albums/ss302/SGDGizzly/HT-Skin/field_blue.png")  !important;
}

#orders .position, #formations 
{
    background-color:transparent!important;
}


/*------MyOffice---------*/
.eventCalendarDate.selectedEventDate {background-color:#fefefe !important; border-color: #d9291a !important; color: #d9291a !important;}
.eventItem { background-color:#fefefe !important; border:1px solid #d9291a !important;}
.eventItem > .eventItemText {color: #d9291a !important;}


.alert, .edit, .kitEdit, .alert_top  {border: 1px solid #d9291a !important; }
/*shortcuts*/
img.scNewMail 
{

}

.sidebarDiscountBox .boxBody,.sidebarDiscountBox .boxHead h2 { border-left: 1px solid #d9291a !important;border-right: 1px solid #d9291a !important;}
}

.ft-match-report-lineup, .ft-match-report-match-report, .ft-match-report-kick-off, .ft-match-report-half-time, .ft-match-report-full-time, .ft-match-report-extra-time, .ft-match-report-penalty-shoot-out, .ft-match-report-result
{
	background-color: #d9291a !important;
}

#stageLogo {display: none !important;}

.currentServer { color: #FFFFFF !important; }
