/*
	==== MyAnimeList.net - Night Theme by Zealotus ====
	Once based on MyAnimeList.net Dark Theme (styleID:5263) by Mathes (UserID:6092)
	But a lot has changed since then ;)

	==== Bookmarks ====
	[Z-RplImg] Bookmark for replacement images and icons.
	[Z-ColFix] Bookmark for my colour fixes.
	[Z-PbrRpl] Bookmark for "progress" bars replacement colours.

	==== Full Change Log ====
	3.0.0  - Rewrite! Colour changes only!
	3.0.1  - Release! + Fixed site header.
	3.1.1  - New Layout, WOO. . . *headache!* >.<;;
	3.1.2  - Minor tweaks.
	3.1.3  - Fixed the new divs.
	3.1.4  - Fixed the borked Nav.
	3.1.5  - Fixed status bar colours.
	3.1.7  - Fixed #rightcontentunder.
	3.1.8  - Includes Ad Blocker (again).
	3.1.9  - Fixed the friends list.
	3.1.10 - Fixed compatibility borders.
	3.1.11 - RegExp site selector courtesy of Cookie338.
	3.2.1  - Adapted the style to "recent" changes.
	3.2.2  - Minor tweaks.
	3.2.3  - Fixed white content background.
	3.3.1  - Fixed Profile, Seasonal Anime and Watch Anime pages.
	3.3.2  - Fixed a few more issues...
	==== End Change Log ====

	==== Credits & Special Thanks ====
	Mathes - For the original theme of which this has be built upon.
	Cookie338 - RegExp Site Selector.
	And everyone in the MAL Theme Club.
*/
@namespace url("http://www.w3.org/1999/xhtml");
@-moz-document regexp(".*myanimelist\\.net/(?!animelist|mangalist).*"){*{/*$*/}
	/* Global Defaults */
	body, #myanimelist, #contentWrapper, #content, #fancy_bg, #footer-block {
		color:					#DDD !important;
		background:				#111 !important;
		margin:					0 auto !important;
	}
	.button, .page-common .btn-form-submit {
		color: AliceBlue !important;
	}
	.page-common .inputButton {
		background-color: #458 !important;
		color: AliceBlue !important;
	}
	.page-common .h1 {
		background-color: #458 !important;
		border: 1px solid #458 !important;
		color: AliceBlue !important;
	}
	#footer-block .footer-link.home {
		border-right:			1px #458 solid !important;
	}
	#content, #contentHome, .container, #rightcontentunder {
		border:					0 !important;
		background:				transparent !important;
	}

	/* Headers */
	#header { display: none !important; } /* Oh my... Random one-liner! :o */
	#headerSmall {
		background:				transparent url(data:image/png;base64,) no-repeat scroll 0px 1px !important;
	}
	.inboxsent_header {
		border-color:			#444 !important;
		background-color:		#458 !important;
		color:					#030303 !important;
	}
	.normal_header {
		border-color:			#333 !important;
		color:					#458 !important;
	}

	/* * Navigator * */
	/* Main */
	#nav li a, #nav a:visited {
		color:					#458 !important;
		background:				#111 !important;
	}
	#nav li a:hover, #nav a:active, #nav li.hover a {
		color:					#DDD !important;
		background:				#111 !important;
	}
	#nav li .new a, #nav li .new a:visited, #nav li .new a:active, #nav li .new a:hover, .count {
		font-weight:			bold !important;
		color:					#F33 !important;
	}
	/* Sub */
	#nav ul a, #nav ul a:visited {
		color:					#DDD !important;
		background:				#111 !important;
		border-width:			0px 1px 1px 1px !important;
		border-color:			#458 !important;
		border-style:			solid !important;
	}
	.header-menu-profile li {
		border: #458 1px solid !important;
  	border-bottom:				none !important;
	}
	.header-menu-profile li:last-of-type {
		border-bottom:			#458 1px solid !important;
	}
	.header-menu-profile li a {
		background-color:		#111 !important;
		color:					#458 !important;
	}
	.header-menu-profile li a:hover {
		background-color:		#458 !important;
		color:					#DDD !important;
	}
	#nav>li>ul {
		border-top:				1px #458 solid !important;
	}
	#nav ul a:hover, #nav ul a:active {
		background:				#458 !important;
	}
	#menu_left, #menu, #menu_right, #menu_left #nav>li>ul>li>span a:hover {
		background:				#111 !important;
	}
	.navi-seasonal {
		background-color: #111 !important;
	}
	.navi-seasonal .horiznav_nav ul li a:hover, .navi-seasonal .horiznav_nav ul li a.on, .navi-seasonal .horiznav_nav ul li a.horiznav_active, .navi-seasonal .horiznav_nav ul li a {
		background-color: #222 !important;
	}
	.navi-seasonal .horiznav_nav ul li a.navtab {
		border-color: #222 !important;
	}
	.navi-seasonal .horiznav-nav-seasonal, .navi-seasonal .horiznav_nav {
		border-bottom: 1px solid #458 !important;
	}
	.navi-seasonal .horiznav-nav-seasonal .btn-seasonal .btn-type:hover, .navi-seasonal .horiznav-nav-seasonal .btn-seasonal .btn-type.on {
		background-color: #458 !important;
		color: AliceBlue !important;
	}
	.navi-seasonal .horiznav-nav-seasonal .btn-seasonal .btn-type {
		background-color: #222 !important;
	}
	.seasonal-anime-list .anime-header {
		background-color: #458 !important;
		color: AliceBlue !important;
		font-weight: bold !important;
		border: 1px solid #458 !important;
	}
	.seasonal-anime-list .seasonal-anime, .watch .watch-anime-list .watch-anime {
		border: 1px solid #222 !important;
	}
	.seasonal-anime-list .seasonal-anime .prodsrc, .watch .watch-anime-list .watch-anime .prodsrc {
		background-color: #222 !important;
	}
	.prodsrc .eps, .prodsrc .source {
		border-left: 1px solid #444 !important;
	}
	.seasonal-anime-list .seasonal-anime .genres, .watch .watch-anime-list .watch-anime .genres {
		background-color: #222 !important;
		border-top: 2px solid #444 !important;
		border-bottom: 2px solid #444 !important;
	}
	.genres .genre, .user-profile .user-compatability-graph .bar-outer {
		background-color: #222 !important;
		border-color: #333 !important;
	}
	.genres .genre a, .genres span.genre {
		color: #458 !important;
	}
	.seasonal-anime-list .seasonal-anime .information {
		border-top: 1px solid #333 !important;
	}
	.seasonal-anime-list .seasonal-anime .information .info, .seasonal-anime-list .seasonal-anime .information .scormem {
		background-color: #111 !important;
	}
	.watch .watch-anime-list .watch-anime .image .info {
		background-color: #111 !important;
		border-top: 1px solid #444 !important;
	}
	.watch .watch-anime-list .watch-anime .information .scormem {
		background-color: #111 !important;
		border-top: 1px solid #333 !important;
		width: 167px;
	}
	.seasonal-anime-list .seasonal-anime .information .scormem .score::after, .seasonal-anime-list .seasonal-anime .information .scormem .member::after, .watch .watch-anime-list .watch-anime .information .scormem .score::after, .watch .watch-anime-list .watch-anime .information .scormem .member::after {
		background-image: url("data:image/png;base64,")
	}

	/* Input Text Fields */
	.inputtext, .textarea {
		border-color:			#333 !important;
		background-color:		#1F1F1F !important;
		color:					#AAA !important;
	}
	.inputtext:hover, .textarea:hover  {
		background-color:		#090909 !important;
	}

	.inputButton, .button {
		border-top-color:		#666 !important;
		border-left-color:		#666 !important;
		border-bottom-color:	#222 !important;
		border-right-color:		#222 !important;
		background-color:		#236 !important;
	}

	h1 {
		border-top:				0 !important;
		border-left:			0 !important;
		border-right:			0 !important;
		border-bottom:			solid 1px #458 !important;
		background:				#222 !important;
		color:					#458 !important;
	}

	h2 {
		color:					#458 !important;
		border-bottom:			solid 1px #333 !important;
	}

	.mini_header {
		background-color:		#222 !important;
		color:					#458 !important;
		border-bottom:			solid #458 1px !important;
	}

	/* Links and Texts */
	a,a:visited {
		color:					#458 !important;
	}
	a:focus {
		outline:				none !important;
	}
	a:hover {
		color:					#67A !important;
		text-decoration:		none !important;
	}
	.lightLink, .lightLink:link, .lightLink:visited, .lightLink:active {
		color:					#666 !important;
	}
	.dark_text, .dark_text:hover,.dark_text:visited , .dark_text_normal, .dark_text_normal:hover,.dark_text_normal:visited  {
		color:					#236 !important;
	}
	.bgColor1 .dark_text, .bgColor1 .dark_text:hover, .bgColor1 .dark_text:visited, .bgColor1 .dark_text_normal,
		.bgColor1 .dark_text_normal:hover, .bgColor1 .dark_text_normal:visited  {
		color:					#458 !important;
	}
	.colored_text {
		color:					#333 !important;
	}

	/* Horizontal Navs */
	#horiznav_nav {
		border-color:			#458 !important;
	}
	#horiznav_nav li {
		background-color:		#111 !important;
	}
	#horiznav_nav ul li a {
		color:					#458 !important;
		background-color:		#222 !important;
	}
	#horiznav_nav ul li a:hover {
		background-color:		#458 !important;
		color:					#AAA !important;
	}
	#horiznav_nav ul li .horiznav_active {
		background-color:		#458 !important;
		color:					#AAA !important;
	}

	/* Results */
	.goodresult {
		color:					#AAA !important;
		background-color:		#121 !important;
		border-color:			#4A4 !important;
	}
	.goodresult a:link,.goodresult a:active,.goodresult a:visited {
		color:					#347 !important;
	}
	.badresult {
		color:					#AAA !important;
		background-color:		#211 !important;
		border-color:			#A44 !important;
	}
	.badresult a:link,.badresult a:active,.badresult a:visited  {
		color:					#347 !important;
	}

	/* Profile */
	.page-common .content-container .container-left {
		border-right: 1px	solid #333 !important;
	}
	.user-profile .user-function .icon-user-function.icon-comment, .user-profile .user-function .icon-user-function.icon-message, .user-profile .user-function .icon-user-function.icon-request,.user-profile .user-function .icon-user-function.icon-remove {
		border: 1px solid #444 !important;
	}
	.user-profile .user-function .icon-user-function {
		background-color: #333 !important;
	}
	.user-profile .user-status.border-top {
		border-top: none !important;
	}
	.user-profile .user-status li, .table-recently-updated tr td, .episode_list .episode-list-data td, body.news .menu-category .btn-edit-tags, body.news .news-content-block.news-tags .news-tags-table tr {
		background-color: #222 !important;
	}
	.user-profile .user-status li:nth-of-type(2n), .table-recently-updated tr:nth-of-type(2n) td, .episode_list .episode-list-data:nth-of-type(2n+1) td, body.news .news-content-block.news-tags .news-tags-table tr:nth-of-type(2n+1) {
		background-color: #333 !important;
	}
	.episode_list .episode-list-header td, .episode_list .episode-list-header td:last-of-type {
		background-color: #333 !important;
		color: AliceBlue !important;
		border-color: #444 !important;
	}
	body.news .content-left, body.news .menu-category .btn-category, body.news .news-side-block .header, body.news .news-side-block .news-list .news, body.news .news-side-block .news-list .featured, body.news .news-side-block .featured-list .news, body.news .news-side-block .featured-list .featured, body.news .featured-side-block .news-list .news, body.news .featured-side-block .news-list .featured, body.news .featured-side-block .featured-list .news, body.news .featured-side-block .featured-list .featured, body.news .featured-side-block .header, .episode_list td, body.news .menu-category, .news-list .comment-list, .news-list .news-unit, body.news .tag-side-block .header, body.news .news-content-block.news-tags h2.news-tags-header, body.news .news-breadcrumbs {
		border-color: #444 !important;
	}
	body.news .menu-category .btn-category.selected {
		border-color: #458 !important;
	}
	body.news .news-content-block.news-tags .news-tags-table, body.news .news-content-block.news-tags .news-tags-table td {
		border: 1px solid #444 !important;
	}
	.user-profile .user-button .btn-profile-submit {
		color: AliceBlue !important;
	}
	.user-profile h4, .page-common .content-container .container-right h5 {
		border-bottom: 1px	solid #333 !important;
	}
	.profile .user-statistics .stats-graph, .profile .user-statistics .stats-graph .graph.plantowatch, .profile .user-statistics .stats-graph .graph.plantoread, .profile .statistics-updates .data .graph {
		border: 1px	solid #333 !important;
		border-radius: 0 !important;
		background-color: #222 !important;
		padding: 1px !important;
	}
	.profile .statistics-updates .data .graph .graph-inner {
		border-radius: 0 !important;
	}
	.profile_leftcell {
		border:					0 !important;
	}
	#profileRows a {
		color:					#458 !important;
		font-weight:			bold !important;
		border-color:			#333 !important;
	}
	#profileRows a:hover {
		color:					#AAA !important;
		background-color:		#458 !important;
		border-color:			#333 !important;
	}
	.js-truncate-outer .btn-truncate {
		background-image: url("data:image/png;base64,") !important;
		background-color: #111 !important;
	}

	/* Friends */
	.friendHolder {
		background-color:		#222 !important;
	}
	.bgLightBlue {
		background-color:		#223 !important;
	}

	/* Useless borders... */
	.borderClass {
		border:					0 !important;
	}
	.borderDark, .borderLight, div[style="border: 1px solid #bebebe; padding: 2px; margin: 4px 2px 2px 2px;"], div[style="border: 1px solid #ebebeb; padding: 1px; margin: 4px 2px 2px 2px;"] {
		border-color:			#333 !important;
		overflow:				auto !important;
	}
	div[style="margin: 0pt 10px 10px 0pt; float: left; display: none;"] {
		border:					1px solid #333 !important;
	}

	/* Messages */
	.row_highlighted {
		background-color:		#222 !important;
		border-color:			#458 !important;
	}
	.row_not_highlighted {
		background-color:		#111 !important;
		border-color:			#333 !important;
	}	

	/* Info bubble */
	.hoverinfo {
		color:					#DDD !important;
		background-image:		none !important;
		background-color:		#111 !important;
		border-color:			#458 !important;
		border-style:			solid !important;
		border-width:			1px !important;
		z-index:				9999 !important;
	}

	.bgbdrContainer {
		background-color:		#111 !important;
		border:					1px solid #333 !important;
	}

	.profile .user-comments .comment {
		background-color: #222 !important;
	}
	.profile .user-comments .comment:nth-of-type(2n+1) {
		background-color: #333 !important;
	}
	.bgColor1 {
		background-color:		#1A1A1A !important;
	}
	.bgColor2, .bgColor, .bgNone {
		background-color:		#222 !important;
	}

	div.picSurround img {
		background-color:		#111 !important;
		border:					1px solid #333 !important;
	}

	.button_form {
		background-color:		#1A1A1A !important;
		border:					none !important;
	}

	.button_form:hover {
		background-color:		#222 !important;
		border:					none !important;
	}

	#dialog {
		border:					10px solid #111 !important;
	}
	#dialog td {
		border:					1px solid #333 !important;
	}

	#copyright {
		/*display:				none !important;*/
		color:					#666 !important;
	}
	#copyright:after {
		content:				' Night Theme by Zealotus.' !important;
	}

	#panel_left {
		border-right:			0 !important;
	}

	.vote_container {
		background:				#CBB !important;
	}
	.message_separator {
		background:				#522 !important;
	}
	.page-common .forum-topic-sort {
		background-color: #458 !important;
		color: AliceBlue !important;
	}
	.page-common .normal_header {
		background-color: #222 !important;
		border-color: #222 !important;
	}
	.forum_locheader {
		background-color: #111 !important;
		border: none !important;
	}
	.forum_boardrow1 {
		background:				#111 !important;
		border:					#333 !important;
	}
	.forum_boardrow2 {
		background:				#222 !important;
		border:					#333 !important;
	}
	.forum_boardrowspacer {
		background:				transparent !important;
	}
	.forum_category {
		background:				#458 !important;
	}
	.forum_category a {
		color:					#AAA !important;
	}
	#forum_options_container ul li {
		border-color:			#458 !important;
	}
	.quotetext, .codetext {
		background:				#222 !important;
		border:					1px solid #333 !important;
	}
	.sig {
		border-color:			#333 !important;
	}

	/* Shared Anime/Manga page fix */
	td[style='background-color: rgb(246, 246, 246);'] {
		background-color:		#222 !important;
	}
	span[style='color: rgb(0, 0, 255);'] {
		color:					#04F !important;
	}
	span[style='color: rgb(255, 0, 0);'] {
		color:					#E10 !important;
	}
	span[style=""] {
		color:					#2D2 !important;
	}

	/* ==== Feature Bookmarks ==== */

	/* ==== [Z-RplImg] START ==== */
	/* The icons */
	img[src*="images/good-off.gif"]		{ background: transparent url(data:image/png;base64,) no-repeat center center !important; }
	img[src*="images/good-on.gif"]		{ background: transparent url(data:image/png;base64,) no-repeat center center !important; }
	img[src*="images/bad-off.gif"]		{ background: transparent url(data:image/png;base64,) no-repeat center center !important; }
	img[src*="images/bad-on.gif"]			{ background: transparent url(data:image/png;base64,) no-repeat center center !important; }
	img[src*="images/cancel-fsg.gif"]	{ background: transparent url(data:image/png;base64,) no-repeat center center !important; }
	img[src*="images/watch_n.gif"]		{ background: transparent url(data:image/png;base64,) no-repeat center center !important; }
	img[src*="images/watch_y.gif"]		{ background: transparent url(data:image/png;base64,) no-repeat center center !important; } 
	img[src*="images/rss_small.gif"]	{ background: transparent url(data:image/png;base64,) no-repeat center center !important; }
	img[src*="images/reply.gif"]			{ background: transparent url(data:image/png;base64,) no-repeat center center !important; }

	/* Needed code for the replacement icins to work */
	img[src*="images/good-off.gif"], img[src*="images/good-on.gif"], img[src*="images/bad-off.gif"],
		img[src*="images/bad-on.gif"], img[src*="images/cancel-fsg.gif"] {
		height:					0 !important;
		width:					0 !important;
		margin:					0 auto !important;
		padding:				13px 13px 0 0 !important;
	}
	img[src*="images/watch_n.gif"], img[src*="images/watch_y.gif"] {
		height:					0 !important;
		width:					0 !important;
		margin:					0 auto !important;
		padding:				15px 15px 0 0 !important;
	}
	img[src*="images/reply.gif"], img[src*="images/rss_small.gif"] {
		height:					0 !important;
		width:					0 !important;
		margin:					0 auto !important;
		padding:				11px 11px 0 0 !important;
	}
	img[src*="images/ignorethread.gif"] {
		background:				transparent url(data:image/png;base64,) no-repeat center center !important;
		height:					0 !important;
		width:					0 !important;
		margin:					0 auto !important;
		padding:				12px 12px 0 0 !important;
	}
	/* Main Header */
	#header_surround {
		height:					49px !important;
		background:				transparent !important;
	}
	#header_left {
		height:					49px !important;
		background:				#111 url('http://i206.photobucket.com/albums/bb123/WhiteNemesis00/MAL/Night%20Theme/myanimelist-header-leftb6c.png') no-repeat top left !important;
	}
	#header_right {
		height:					49px !important;
		background:				#111 url('http://i206.photobucket.com/albums/bb123/WhiteNemesis00/MAL/Night%20Theme/myanimelist-header-rightb6c.png') no-repeat top left !important;
	}
	/* Re-enable "Add to List" icon */
	.button_add {
		height:					0 !important;
		width:					0 !important;
		border:					0 !important;
		font-size:				0 !important;
		margin:					0 auto !important;
		background:				transparent url(data:image/png;base64,) no-repeat center center !important;
		position:				relative !important;
			top:				2px !important;
		padding:				13px 13px 0 0 !important;
	}
	/* Re-enable "Edit List Entry" icon */
	.button_edit {
		height:					0 !important;
		width:					0 !important;
		border:					0 !important;
		font-size:				0 !important;
		margin:					0 auto !important;
		background:				transparent url(data:image/png;base64,) no-repeat center center !important;
		position:				relative !important;
			top:				2px !important;
		padding:				13px 13px 0 0 !important;
	}
	/* Maintenance image */
	img[src*="maintenance.png"] {
		background:				transparent url('http://i206.photobucket.com/albums/bb123/WhiteNemesis00/MAL/Night%20Theme/maintenance.png') no-repeat center center !important;
		height:					0 !important;
		width:					0 !important;
		margin:					0 auto !important;
		padding:				125px 250px !important;
	}
	/* ==== [Z-RplImg] END ==== */

