css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("intrepidmuseum.org") {

@media all and (max-width: 642px) {
* {
	padding:0 !important;
	margin:0 !important;
	border:0 !important;
	float:none !important;
	text-align:left !important;
	background:none !important;
	color:#000;
	border-collapse:collapse !important;
	border-spacing:0 !important;
	box-sizing:border-box;
	font-size:14px;
	position:static !important;
	font-family:Helvetica;
}

body {
	background:#d2dce4 !important;
	background-size:100% auto;
}

table {
	width:100% !important;
	height:auto !important;
}
tr, td {
	display:block !important;
	overflow:hidden !important;
	clear:both !important;
	width:100% !important;
	height:auto !important;
}
div, div#rotator {
	width:auto !important;
	height:auto !important;
	float:none !important;
}
img {
	display:block;
	max-width:100% !important;
	height:auto !important;
}

a[title="Intrepid Home"] img,
img[alt="Rainbow Band"],
#leftShell, #rightShell,
a[href*="Make-a-Gift"],
.VideoLikeContent

{
	display:none !important;
} 

td#middleShell {
	margin:0 auto !important;
	width:80% !important;
	border:1em solid #fff !important;
	border-width:0 1em !important;
	background:#fff !important;
	box-sizing:content-box;
}

/* LOGO */

a[title="Intrepid Home"] {
	display:block !important;
	background:#fff url(http://sobolev.us/downloads/stylish/intrepid/logo.png) 50% 100% no-repeat !important;
	background-size:100% auto;
	padding-bottom:31% !important;
	text-decoration:none !important;
}
a[title="Intrepid Home"]:hover:before {
	color:#e63a3a !important;
}
a[title="Intrepid Home"]:before {
	display:block !important;
	width:100% !important;
	text-align:center !important;
	content:'Intrepid';
	text-transform:uppercase;
	font:normal 13.8vw/13.4vw 'Impact';
	color:#1e537e !important;
}
a[title="Intrepid Home"]:after {
}

/* SEARCH */

#plc_lt_ctl00_pnlSearch {
	position:relative !important;
	padding:1em 0 !important;
}
#plc_lt_ctl00_txtWord,
.TextBoxField {
	width:100% !important;
	border:3px solid #ccc !important;
	font-size:2em;
	line-height:1.4em;
	padding:0 2em 0 .4em !important;
	background:url(http://sobolev.us/downloads/stylish/intrepid/search.png) 97% 50% no-repeat !important;
	background-size:auto 80%;
	border-radius:.2em;
	overflow:hidden;
	-webkit-background-clip:content-box;
}
#plc_lt_ctl00_btnImageButton {
	display:block;
	position:absolute !important;
	right:0;
	top:50%;
	height:3em;
	width:6em;
	margin-top:-1.5em !important;
	opacity:0;
}
.lnkAdvSearch {
	display:none;
}

/* NAVIGATION */

.CMSMenuItem img,
.CMSMenuItemMouseOver img,
.CMSMenuItemMouseDown img,
.CMSMenuHighlightedMenuItem img,
.CMSMenuHighlightedMenuItemMouseOver img,
.CMSMenuHighlightedMenuItemMouseDown img,
.nav_divider,

.CMSMenuHighlightedMenuItem img {
	display:none !important;
} 

.CMSMenu td:before,
.treeNavCMSTreeMenuLink,
.treeSubNavCMSTreeMenuLink,
.treeNavCMSTreeMenuSelectedLink,
.treeSubNavCMSTreeMenuSelectedLink,
treeSubNavCMSTreeMenuLink,
.leftmenuCMSMenuItem,
.leftmenuCMSMenuItemMouseOver,
.leftmenuCMSMenuItemMouseDown {
	display:block;
	content:'xxx';
	color:#fff !important;
	padding:1em !important;
	text-transform:uppercase;
	font-size:1.4em;
	border-bottom:2px solid #fff !important;
	border-radius:.4em;
	background:#e63a3a url(http://sobolev.us/downloads/stylish/intrepid/star.png) 97% 50% no-repeat !important;
	background-size:auto 40%;
	text-shadow:1px 1px 1px rgba(0,0,0,.4);
}
.CMSMenu td:hover:before,
.CMSMenu td.CMSMenuHighlightedMenuItem:before {
	color:#fff !important;
	background-color:#00aae7 !important;
}

#plc_lt_zoneHeader_cmsmenu_menuElem-000:before {
	content:'Plan Your Visit';
}
#plc_lt_zoneHeader_cmsmenu_menuElem-002:before {
	content:'The Intrepid Experience';
}
#plc_lt_zoneHeader_cmsmenu_menuElem-004:before {
	content:'Education';
}
#plc_lt_zoneHeader_cmsmenu_menuElem-006:before {
	content:'Groups';
}
#plc_lt_zoneHeader_cmsmenu_menuElem-008:before {
	content:'Kids & Families';
}
#plc_lt_zoneHeader_cmsmenu_menuElem-010:before {
	content:'Host an Event';
}
#plc_lt_zoneHeader_cmsmenu_menuElem-012:before {
	content:'Education';
}
#plc_lt_zoneHeader_cmsmenu_menuElem-014:before {
	content:'Membership';
}

/* SECONDARY NAVIGATION */

.leftmenuCMSMenu {
	margin:2em 0 !important;
}

.leftmenuCMSMenuItem,
.treeNavCMSTreeMenuLink:hover,
.leftmenuCMSMenuItemMouseOver,
.leftmenuCMSMenuItemMouseDown {
	background-color:#0ae !important;
}
.leftmenuCMSMenuItem:hover,
.leftmenuCMSMenuItemMouseOver:hover,
.leftmenuCMSMenuItemMouseDown:hover {
	background-color:#e63a3a !important;
}

/*  SUBMENU */

.treeSubNavCMSTreeMenuItem,
.treeSubNavCMSTreeMenuSelectedItem {
	font-size:0;
}
.treeNavCMSTreeMenuLink,
.treeSubNavCMSTreeMenuLink,
.treeNavCMSTreeMenuSelectedLink,
.treeSubNavCMSTreeMenuSelectedLink {
	background:#999 !important;
	font-size:18px !important;
}

.treeNavCMSTreeMenuSelectedLink,
.treeSubNavCMSTreeMenuSelectedLink {
	background:#666 !important;
}
.treeSubNavCMSTreeMenuSelectedLink {
	padding-left:2em !important;
}

/* BANNER IMAGE */

.show a {
	display:block !important;
	width:100% !important;
	padding-bottom:21.83% !important;
	position:relative !important;
	margin:1em 0 !important;
}
.show img {
	position:absolute !important;
	left:50% !important;
	top:50% !important;
	width:100% !important;
	border-radius:.4em;
	max-width:9999px !important;
	-webkit-transform: translate(-50%, -50%) scale(1);
}

@-webkit-keyframes zoom {
	0% {
		-webkit-transform: translate(-50%, -50%) scale(1);
	}                                           
	50% {                                        
		-webkit-transform: translate(-50%, -50%) scale(1.2);
	}
	100% {                                        
		-webkit-transform: translate(-50%, -50%) scale(1);
	}
}

/* HEADERS */

.pageTitle,
.box_left,
table[width="465"] strong a span,
a[href*="http://www.tripadvisor.com"]:before {
	font:normal 2em/2.1em Impact !important;
	color:#2c6189;
	text-align:center !important;
	text-transform:uppercase;
	background:url(http://sobolev.us/downloads/stylish/intrepid/star-blue.png) 3% 50% no-repeat, url(http://sobolev.us/downloads/stylish/intrepid/star-blue.png) 97% 50% no-repeat !important;
	background-size:auto 40%;
	background-color:#ddd !important;
	border-radius:.2em;
	text-shadow:0 1px 0 #fff, 0 -1px 0 #111;
	margin:.5em 0 !important;
}

/* RSS */

a[href*="Rss.aspx"] img {
	display:no1ne !important;
}
a[href*="Rss.aspx"] {
	display:none !important;
	float:right !important;
	width:3em;
	height:3em;
	background:#f37c14 url(http://sobolev.us/downloads/stylish/intrepid/rss.png) 50% 50% no-repeat !important;
	background-size:100% auto;
	border-radius:.4em;
}

/* CALENDAR */

.hdr_title_left_whathapp {
	text-align:center !important;
	background:url(http://sobolev.us/downloads/stylish/intrepid/arr-l.png) 3% 50% no-repeat, url(http://sobolev.us/downloads/stylish/intrepid/arr-r.png) 97% 50% no-repeat !important;
	background-size:auto 80%;
	white-space:nowrap;
	margin-bottom:.5em !important;
}
.hdr_title_left_whathapp input[type="image"] {
	display:inline-block !important;
	width:15% !important;
	height:3em !important;
	vertical-align:top;
	cursor:pointer;
	opacity:0;
}
.lblDate {
	display:inline-block !important;
	width:70% !important;
	text-transform:uppercase;
	line-height:1.5em;
	vertical-align:top;
	text-align:center !important;
	font-size:2em;
	color:#e63a3a !important;
}

.hdr_title_right_whathapp {
	font-size:0;
	color:#fff !important;
	text-align:justify !important;
}
.hdr_title_right_whathapp a,
a[href*="Calendar.aspx"] strong,
.FormButton {
	display:inline-block !important;
	width:32% !important;
	text-align:center !important;
	color:#fff !important;
	background:#e63a3a !important;
	line-height:2em;
	border-radius:.4em;
	margin:.5em 0 !important;
	text-decoration:none !important;
	text-transform:uppercase;
	font-weight:bold;
	text-shadow:0 1px 1px rgba(0,0,0,.8);
}
.hdr_title_right_whathapp a:hover {
	color:#222 !important;
}
.hdr_title_right_whathapp:after {
	content:'';
	display:inline-block !important;
	width:100%;
}
.hdr_title_right_whathapp a[style="color:#A81A24;"] {
	color:#fff !important;
	background:#0ae !important;
	text-shadow:0 -1px 0 rgba(0,0,0,.8);
}

.column_text {
	position:relative !important;
	line-height:4em;
	color:#e63a3a;
	padding:0 1em !important;
	vertical-align:top;
	font-weight:bold;
	font-size:1.2em;
}
.column_text br {
	display:none !important;
}
.column_text a {
	width:100% !important;
	color:#1c5179 !important;
	text-transform:uppercase;
	position:absolute !important;
	left:0;top:0;
	font-size:1.2em;
	height:100% !important;
	padding:0 0 0 12.5em !important;
	border-bottom:1px dotted #0ae !important;
}

a[href*="Calendar.aspx"] img {
	display:none !important;
}
a[href*="Calendar.aspx"] strong {
	width:100% !important;
	margin:1em 0 !important;
}
#plc_lt_zoneBody_pageplaceholder_pageplaceholder_lt_zoneLeft_WhatsHappening_pnlWhatsHappening .hdr_title_left_column {
	background:none !important;
}

/* CURATOR'S CORNER & FORMER CREWMEMBERS CORNER */

.hdr_title_left_column td,
.hdr_title_right_column td {
	width:auto !important;
	display:block !important;
	clear:none !important;
	float:left !important;
	line-height:4em;
	font-size:1.2em;
}
.hdr_title_left_column td[align="right"],
.hdr_title_right_column td[align="right"] {
	float:right !important;
	color:#e63a3a;
	font-weight:bold;
}
.hdr_title_left_column td a,
.hdr_title_right_column td a {
	color:#1c5179 !important;
	text-transform:uppercase;
}
.blog_content_text,
.blog_content_textRight {
	padding-bottom:1em !important;
	font-size:1.2em;
	color:#444 !important;
}
.blog_content_text a,
.blog_content_textRight a,
a[title="more story..."] {
	display:inline-block !important;
	padding:0 0 0 1em !important;
	font-size:.8em !important;
	color:#fff !important;
	background:#e63a3a !important;
	line-height:1.2em;
	border-radius:.4em;
	margin:0 .5em !important;
	text-decoration:none !important;
	text-transform:uppercase;
	position:relative !important;
}
a[title="more story..."] {
	padding-right:.5em !important;
	color:#e63a3a !important;
}
.blog_content_text a:after,
.blog_content_textRight a:after {
	content:' »';
	padding-right:.5em;
}
a[title="more story..."]:after {
	content:'More »';
	position:absolute;
	left:.5em;
	font-size:.9em !important;
	color:#fff !important;
}

/* SOCIAL */

table[width="468"][cellspacing="0"] td[align="center"] {
	position:relative !important;
	min-height:64px;
	background:#fafafa url(http://sobolev.us/downloads/stylish/intrepid/social.png) 50% 50% no-repeat !important;
	padding:1em 0 !important;
	border-radius:.4em;
	box-shadow:inset 0 1px 2px rgba(0,0,0,.2);
}
table[width="468"][cellspacing="0"] td[align="center"]:before,
table[width="468"][cellspacing="0"] td[align="center"]:after {
	float:left;
	line-height:64px;
	width:50%;
	text-align:right;
	font-size:12px !important;
	box-sizing:border-box;
}
table[width="468"][cellspacing="0"] td[align="center"]:before {
	content:'Become a fan on Facebook';
	padding-right:85px !important;
}
table[width="468"][cellspacing="0"] td[align="center"]:after {
	content:'Follow us on Twitter';
	float:right;
	padding-left:85px !important;
	text-align:left;
}
table[width="468"][cellspacing="0"] table[width="465"] td[align="center"] {
	background:none !important;
	box-shadow:none;
}
table[width="468"][cellspacing="0"] table[width="465"] td[align="center"]:before,
table[width="468"][cellspacing="0"] table[width="465"] td[align="center"]:after {
	display:none;
}

img[alt="Latest News"] {
	position:absolute !important;
	left:50%;top:50%;
	-webkit-transform: translate(-169.5%, -90%) scale(2.8);
	width:auto !important;
	max-width:auto !important;
	margin:20px 0 !important;
	opacity:0;
}

/* COPY */

table[width="465"] strong a {
	text-decoration:none;
}
table[width="465"] strong a span {
	display:block;
	background:none !important;
	line-height:1.1em !important;
	font-size:2.6em !important;
	padding:1.6em 0 1em 0 !important;
	margin:.5em 0 0 0 !important;
	background:url(http://sobolev.us/downloads/stylish/intrepid/star-blue.png) 50% 0 no-repeat !important;
	background-size:auto;
}
table[width="465"] strong a br,
td[height="19"],
table[width="465"] td[width="297"] br,
img[width="154"][height="144"] {
	display:none !important;
}
table[width="465"] td[width="297"] {
	font-size:1.4em;
	color:#333;
	line-height:1.4em;
}
td[bgcolor="#f0f0f0"] span {
	display:block;
	background:#eee !important;
	padding:.5em 1em !important;
	font-style:italic;
	font-size:1.2em !important;
	color:#888;
	border-radius:.4em;
	text-shadow:0 1px #fff;
	margin-bottom:2em !important;
}
table[width="465"][cellspacing="0"] table[width="468"] td[bgcolor="#f0f0f0"] td[align="center"] {
	padding-bottom:2em !important;
	background:url(http://sobolev.us/downloads/stylish/intrepid/star-blue.png) 50% 100% no-repeat !important;
	background-size:auto;
	margin-bottom:2em !important;
}

/* BANNERS */

img[style="width: 465px; height: 97px;"] {
	display:block !important;
	width:100% !important;
	-webkit-transform:scale(1.05);
}

table[width="468"][cellpadding="1"] tr,
table[width="468"][cellpadding="1"] td {
	display:inline-block !important;
	width:50% !important;
	height:30% !important;
	overflow:hidden !important;
	border-radius:.4em;
}
table[width="468"][cellpadding="1"] td {
	width:99% !important;
	height:auto !important;
	margin:.25em 0 !important;
}
table[width="468"][cellpadding="1"] tr:nth-child(2n+1) {
	display:none !important;
}


/* EMAIL SIGNUP */

.TextBoxField {
	background:#fff !important;
}
.FormButton {
	display:block;
	width:100% !important;
	float:right !important;
	text-align:center !important;
	margin-bottom:1em !important;
}
label.EditingFormLabel {
	display:block !important;
	color: #333 !important;
	margin:.5em 0 !important;
}

/* WEBCAM */

a[href*="Live-Webcams"] img {
	display:none !important;
}
a[href*="Live-Webcams"] {
	display:block !important;
	background:url(http://earthcam.com/cams/newyork/intrepid/images/intrepid5.jpg?timer=60) 50% 50% no-repeat !important;
	background-size:cover;
	padding-bottom:58% !important;
	border-radius:.4em;
	text-decoration:none;
	color:rgba(255,255,255,.8);
	text-shadow:0 0 2px rgba(0,0,0,.4);
	font-size:2em !important;
}
a[href*="Live-Webcams"]:after {
	content:'LIVE';
	line-height:2.2em;
	vertical-align:bottom;
	font-weight:bold;
}
a[href*="Live-Webcams"]:before {
	content:'•';
	text-decoration:blink !important;
	font-size:4em;
	line-height:.53em !important;
	vertical-align:bottom;
}

/* TRIP ADVISOR */

table[width="250"] td[align="left"] {
	color:#fff !important;
	font-size:0;
}
img[alt="Trip Advisor"],
table[width="250"] td[align="left"] br,
a[href*="http://www.tripadvisor.com"] span {
	display:none !important;
}
a[href*="http://www.tripadvisor.com"] {
	display:block !important;
	width:100% !important;
	padding-bottom:12em !important;
	background:url(http://sobolev.us/downloads/stylish/intrepid/tripadvisor.png) 50% 70% no-repeat !important;
	background-size: auto 30%;
	position:relative !important;
}
a[href*="http://www.tripadvisor.com"]:before {
	content: 'Intrepid on TripAdvisor';
	display:block;
	position:absolute;
	left:0;top:0;width:100%;
	height:2em !important;
	text-align:center !important;
}
a[href*="http://www.tripadvisor.com"]:after {
	content: '';
	display:block;
	position:absolute;
	left:0;top:0;width:100%;
	height:90% !important;
	background:url(http://sobolev.us/downloads/stylish/intrepid/tripadvisor.php) 50% 100% no-repeat !important;
}

/* CHARITY NAVIGATOR */

a[href^="http://www.charitynavigator.org"] {
	display:block !important;
	width:100% !important;
	background:url(http://sobolev.us/downloads/stylish/intrepid/charity.png) 50% 50% no-repeat !important;
	background-size:cover;
	padding-bottom:12.8% !important;
}
a[href^="http://www.charitynavigator.org"] img {
	display:none !important;
}

/* FOOTER */

#footer {
	margin-top:2em !important;
	padding:1em !important;
	text-transform:uppercase;
}

#footer_lft {
	text-align:center !important;
	color:#bbb !important;
	border:1px solid #ddd !important;
	border-width: 1px 0 !important;
	padding:1em 0 !important;
	margin-bottom:2em !important;
}
#footer_rt {
	padding-bottom:.5em !important;
}
#footer_rt tr {
	width:100% !important;
	text-align:center !important;
	display:block !important;
}
#footer_rt td {
	width:auto !important;
	display:inline-block !important;
	float:none !important;
	clear:none !important;
	padding:1em !important;
}
#footer_rt tr:after {
	content:'';
	display:inline-block !important;
	width:100% !important;
}
#footer_rt .footer_spacer {
	display:none !important;
}

}

}

@-moz-document regexp(".*\.aspx$") {

@media all and (max-width: 642px) {

.leftmenuCMSMenu,
.CMSMenuItem {
	display:none !important;
}
a[href*="_Map-pdf"] img {
	display:none !important;
}
a[href*="_Map-pdf"] {
	background:#9bc url(http://maps.googleapis.com/maps/api/staticmap?center=W+48th+and+10th+Ave,New+York,NY&zoom=15&size=640x640&sensor=false&markers=label:I%7Ccolor:gray%7CIntrepid+Museum,New+York,NY&markers=label:A%7Ccolor:blue%7CW+50th+and+8th+ave,NY&markers=label:1%7Ccolor:red%7CW+50th+and+Broadway,NY&markers=label:R%7Ccolor:yellow%7CW+48th+and+7th+ave,NY) 50% 50% no-repeat !important;
	background-size:cover;
	display:block !important;
	width:100% !important;
	padding-bottom:100% !important;
	margin-top:.5em !important;
}

td[width="655px"] table[width="699"] * {
	color:#666 !important;
	font-size:inherit !important;
}
br[type="_moz"],
br,
img[width="167"][height="73"],
img[alt="Seperator"] {
	display:none !important;
}

td[width="655px"] table[width="699"] p {
	padding:.5em 0 !important;
	font-size:1.2em !important;
	line-height:1.4em !important;
}
td[width="655px"] table[width="699"] span span strong {
	display:block !important;
	margin:1em 0 0 0 !important;
	font:normal 22px Impact !important;
	text-transform:uppercase;
	color:#1c5179 !important;
}
td[width="655px"] table[width="699"] em {
	font-style:normal !important;
	color:#000 !important;
}

}

}
