css:
@-moz-document url-prefix("http://docs.opencv.org") {
body {
  font-family: "Droid Sans Mono", sans-serif !important;
}

div.body {
  background-color: #333 !important;
  color: #ccc !important;
}
div.body p {
}
div.body h1, div.body h2 {
  background-color: #000 !important;
  color: #ccc !important;
  border-bottom: none;
  box-shadow: 0 3px 10px rgba(0,0,0,0.6);
}
strong {
  color: rgb(0, 96, 144) !important;
}
div.body img {
  -webkit-filter: invert(80%);
}
tt {
  color: #fff !important;
}
dt:target, .highlighted {
  background-color: rgba(251,229,78, 0.3) !important;
  font-weight: bold;
}


div.seealso, div.note {
  background-color: #444 !important;
  font-size: 0.8em !important;
  border: none;
}


pre {
  font-family: "Droid Sans Mono" !important;
  background-color: #222 !important;
  border: none;
}

div.document {
  background-color: #222 !important;
  color: #ccc !important;
}
div.related {
  display: none !important;
}
div.sphinxsidebar {
  float: none !important;
  position: fixed !important;
  top: 0px !important;
  margin-left: 0 !important;
  height: 100% !important;
  overflow-y: scroll !important;
}

img.logo {
  width: 64px !important;
}
}

@-moz-document url-prefix("http://docs.opencv.org/search") {
div.sphinxsidebar {
  display: none;
}
div.bodywrapper {
  margin: 0 !important;
}
}
