css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("neopets.com") {
 #header {
  background-image: none !important;
  height: 30px !important;
 }
 #header > table,   /* Untable the table */
 #header > table > tbody,
 #header > table > tbody > tr{
  display: block !important;
 }
 #header > table > tbody > tr > td {
  display: block !important;
 }
 #header > table > tbody > tr:first-child > td:first-child { /* Hide the first cell */
  display: none !important;
 }
 #header > table > tbody > tr > td.eventIcon{ /* Position mail/notification */
  font-size: 9px !important;
  position: relative !important;
  display: block !important;
  left: 620px !important;
  top: -1px !important;
  height: 12px !important;
  width: 200px;
 }
 #header > table > tbody > tr > td.user { /* Position [username, np, logout] */
  position: relative !important;
  display: block !important;
  left: 685px !important;
  font-size: 9px !important;
  top: -15px !important;
  width: 300px !important;
  border: 0px solid blue !important;
  visibility: hidden !important;
  height: 12px !important;
  letter-spacing: -7px;
BORDER: 1px solid red;
 }
 #header > table > tbody > tr > td.user *{ /* [username, np, logout] etc stuff */
  visibility: visible;
  letter-spacing: 0px;
 }
 #header > table > tbody > tr > td.user a { /* [username, np, logout] links */
  display: inline !important;
  letter-spacing: 0px;
  visibility: visible;
 }
 #navigation table,
 #navigation table > tbody,
 #navigation table > tbody > tr,
 #navigation table > tbody > td{ /* untable the table */
  display: block;
 }
 #template_nav {  /* The navigation list */
  position: relative !important;
  display: block !important;
  left: -80px !important;
  top: -38px !important;
  width: 620px !important;
  border: 0px solid blue !important;
BORDER: 1px solid green;
 }
 #nst { /* The neotime */
  position: relative !important;
  display: block !important;
  left: 890px !important;
  top: -55px !important;
  width: 100px !important;
  border: 0px solid blue !important;
 }
 #template_nav > li + li + li + li + li +li + li + li {
  display: none !important; /* No video for you */
  width: 0px !important;
 }
 ul.template_nav li.nav_image {
   margin-left: -2px !important;
 }
 #template_nav > li + li + li + li + li +li + li + li + style + li  {
  display: none !important; /* No Mall rats */
  width: 0px !important;
 }

}
