css:
@-moz-document domain(tumblr.com) {
/* Code repaired by admin. See http://forum.userstyles.org/discussion/37591/attempting-to-mass-fix-broken-tumblr-dash-icon-styles */
.new_post {
	background: #ffffff url('http://i1230.photobucket.com/albums/ee494/uzumaki_kirsty/narutoiconsdash-1.png') center center no-repeat !important;
}
.new_post_label_icon {
	background-image: none !important;
}

.new_post_label{
opacity:0 !important;
}

.new_post_label_icon {
background-image: none!important;
background-repeat: no-repeat;
position: center;
display: block;
width: 76px;
height: 90px;
top: 5px;
left: 0;
}

#posts .post.new_post {
padding-left: 0px;
text-align: center;
text-transform: lowercase !important;
letter-spacing: 0px !important
}
	
	
a.explore_more_tags,
a.add_and_remove,
a.customize,
a.mass_editor {
background-color: transparent !important;
}
	
.avatar_frame{background-color:transparent!important;border:none!important;}
.content.is_media, 
.mask.top, 
.mask.bottom {
background: none !important; 
}

.post.photo_post .photo, 
.post.photo_post .video_thumbnail {
border-radius: 16px !important;
}

.post.photo_post .photo, 
.post.photo_post .video_thumbnail {
-moz-box-shadow: 1px 1px 3px #000000 !important;
}

#posts .notification a:not(.avatar_frame) {
border-bottom: none !important;
}

}
