@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);


#main-window :-moz-any(/*#back-button, #forward-button, #reload-button, #stop-button, #home-button,/**/ #print-button, #downloads-button,/* #history-button, #bookmarks-button, #bookmarks-menu-button,/**/ #new-window-button, #cut-button, #copy-button, #paste-button, #fullscreen-button, #zoom-out-button, #zoom-in-button, #sync-button:not([status="active"]), #feed-button, #new-tab-button) > .toolbarbutton-icon

	{list-style-image: url("chrome://browser/skin/Toolbar-inverted.png") !important; }

#main-window #nav-bar #feed-button > .toolbarbutton-icon,
#main-window #nav-bar #back-button > .toolbarbutton-icon,
#main-window #nav-bar #forward-button > .toolbarbutton-icon,
#main-window #nav-bar #reload-button > .toolbarbutton-icon,
#main-window #nav-bar #stop-button > .toolbarbutton-icon, 
#main-window #nav-bar #home-button > .toolbarbutton-icon,
#main-window #navigator-toolbox #history-button > .toolbarbutton-icon, 
#main-window #navigator-toolbox #bookmarks-button > .toolbarbutton-icon/*,
#main-window #navigator-toolbox #bookmarks-menu-button > .toolbarbutton-icon/**/

	{list-style-image: url("chrome://browser/skin/Toolbar-inverted.png") !important; }

#main-window #navigator-toolbox #bookmarks-menu-button > .toolbarbutton-icon

	{list-style-image: url("chrome://browser/skin/places/bookmarksMenu.png") !important;
	-moz-image-region: rect(0px, 16px, 16px, 0px) !important;/*bookmarksToolbar.png*/
	}

#main-window #minMaxClose-button > .toolbarbutton-icon

	{list-style-image: url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABIAAAASCAYAAABWzo5XAAAAaElEQVQ4jcWSOQ7AIAwE55N+L09LmlBE8poNWMpKLmiG8QEQwEgq+JhxJXlgbSBVqa1qLf1gx/YYJA0n3AWVJv+BVEu7IAW3jrU0cgAvULH2aRQsDJcHKt52IrOtjGxYC+QE1jsjxNZuInFUUH8iHX0AAAAASUVORK5CYII=") !important; }

