	{list-style-image: url("data:image/png;base64,") !important; }

#undoclosetab-button .toolbarbutton-icon, #btn_undoclose .toolbarbutton-icon,
#uctb-toolbar-button .toolbarbutton-icon

	{list-style-image: url("data:image/png;base64,") !important; }

#main-window #BHDToggle-button > .toolbarbutton-icon

	{list-style-image: url("data:image/png;base64,") !important; }

