css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tweakers.net") {
body, div#searchbar, .notificationsContainer {
  background: #414141 !important
}

#search, #mainColumn, #secondColumn, #fp_tabs_container, #contentArea, #top:not(:first-child) {
  background: #4e4e4e !important
}

.fpaItem {
  margin-bottom: 10px !important;
  max-height: 165px !important
}

.fpItem {
    background: #FFF !important;
    box-shadow: 0px 0px 0px 1px rgba(255, 255, 255, 0.33) inset;
}

table.highlights tr:last-of-type td {
  border-bottom: 0 !important;
  padding-bottom: 5px !important
}

#contentArea {
  padding-top: 0 !important
}

div.top > a {
  color: rgb(200,203,203)
}

#bottom > DIV.hr {
  height: 1px !important;
  background: #414141 !important
}

#top a, #top a:visited, #bottom a {
 color: rgb(200, 203, 203) !important
}

.trackerPopup .trackeritem li .caption {
    color: rgb(183, 180, 178) !important
}

#top {
  border-bottom: none !important
}

#fp_tabs {
  border-bottom: 1px solid #414141 !important
}

a, a:visited, #notifications p, #notifications a {
  color: #000 !important
}

h2 {
  font-family: "century gothic",futura,arial,helvetica,"liberation sans","dejavu sans condensed",sans-serif;
  padding-left: 20px !important;
  padding-top: 5px !important
}

div#cookieBar, .b_ad, .advertorial, #tracker-sponsored {
  display: none !important
}

#fp_tabs_container {
  margin-bottom: 0px !important
}

.reacties {
  margin-left: 20px !important
}

#search {
  background: #4e4e4e !important;
  padding-bottom: 10px !important
}

#news h2 {
  padding-top: 5px !important;
  padding-left: 20px !important
}

table.highlights td {
  padding: 4px 0 !important
}

table.highlights td.type {
  padding-left: 5px !important
}

table.highlights td.replies {
  padding-right: 10px !important
}

.relevancyColumn {
  box-shadow: 0px 0px 0px 1px rgba(255, 255, 255, 0.33) inset !important
}

#header,
.articleColumn, .relevancyColumn,
.article, p.author, .sidebar, #reacties,
.nextPrevious, .popularEditorialReviewsBlock, .popularUserReviewsBlock,
P.readMore, #listingContainer, .popularProductListing, .secondColumn,
.vaPortalColumnLeft, .vaPortalColumnRight, .channelHeader H2,
#forumheading > #title > H1, #jobContent, .meukTable, .content {
  background: #FFF
}

.relevancyColumn, #header, .article p, .sidebar, #reacties, .nextPrevious, .popularEditorialReviewsBlock, .popularUserReviewsBlock, .readMore, #listingContainer, .popularProductListing, .vaUserAds, .addUserAd, .content {
  padding-left: 20px !important;
  padding-right: 20px !important
}

#header {
  margin-bottom: 0px !important
}

.nextPrevLinks {
  padding-left: 20px !important
}

p.author {
  padding-left: 20px !important;
  margin-bottom: 0px !important
}

.sidebar {
  padding-right: 20px !important
}

#filterContainer {
  margin-right: 0 !important
}

#menu .dropdown span,
#menu .dropdown li a {
  color: #000 !important
}

.meukTable {
  padding-left: 20px;
  padding-right: 18px !important
}

#contentArea, .pageTabsContainer {
  background-color: #fff !important
}

#contentArea, #search {
  width: 90% !important;
  min-width: 980px
}

#tweakbase, .pageTabsContainer  {
  margin: 0 11% !important;
  background: #fff !important;
  /* background-color: #4e4e4e !important*/
}

#fpaItems {
  float: right;
  width: 490px !important;
  padding-right: 25px;
  text-align: center !important;
}

.fpaItem div.textContainer {
  max-height: 60px !important;
  line-height: 100% !important;
  padding: 10px 0 !important
}

.articleColumn {
  float: left;
  min-width: 620px;
  width: 800px !important;
  line-height: 1.6;
  margin-top: -5px
}
}
