css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.linux.org.ru") {

 

/* tango-light 2 2.0.0 ollowtf (http://www.linux.org.ru/people/ollowtf/profile) */


html,body {
    color: #24221D !important;
    background-color:#f5f2dd !important;
    line-height: 1.2 !important;
}

a {
    color: #69797D;
    text-decoration:none !important;
}

#bd a,#bd a:visited {
    color: #69797D !important;
}

#bd a.tag {
    color: #69797D !important;
    font-weight: bold;
}

#bd .sign a {
    font-weight: bold !important;
    text-decoration: none !important;
    color: #69797D !important;
}

.stars {
    color: #69797D !important;
    font-size: 0.7rem;
    letter-spacing: 0.1rem;
    vertical-align: middle;
}

div.quote {
    font-style: normal;
    background: #EEF0DA;
    padding: 3px;
}

.nav li a.current {
    font-weight:bold !important;
}

#user-menu {
    background-color:#f5f2dd !important;
}

#contents {
    background-color:#f5f2dd !important;
    color: #413003 !important;
}

#toc {
    background-color:#f5f2dd !important;
}

h2 {
    color:#413003 !important;
    border-bottom: 1px solid #413003 !important;
    font-size: 175%;
    font-weight: normal;
}

h1, h2, h3, h4, h5 {
    color:#413003 !important;
}

ul.tab-menu li {
    background-color:#f5f2dd !important;
}

.tab-menu a {
    color:#1C55AE !important;
}

ul.tab-menu li {
    border-style: none !important;
}

#user-menu a {
    color:#1C55AE !important;
    text-decoration: underline !important;
}

.portlet {
    background: none repeat scroll 0 0 #f5f2dd !important;
}

/* p :) */

p {

    margin-top: 5px !important;

    margin-bottom: 5px !important;

}

time {
    font-size: 0.9em !important;
}

/*юзерпик*/
#whois_userpic {
    background: none repeat scroll 0 0 #fdf9ef !important;
}

.boxlet {
    background-color:#fdf9ef !important;
}

.infoblock {
    background-color:#fdf9ef !important;
}



/* оформление полей ввода */ 

input, textarea, select {

    background:inherit !important;

    background-color: inherit !important;

    border-color: #555753 !important;

    border-style: solid !important;

    border-width: 1px !important;

    color: inherit !important;

    font-size: 0.9em !important;

    -moz-appearance: none !important;

}



/* уменьшаем заголовок новостей на главной*/

#bd .news h2 {

    font-size:1.2em !important;

}



/* шапка страницы и меню в одну строку*/

#sitetitle {

    color:black !important;
    font-size:1em !important;
    margin-right:0.5em !important;

}



#loginGreating {

    margin-top: 0 !important;

    margin-right: 0 !important;

}



#hd {

    padding-right:1em !important; 

    margin-bottom: 0 !important;

}

 

#hd h1 {

    display: none !important;

    font-size: 1em !important;

    font-weight: bold !important;

}



#hd .menu {    

    margin-top: 0 !important;

    margin-bottom: 0 !important;       

    text-align: center !important;

    /*float: left !important;*/

    font-size:1em !important;

}

 

#hd .menu li {

    margin-right:4px !important;

    margin-top:0 !important;

}

 

#hd .menu li:after {

    content:"" !important;

} 



#hd .menu a {

    color: #b87a1c !important;
    text-decoration:none !important;

}

/*заголовок топика*/
div.msg_body h1 {
    font-size: 1.1em !important;
    font-weight: bold;
}




/* скрытие пунктов главного меню: не нужны */

a[href="/server.jsp"] {

    display:none !important;

}



a[href="/wiki/"] {

    display:none !important;

}



a[href="/wiki/en/Linux"] {

    display:none !important;

}



/* инфоблоки */

.infoblock {

    display:none;

}



/* навигация */

.nav { 

    margin-top: 3px !important;

    margin-bottom: 3px !important;

    padding: 0 !important;

}



#navPath {    

    font-size:1.2em !important; 

    font-weight:bold !important;

}



.nav li a {

    text-decoration:none !important;
    background-color: inherit !important;

}



.message-table a.secondary {

    text-decoration: none !important;

}



/* уменьшаем "сенсорные" кнопки страниц */

.page-number {

    margin-bottom:2px !important;

    margin-top:0 !important;

    padding:1px 3px 1px 3px !important;

    border-radius:3px 3px 3px 3px !important;

    background-color: inherit !important;

}


/* кнопки листания топиков: не нужны */

.nav td[width="35%"] {

    display:none !important;

}

#bd .forum table tbody tr:hover {
    background-color: rgb(222, 222, 222) !important;
} 

/* оформление таблиц */

#bd .forum table thead {

    background-color: inherit !important; 

    font-weight: bold !important; 

}

#bd .forum table tbody td:nth-child(2) a {
    color: #413003 !important;
}

#bd .forum table tbody td {
    border-color: #CCCCCC !important;
}

/*#bd .forum table tbody td:first-child {
    background-color: #CCCCCC !important;
}*/

.message-table .tag {
    background-color: #cfccbb !important;
    font-size: 0.8em !important;
    display:block !important;
    float:right !important;
    margin: 2px !important;
}



table {

    font-size: 0.9em !important;

}



/* скрытие шапок таблиц: не нужны*/

#bd .forum table thead {

    display: none !important;

}

 

/* сообщение */

.msg_body h1 a {
    color: #1C55AE !important;
}



.msg .title { 

    display: inline-block !important;

    margin: 0px !important; 

    padding: 0px !important;

    font-size: 0.9em !important; 

    width: auto !important; 

}

 

.msg {

    background-color:#fdf9ef !important;
    display: inline-block !important;

    padding: 7px !important;

    margin-top: 5px !important;

    margin-bottom: 5px !important;

    border-width:0 0 0 12px !important;

    border-style:solid;

    /*border-color:#63605a !important;*/

    border-radius: 0 !important;

    float: none !important;

    width: 100% !important;

}



/* юзерпик, фиксируется ширина */

.msg_body, .message-w-userpic {

    padding-left: 0px !important;

    padding-right: 90px !important;

}

 

.userpic {    

    float: right !important;    

    height: auto !important;    

    padding: 0px !important;   

    text-align: center;    

    width: 80px !important;

    border-radius: 3px !important; 

}  

 

.userpic img {

    width: 80px !important; 

    height: auto !important;

}



/* подпись и кнопки ответа в одну строку */

.sign {

    float: left !important;

    margin-right:3px !important;

    margin-top: 0px !important;

    margin-bottom: 0px !important;

    font-size:medium !important;

}


.reply {

    float: left !important;

    font-size:medium !important;

}



/* оформление pre и code */

.code {

    font-family: monospace !important; 

    font-size:1.2em !important;

    border-radius: 0px !important; 

    border-color:#4C5658 !important;

    border-style:solid !important;

    border-width:0 0 0 5px !important;

}

.code code {
    color: #FFFFFF;
}


/* скрытие элементов между топиком и комментариями */

div[style="text-align: center; margin-top: 1em"] {

    display:none !important;

}



div.messages > br {

    display:none !important;

}



/* Эпический костыль, чтобы заменить вырвиглазную картинку разлогина: */

/* взято отсюда http://www.linux.org.ru/gallery/screenshots/7261137 */

a[title="Выйти"]{

    text-decoration : none !important;

}



a[title="Выйти"]::before {

    content : "[x]" !important;

}



img[src="/img/logout.png"] {

    display: none !important;

}



/* кнопки LORcode tools (http://www.linux.org.ru/forum/lor-source/6956920) */

.atag a {

    color: #413003 !important;

    background-color:inherit !important;

    border:1px solid #BABDB6 !important;

}

}
