@-moz-document url-prefix("http://www.nicovideo.jp/watch/"){
body {
    background-color: #999999 !important;
}
#videoDetailInformation {
    background-color: #E0E0E0 !important;
}
#videoHeaderTagList .toggleTagEdit {
    background-color: #c0c0c0 !important;
    height: 50px !important; 
    margin-top: 6px !important;
}
#videoHeaderTagList li {
    margin: 0px 30px 4px 0px !important;
    height: 14px !important;
    padding: 1px 2px !important;
}
#playerCommentPanel {
    background: -moz-linear-gradient(center top , #aaaaaa 0%, #dcdcdc) repeat scroll 0% 0% transparent !important;
}
#playerTabContainer.has_panel_ads .playerTabContent {
    bottom: 0px !important;
}

#videoHeader .shortVideoInfo .shortDescription {
    color: rgb(100, 1, 100) !important;
}

#ichibaPanel,
#ichibaPanelAd,
#textMarquee,
#playerBottomAd,
.videoInformationOuter,
.videoStartAdsOuter,
.posCenter,
.adBnr,
#videoInfo .videoThumb,
#videoComment,
#pageFooterAds,
.playerTabAds,
.panelAds,
#videoReviewBottomAdAds,
#selectionSideAd,
#twoMillion,
#web_pc_watch_footer,
.nicoSpotAds,
.tooltip,
#tagRelatedBanner  {
    display:none !important;
}
#commentDefault.commentTable,
#commentLog.commentTable,
#commentNgSetting.commentTable {
    bottom: 8px !important;
}
#searchResultExplorer #openSearchResultExplorer,
#searchResultExplorer #searchResultExplorerButton,
#searchResultExplorer #closeSearchResultExplorer {
    background-image:none !important;
    background-color: #3c3c3c !important;
    width: 180px !important;
    border-radius: 10px !important;
    text-shadow:none !important;
}
#searchResultContainer {
    background-color: #3c3c3c !important;
}
.main #videoInfo #videoInfoHead div.videoInformation,
.main #videoInfo #videoShareLinks div.videoInformation,
.main #videoInfo .parentVideoInfo div.videoInformation,
.main #videoInfo #userProfile div.videoInformation,
.main #videoInfo #bottomVideoDetailInformation div.videoInformation,
.main #videoInfo #videoComment div.videoInformation,
.main #videoInfo .videoEditMenu div.videoInformation {
    float: right;
    width: 610px !important;
}
#outline {
    padding-top: 5px !important;
    background-color: #444444 !important;
}
.videoPostedAt, .postNameSubmit,
.postNameBroadCast,
#videoInfo #videoStats li,
.supplementary li {
    color: #c0c0c0 !important;
}
.main #videoInfo #videoInfoHead div.videoInformation h3,
.main #videoInfo #videoShareLinks div.videoInformation h3,
.main #videoInfo .parentVideoInfo div.videoInformation h3,
.main #videoInfo #userProfile div.videoInformation h3,
.main #videoInfo #bottomVideoDetailInformation div.videoInformation h3,
.main #videoInfo #videoComment div.videoInformation h3,
.main #videoInfo .videoEditMenu div.videoInformation h3 {
    color: #ecbb00 !important;
}
#bottomVideoDetailInformation .triger {
     background-color: #444444 !important;
     color: #d03737 !important;       
}
#videoReview {
    background-color: #505050 !important;
    border-radius: 10px !important;
    border: 1px solid  #c0c0c0 !important;
}
.stream .inner {
    border-bottom: 1px solid #c0c0c0 !important;
    background-color: #d3d3d3 !important;
}
#videoReview h3,
.videoReviewAside .videoReviewContrl h4 {
    color:#b0c4de !important;
}    
#ichibaMain #ichibaMainLogo a {
    color: #444444 !important;
    font-weight: bold !important;
    margin-left:10px!important;
}
#ichibaMain,
#ichibaMain dl.pia dd.pia {
    background-color: #d3d3d3 !important;
    border-radius: 10px !important;
}
#ichibaMain .maker {
    color: #909090 !important;
}
#videoInfoHead .infoHeadOuter .triger, #videoShareLinks .infoHeadOuter .triger, .parentVideoInfo .infoHeadOuter .triger, #userProfile .infoHeadOuter .triger, #bottomVideoDetailInformation .infoHeadOuter .triger, #videoComment .infoHeadOuter .triger, .videoEditMenu .infoHeadOuter .triger {
   background: #444444 !important;
}

#videoDetailInformation .translation {
    display: none !important;
}

#videoDetailInformation .description {
    margin: 0px 0px 0px 0px !important;
    padding-top: 10px !important;
    padding-left: 10px !important;
}

#topVideoInfo .videoThumb {
    display: none !important;
}

#videoDetailInformation .videoMainInfoContainer {
    padding-top: 3px !important;
    padding-left: 10px !important;
}

#wallImageContainer .wallAlignmentArea.image1 {
   background-image: url("http://res.nimg.jp/img/watch_zero/walls/wall_q_silver.png") !important;
}

/* ニコる消し----ここから----- */

#playerCommentPanel .section .commentTable .commentTableContainerInner .cell span.nicoru-button,
#videoTagContainer .tagInner #videoHeaderTagList li .tagControlContainer .nicoruWrapper,
ul.socialLinks li.socialLinkNicoru,
.nicoru-button.nicorub-loaded,
#videoMenuTopList li.videoMenuListNicoru {
    display: none !important;
}
/* ニコる消し----ここまで----- */

/* 再生リスト暗く---ここから--- */

#playlistContainer .playlistItem.imageLoadSuccess{
    opacity: 0.1 !important;
}
#playlistContainer .playlistItem.imageLoadSuccess.active{
    opacity: 1 !important;
}
#playlistContainer .playlistItem.playing.imageLoadSuccess{
    opacity: 0.4 !important;
}
#playlistContainer .playlistItem.playing.imageLoadSuccess.active{
    opacity: 1 !important;
}
/* 再生リスト暗く---ここまで--- */

/* コメント欄任意サイズ変更---ここから--- */

#playerCommentPanel {
    overflow: auto !important;
    resize: horizontal !important;
}
#playerCommentPanel .section {
    width: auto !important;
    position: static !important;
}
#playerCommentPanel .section .commentTable {
    width: 95% !important;
}
#playerCommentPanel .section .commentTable .commentTableContainer {
    width: 100% !important;
}

/* コメント欄任意サイズ変更---ここまで--- */

/* douga info font color 2012/11/10 -----start--- */

#videoInfo #videoStats li span {
    color: rgb(200, 200, 200) !important;
}

/* douga info font color 2012/11/10 ----end------ */

/* leftpanel remove    -----here------ */

#leftPanel {
    display: none !important;
}

/* leftpanel remove    ----end-------- */

/* 再生リスト消し　2013/11/10------here------ */

#playlist {
    display: none !important;
}

#videoExplorerExpand {
    height: 30px !important;
    border: 1px solid #555555 !important;
    background: #555555 !important;
}

#videoExplorerExpand .expandButton {
    text-shadow: none !important;
    color: #000000 !important;
    line-height: 30px !important;
}

/* 再生リスト消し　---------end----------- */

/*　ニコメンド消し　2013/11/10--------here-------- */

#nicommendContainer,
#nicommendPanelContent .nicommendHeader,
#nicommendPanelContent .nicommendContentsOuter .nicommendContentInner .contentWrap .contentHeader.default,
#nicommendPanelContent .nicommendContentsOuter .nicommendContentInner .contentWrap .contentHeader.priority,
#nicommendPanelContent .nicommendContentsOuter .nicommendContentInner .contentWrap .nicommendItemList .item.video,
#nicommendPanelContent .nicommendContentsOuter .nicommendContentInner .contentWrap .nicommendItemList .item.news,
#nicommendPanelContent .nicommendContentsOuter .nicommendContentInner .contentWrap .nicommendItemList .item.illust,
#nicommendPanelContent .nicommendContentsOuter .nicommendContentInner .contentWrap .nicommendItemList .item.book,
#nicommendPanelContent .nicommendContentsOuter .nicommendContentInner .contentWrap .nicommendItemList .item.live,
#nicommendPanelContent .nicommendContentsOuter .nicommendContentInner .contentWrap.addContent.expand,
#nicommendPanelContent .nicommendContentsOuter .nicommendContentInner .contentWrap .nicommendItemList .item.mylist .column,
#nicommendPanel .contentHeader.assist {
    
    display: none !important;
}

/* ニコメンド消し　------------------end-------------------- */


}
