css:
@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')  
/* original by behindmylove.tumblr.com 
modified by dear-poop.tumblr.com */



 {div#right_column,
div#right_column:after {background: none !important;}

body {background-image:url('http://dl.dropboxusercontent.com/s/jc9ey7fp46vdl9l/Fond.png') !important;
background-color:#fff !important;
background-repeat:repeat !important;
background-position: center top !important;
background-attachment:fixed !important;}

a:link, a:active, a:visited {color: #685566 !important;}
textarea, #post_two_ifr, #post_two {background: white !important; background-image: none !important;}
	
/* HEADER */
div.l-header {
background:#a0685c !important;
border-radius: 15px !important;
border: 0px outset #a0685c;
margin-top: -8px !important;
margin-bottom: 0px !important;
padding-bottom: 0px !important;
box-shadow: 0px 10px 8px -12px #fff !important;
height: 60px !important;}

/* TUMBLR LOGO */
#logo {
height: 0 !important;
width: 0 !important;
margin-top: 19px !important;
padding-left: 137px !important;
padding-top: 32px !important;
background: url(http://img4.hostingpics.net/pics/896017Logo.png) no-repeat !important;
box-shadow: none !important;}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background:transparent!important;
margin-top: -15px !important;
border-radius:0px !important;
padding-top:45px!important;
text-shadow:none !important;}

*{text-shadow:none !important; color:#626262!important;}

.tab_notice {top: 0px !important;
background-image:none!important;
margin-left: 4px !important;
height:auto!important;
width:auto !important;
text-align: center !important;
border-radius: 6px !important;
box-shadow: 1px 1px 2px rgba(0, 0, 0, 0.7) !important;
vertical-align:middle !important;
font:11px consolas !important;
font-weight: bold !important;}

*.tab_notice_value{text-shadow:none !important; color:#F8E9E9!important;}

/* SEND BOTTOM TOP */
#content_top{opacity:0.0;filter:alpha(opacity=0);background:#fff!important;}
#content_bottom{opacity:0.0;filter:alpha(opacity=0);background:#fff!important;}
.content_cap{opacity:0.0;filter:alpha(opacity=0);background:#fff!important;}

div.post_content blockquote {
margin-left:0 !important;
padding-left:10px !important;}

ol#posts #new_post {background: #ffffff url('http://img4.hostingpics.net/pics/125729Navig2.png')  top center no-repeat !important;
box-shadow: none !important;
border: 0px solid #775e6d !important;
border-radius:20px !important;
padding-top:0px!important; margin:0px!important;
font-family:consolas!important; text-transform:uppercase!important; font-size:0,1em;}

.new_post_label {padding-top:74px !important;}

.new_post_label_icon{background-image:none !important}

.new_post_label > I {background-image:none !important;}
.new_post_label > I:before {content: none !important;}




.controls_section .selected, #right_column #MissingE_sidebar_title {background:transparent !important;}
#right_column a#dashboard_switch_blog_menu_current {background-color:transparent!important;}
#right_column a#dashboard_switch_blog_menu_current:hover {background-color:transparent!important;}

.hide_overflow{background-color:transparent!important;}

#dashboard_controls_open_blog{background-color:#FFFFFF!important;
box-shadow: none !important;
border: 1px solid #D7D7D7!important;
border-radius:30px 30px 0px 0px !important;
padding-top: 15px !important;
opacity:0.7;}

.controls_section{background-color:#FFFFFF!important;
box-shadow: none !important;
border: 1px solid #D7D7D7!important;
border-radius:30px 30px 0px 0px !important;
padding-top: 15px !important;
opacity:0.7;}

li.selected {box-shadow: none !important;
border-bottom: 0px solid #D7D7D7!important;
border-radius:0px !important;
opacity:0.7;}

#nav .nav_item.active{background-color: #00C}

/* LOADING MORE POSTS */
#previous_page_link, #next_page_link, .add_and_remove,
.mass_editor, .MissingE_marklist_title, .customize,
.recessed, .answer_avatar, .flat, .dashboard_options_form,
#auto_pagination_loader{background:#FFFFFF !important;}    

/* RETURN TO TOP */
div.return_to_top_icon{	background: none !important;
width: 0px !important;
height: 0px !important;
margin-top: 0px !important;
border-bottom: solid 30px #8b5666 !important;
border-right: 15px solid transparent !important;
border-left: 15px solid transparent !important;}

/* Sidebar shadow hack */
.count {text-shadow: none !important;}

.post img, .reply_text, .text_field, #tag_editor,
.source_url_questionmark_gradient, #post_one {border-radius: 12px !important;box-shadow: none !important;}
    
.posts, .tag {background-color:#F0F0F0!important; text-transform:none!important}

#post_two {border-radius: 0px 0px 8px 8px !important;box-shadow: none !important;}

#content_top, #content_bottom, .inline_external_image, .source_url {border-radius: 0px !important;
box-shadow: none !important; background-color:#fff;padding-top:0px!important;}

.tags{font-size:11px!important;margin-top:0px!important;
font-family:consolas;vertical-align:middle !important;}
.source_url{font-size:11px!important;
font-family:consolas!important;
text-transform:uppercase;
vertical-align:middle !important;
margin-right:4px!important;}
.footer_links{margin-top:20px!important;
vertical-align:middle !important;}

.photo {box-shadow: none !important; border: 1px solid #E7EAEC !important;border-radius:0px !important;}

.avatar_frame {border: 2px solid #fff !important;background-color:#FFF !important;}
.avatar_frame:hover {opacity: 0.7;}


.post_avatar {border: 4px solid #ffffff !important;
border-radius:50px !important;
box-shadow: 0px 10px 10px -12px #333333!important;}
    
.token {border: 0px solid #444 !important;background-color:#F0F0F0!important;}

img[src*="http://assets.tumblr.com/images/content_bottom_edit_form.png"],
img[src*="http://assets.tumblr.com/images/content_top_edit_form.png"]{height: 0px !important;background-color:#FFF!important;
box-shadow: none !important;
border: 0px solid #E7EAEC !important;
border-radius:0px !important;
padding:0px!important; margin:0px!important;}

#posts LI[class*=" post"] {box-shadow: none !important; border: 1px solid #E7EAEC !important;border-radius:0px !important;}

#posts LI[class*=" post"]:hover {box-shadow: none !important; border: 1px solid #BCBCBC !important;border-radius:0px !important;}

#posts LI[class*="notification"] {box-shadow: none !important;
border: 1px solid #E7EAEC !important;
border-radius:0px !important;
background-color:#FFFFFF !important;
border-bottom: 0px solid #E8E8E8!important;
border-top: 0px solid #333!important;
border-left: 0px solid #333!important;
border-right: 0px solid #333!important;}

#posts LI[class*="notification"]:hover {box-shadow: none !important;
border: 1px solid #E7EAEC !important;
border-radius:0px !important;
background-color:#FFFFFF !important;
border-bottom: 0px solid #BCBCBC!important;
border-top: 0px solid #E1E1E1!important;
border-left: 0px solid #E1E1E1!important;
border-right: 0px solid #E1E1E1!important;}

.post_info A,
.post_controls A,
.footer_links.with_tags SPAN A {
color:#333333 !important;}

.camera {background: transparent !important;
border-radius: 0px !important;
opacity: 0.7 !important;
padding-bottom: 4px;}

.image { display: block !important;
margin-left: auto !important;
margin-right: auto !important;}

#content_bottom {border-radius:0px !important;}

.notification a {border-bottom: 0px solid #3366FF !important;}
.notification blockquote {border-left: 3px solid #3366FF !important;}

/*SELECTION ARROW begin - points to dashboard selection*/
.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {
  background: none !important;
  width: 0px !important;
  height: 0px !important;
  margin-top: 0px !important;
  border-bottom: solid 4px #a27362 !important;
  border-right: 11px solid transparent !important;
  border-left: 11px solid transparent !important;
}

.selection_nipple:not([class="selection_nipple white"]) {
  border-bottom: solid 11px #a27362 !important;}

/*SIDE ARROWS*/
.arrow {
background: none !important;
width: 0px !important;
height: 0px !important;
border-right: solid 9px #a27362 !important;
border-bottom: 9px solid transparent !important;
border-left: 2px solid transparent !important;
border-top: 9px solid transparent !important;
margin-top: 0px !important;
}
.nipple {
background: none !important;
width: 0px !important;
height: 0px !important;
border-right: solid 6px #a27362 !important;
border-bottom: 6px solid transparent !important;
border-left: 0px solid transparent !important;
border-top: 6px solid transparent !important;
margin-top: 0px !important;
}
.nipple.border {
background: none !important;
width: 0px !important;
height: 0px !important;
border-right: solid 6px #a27362 !important;
border-bottom: 6px solid transparent !important;
border-left: 1px solid transparent !important;
border-top: 6px solid transparent !important;
margin-top: 1px !important;}

.notification, .answer_container, .answer_input, .dashboard_options_form,
.flat{border: 0px solid #FF00FF !important;}

/* SEARCH */
#search_query {background:#FFFFFF !important;border-radius:6px !important;}

.post p, .post blockquote, .post_info, .caption, .conversation_lines, .quote_source,
.MissingE_askTweaks_buttons {text-align: left !important;background-color:#fff!important;}

#left_column #posts .post .permalink{background-color:transparent!important;}

#dashboard_switch_blog_menu_current,
#dashboard_switch_blog_arrow_and_divider,
#dashboard_switch_blog_arrow_current{background-image:#C0C0C0!important}

#left_column #posts .notification {background-color:transparent!important;}

}
