css:
/*-  MEDIA QUERIES
----------------------------------------------------------------------*/
/* Disable iOS/WinMobile font size changes */
@media screen and (max-width: 600px),
screen and (max-device-width: 480px) {
	html {
		-ms-text-size-adjust: none;
		-webkit-text-size-adjust: none;
	}
}

/*
	Tiny
*/
@media screen and (max-width: 768px) {
body {
	min-width:320px !important;
	text-align: center !important;
}

h2 {
	padding: 8px 0 4px;
}

p, .grey  {
	line-height: 1.4em;
	padding: 0;
}

.contenu {
	width: 100% !important;
    margin: 0 auto !important;
}

#logo {
	position:relative !important;
	margin: 0 auto !important;
}

.creneaux {
	width: 100% !important;
	margin-right: 29px !important;	
}

#bloc_infos {
	width: 100% !important;
	top: 125px !important;
}

bloc_infos h1 {
	text-align: center !important;
}

#infos_musee {
	height: 453px !important;
}

.infos_titre {
	position: inherit !important;
	line-height: 2em !important;
	margin: -55px auto 0 !important;
	padding: 0 !important;
}

.bloc_transparent {
	height: auto !important;
	padding: 15px 10px 20px !important;
}

#bandeau_expo {
	padding: 50px 0 !important;
	margin: 0 !important;
	height: auto !important;
}

#bandeau_expo h1 {
	width: auto !important;
	font-size: 40px !important;
	text-align: center !important;	
	left: auto !important;
	top: auto !important;	
	line-height: 1.1em !important;	
}

#expo_content {
	width: 95% !important;
	margin: 0 2.5% !important;
	
}

#expo_content h2 {
	font-size: 26px !important;
	text-align: center !important;
	line-height: 1em !important;	
}


#expo_content h3 {
	font-size: 26px !important;
	text-align: center !important;
	margin: -6px 0 -5px !important;
}

#expo_content p {
	font-size: 16px !important;
	text-align: center !important;	
	top: auto;
	
}

#visuel_expo {
	width: 177px !important;
	float: none !important;
	margin: 0 auto !important;
	right: auto !important;
	top: auto !important;
}

#programme {
	margin: 35px 0 0 !important;
}

#travaux {
	float: left !important;
	width: 95% !important;
	margin: 40px 2.5% !important;
}

#travaux p {
	font-size: 18px !important;
}

#menu {
	margin: 0 !important;
	width: 100% !important;
}

.menu_top {
	height: auto !important;
}

.menu_top ul, .menu_bottom ul {
	float: left !important;
	padding: 0 !important;
}

.menu_top ul li, .menu_bottom ul li {
	width: 100% !important;
	height: 33px !important;
	font-size: 30px !important;
	padding: 20px 0 25px !important;
	border-left: none !important;
	background: -moz-linear-gradient(center top , #8BA2AB 0%, #869DA6 100%) repeat scroll 0 0 transparent;
	border-bottom: 1px solid #91A7B0;
}

.menu_top ul li:hover, .menu_bottom ul li:hover, .bouton:hover {
	background: #9d0052 !important;	
}


.bouton {
	border-left: none !important;
}

#credits, #contact, #mentions {
	width: 90% !important;
	left:  5% !important;
}

#footer {
	height: 100px !important;
}

#footer ul {
	float: none !important;
	display: inline-block !important;
	margin: 0 auto !important;
}

#footer ul li a {
	font-size: 15px !important;
}

#social {
	float: none !important;
	display: inline-block !important;
	margin: 5px auto 30px !important;
}

}
