css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tweakers.net") {
.priceFilter{
	float: right !important;

}

#bottom {
		min-width: 100px !important;
		text-align: center;
		margin-left: 322px;
		width: 1020px !important;
}

#top {
		margin-left: 322px;
		width: 1020px;
		text-align: center;
		height: 110px !important;
}
#contentArea{
		margin-left: 322px  !important;;
}

#logo {
		margin-top: 25px;
}

#menubar {
		margin-top: 0px !important;
}

#searchbar {
		width: 100%;
		margin-top: -60px !important;
		height: 60px !important;
		float: right;
}

#community {
		display: none;
}

#fpaItems {
		display: none;
}

#layout {
		background-color: #585858;
}

h1,h2,h3,p {
		margin: 5px 0px 0px !important;
}

#forumheading {
		background-color: #D8D8D8;
}

#fp_tabs_container {
		overflow: hidden;
		height: 37px;
		margin-bottom: 10px !important;
}

#fpaItems {
		overflow: hidden;
		margin-bottom: 10px !important;
}

#contentArea {
		background-color: #E1E1E1;
		padding-top: 0px !important;
		padding-left: 20px;
		padding-right: 20px;
}

table.highlights span,table.highlights .replies a {
		color: rgb(77, 80, 80) !important;
}

div.message div.messageheader div.date a {
		color: rgb(77, 80, 80) !important;
}

#messages {
		width: 980px;
}

.messagecontent {
		width: 826px;
}

table.highlights {
		border-top: 1px solid rgb(190, 190, 190) !important;
		margin-bottom: 25px;
		width: 100%;
		table-layout: fixed;
}

table.highlights td {
		line-height: 15px;
		border-bottom: 1px solid rgb(190, 190, 190) !important;
		padding: 4px 0px !important;
}

.article {
		font-size: 13px;
		overflow: hidden;
		margin-top: -5px !important;
}

.eenReactie {
		margin-top: 5px !important;
}

.reactieHeader {
		line-height: 20px;
		padding: 8px 0px 6px !important;
}

p.vaTopLinks {
		margin-bottom: 10px !important;
}

h2.detailHeader {
		margin-bottom: 15px !important;
}

div.message,div.faq {
		margin-bottom: 5px !important;
		line-height: 20px;
}

div.message div.post {
		padding: 5px 0px 8px 14px !important;
}

div.messageheader {
		background-color: rgb(200, 200, 200);
		position: relative;
		padding: 5px 14px !important;
		overflow: hidden;
}

div.messageheader div.date {
		position: absolute;
		top: 0px !important;
		left: 139px;
		font-size: 11px;
		color: rgb(97, 100, 100);
		white-space: nowrap;
		z-index: 1;
}

#bottom div.hr {
		background-color: rgb(37, 39, 39) !important;
		height: 6px;
		border-radius: 0px 0px 6px 6px;
}

#toggleButtonTxt {
		color: rgb(100, 100, 100) !important;
		background-color: #E1E1E1 !important;
}

#socialButtons {
		height: 28px;
		margin-bottom: 5px !important;
}

#reacties>.spacer {
		border-top: 1px solid rgb(215, 223, 221);
		margin: 5px 0px 5px !important;
}

.filterBox {
		overflow: hidden;
		margin-bottom: 10px !important;
}
}
