css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("chrd.lyon.fr") {
	*{
		margin: 0 !important;
		padding: 0 !important;
		-moz-box-sizing: border-box;
		box-sizing: border-box;
	}

    html, body{
		width: 100% !important; 
		background: white !important;
	}
	
	#page, #pageEntete, #enteteBandeau2, #pageCorps, #piedPage  {
		width: 100% !important;
	}
	#enteteBandeau2{
		background-position: -20px 0px !important;
	}
	#enteteLogo{
		height: 86px !important;
	}
	
	#enteteLogoVDL a img{
		display: none !important;
	}
	#enteteLogoVDL{
		clear: both !important;
		float: none !important;
		height: 20px !important;
	}
	#enteteLogoVDL a:after{
		color: #000 !important;
		content: "Ville de Lyon" !important;
		display: block !important;
		width: auto !important;
		height: 20px !important;
		text-align: center !important;
	}
	
	#dynMenu table{
		background: #eee !important;
	}
	#dynMenu td, #piedPageMenu a{
		display: block !important;
		width: auto !important;
		padding: 5px 10px !important;
		margin: 5px !important;
		height: auto !important;
	}
	
	#piedPageMenu{
		position: relative;
		background: #eee !important;
		height: auto !important;
		font-size: 0px !important;
		border-top: 2px solid #999 !important;
	}
	
	#piedPageMenu a{
		font-weight: bold !important;
		margin: 3px 5px !important;
		background: #B7C0C5 !important;
		font-size: 11px !important;
		color: #5B6A73 !important;
	}
	
	#piedPageMenu a:hover{
		color: #000 !important;
	}
	
	.box{
		position: relative !important;
		background: black;
		bottom: auto !important;
		right: auto !important;
		width: auto !important;
		height: auto !important;
	}
	
	#dynMenu td a:before, #piedPageMenu a:before, #menuGauche li a:before, .lienSuite:before{
		content: "> ";
	}

	#menuGauche, #menuDroite, .folder_center_center {
		width: 100% !important;
	}
	
	#menuGauche{
		padding-bottom: 5px !important;
		background: #eee !important;
		border-bottom: 2px solid #999 !important;
	}
	
	#menuThemeTitre{
		background: #B7C0C5 !important;
		color: #000 !important;
		padding: 0px 20px !important;
		border: 0 !important;
	}
	
	#menuGauche a:hover{
		background: none !important;
	}
	
	#menuGauche ul ul{
		margin-left: 30px !important;
	}
	
	#menuGauche li{
		width: auto !important;
		background : #B7C0C5 !important;
		padding: 5px !important;
		margin:  5px 10px !important;
	}
	#menuGauche ul ul li{
		margin: 5px 10px 5px 0!important;
	}
	
	#menuDroite .odd{
		height: auto !important;
	}
	
	#menuDroite .folder{
		width: 100% !important;
	}
	
	#menuDroite .folder h2{
		padding: 5px !important;
	}
	
	#menuDroite .box{
		text-align: center !important;
		bottom: auto !important;
		position: relative !important;
		background: #fff ! important;
	}
	
	#menuDroite .box a{
		float: none !important;
	}

	#enteteRecherche{
		float: none !important;
		background: #eee !important;
		padding: 5px !important;
	}
	
	#enteteRecherche input[type=text]{
		display: block !important;
		height: 20px !important;
		width: 100% !important;
		padding: 2px 2px 2px 18px!important;
		background: #fff url("http://cdn1.iconfinder.com/data/icons/softwaredemo/PNG/16x16/Magnifier2.png") left center no-repeat;
	}
	#enteteRecherche input[type=image]{
		display: none !important;
	}
	
	#socialnetwork{
		width: 100% !important;
		text-align: center !important;
		height: auto !important;
		padding: 5px !important;
	}
	
	#socialnetwork a{
	}
	
	#socialnetwork a img{
		display: inline-block !important;
	}
	
	span.currentItem{
		background: none !important;
	}
	span.currentItem:before{
		content: "> ";
	}
	
	#boiteInternautes{
		display: none;
	}
	
	#pageCentrale, #content, #container{
		width: 100% !important;
	}
	
	.contentAvecGaucheAvecDroite, .contentSansGaucheAvecDroite, .contentAvecGaucheSansDroite,.contentSansGaucheSansDroite, .majeur1, .majeur2 {
		width: 100% !important;
	}
	
	#full_content{
		background: #fff;
		color: #111;
		padding: 5px !important;
	}
	
	#breadcrumbs{
		background: #999 !important;
		color: #000 !important;
		padding: 5px !important;
	}
	
	#breadcrumbs a{
		color: #000 !important;
	}
	
	#breadcrumbs:before{
		content: "- ";
	}
	
	#full_content h1{
		margin: -5px -5px 10px !important;
		padding: 5px !important;
		background: #DC632A !important;
	}
	
	#content img{
		max-width: 100% !important;
	}
	
	#content li{
		padding-left: 10px !important;
	}
	
	.widget_left, .widget_right, .widget_center{
		display: block !important;
		float: none !important;
		width: 100% !important;
	}
	
	#stwrapper, #stwrapper iframe, #stwrapper div{
		display: none !important;
		width: 0px !important;
	}

	#agenda{
		padding: 2.5px 5px !important;
	}
	
	.evenement{
		padding: 5px !important;
		margin: 2.5px 0 !important;
		height: auto !important;
	}
	
	.evenement .lienSuite{
		background: none !important;
		bottom: 5px !important;
		right: 5px !important;
		padding: 0 !important;
		left: auto !important;
	}
	
	.evenement .type{
		padding-right: 5px !important;
		border-style: solid !important;
		border-width: 0 1px 1px 0 !important;
		margin-left: -5px !important;
		padding-left: 10px !important;
	}
	
	.evenement h1{
		border-style: solid !important;
		border-width: 0 0 1px 0 !important;
		margin: 0 -5px !important;
		padding: 10px !important
	}
	
	.vignette{
		display: none !important;
	}
	
}
