css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

#page-proxy-favicon[src$="google.com/favicon.ico"],
tab[image$="google.com/favicon.ico"] .tab-icon,
.bookmark-item[statustext*="google.com"] .menu-iconic-icon,
.bookmark-item[statustext*="google.com"] .toolbarbutton-icon,
.bookmark-item[image*="google.com"]:not([container]) .menu-iconic-icon,
.bookmark-item[image*="google.com"]:not([container]) .toolbarbutton-icon {
  width: 0 !important;
  padding-left: 16px !important;
  background: transparent url("data:image/x-icon;base64,") center center no-repeat !important;
}


#page-proxy-favicon[src$="google.co.uk/favicon.ico"],
tab[image$="google.co.uk/favicon.ico"] .tab-icon,
.bookmark-item[statustext*="google.co.uk"] .menu-iconic-icon,
.bookmark-item[statustext*="google.co.uk"] .toolbarbutton-icon,
.bookmark-item[image*="google.co.uk"]:not([container]) .menu-iconic-icon,
.bookmark-item[image*="google.co.uk"]:not([container]) .toolbarbutton-icon {
  width: 0 !important;
  padding-left: 16px !important;
  background: transparent url("data:image/x-icon;base64,") center center no-repeat !important;
}
