css:
@-moz-document url-prefix("http://weibo.com/"), url-prefix("http://www.weibo.com/") {
.WB_global_nav .gn_header{width:830px}
.gn_title,.gn_logo_v2 {display:none}
.gn_title[node-type=home]{display:block}
.W_main {width:830px;background:#eee}
.W_main_c {background:#fff}
#Box_center {background:#fff}
#Box_right>div{display:none}
#Box_right #pl_rightmod_myinfo{display:block}
.W_main_bg {position:relative}
.W_main_l {width: 230px;right: 0;top:0;float:none;position: absolute;}
#pl_leftnav_common,#pl_leftnav_group {display:block}
.WB_left_nav .lev a:hover, .WB_left_nav .lev2 a:hover, .WB_left_nav .lev2 a.lev_curr, .WB_left_nav .lev2 a.lev_curr:hover,.WB_left_nav .lev_curr {background-image:none!important}
.W_main_a {background:#eee}
div[node-type=left_fixed],#pl_leftnav_app {display:none}

.B_fans_myconn .W_main_r,
.B_favor_me .W_main_r {display:none}

.B_index .W_main_l,
.B_send_me .W_main_l,
.B_closefriend  .W_main_l{top: 165px;border-top: 1px solid #ccc;}

.global_footer {width:790px}
.global_footer dl.list {width:142px}

.send_weibo .S_textb,.send_weibo .S_txt2>a,.gn_logo,#pl_content_biztips {display:none!important}
.send_weibo .S_txt2 {color:#fff}
.send_weibo .title {background:none}
.S_txt2 span {color:#999}
.S_txt2 .S_error {color:red}
.WB_global_nav .gn_nav {margin:0}

.S_profile .profile_tabbig {width:830px}
.S_profile .W_main_2r {padding:0;width:230px}
.S_profile .profile_right_module {margin:-1px 0;border:none}
.S_profile .prm_app {border:none}
.S_profile .pul_list_big {width:190px;height:166px}
.S_profile .pul_list_big .pul_item,.B_profile .pul_list_fansbig .pul_item {margin:0 12px 20px 20px}
.S_profile .pul_list_fansbig {width:190px;height:224px}
.S_profile .prm_app_pic .piclist {width:190px}
.S_profile .prm_app_pic .piclist li {margin:10px 8px 0 12px}
.S_profile .profile_right_module {overflow:hidden}
.S_profile .profile_right_module>div {display:none}
.S_profile #pl_profile_moduleMyRelation,.S_profile  #pl_profile_modulealbum {display:block}
#pl_profile_unfollow,.popular_buss,*[node-type=feed_spread] {display:none}
#Pl_Core_Nav__2 {overflow:hidden}
}
