css:
@-moz-document domain("tweakers.net") {

/** Spoilers zijn leesbaar bij mouse over */ 
div.spoiler:hover  
    { 
    color: white !important; 
    } 

/* Verkleinen notepad in myreact */ 
#mynotepad textarea { 
    height: 150px !important; 
    width: 450px !important; 
} 

/* Weghalen True reclame */ 
#true 
    { 
    display: none !important; 
    } 
/* weghalen sidebar */ 
#sidebar 
    { 
    display: none !important; 
    } 

/* Weghalen newsletter subscription FP */     
#newsletter 
    { 
    display: none !important; 
    } 

/* Weghalen jobs ruimte FP */ 
#jobs 
    { 
    display: none !important; 
    } 

/* Weghalen Twitter/Facebook van FP */ 
#social 
    { 
    display: none !important; 
    } 

/* Header website blauw */     
#top { 
    background-image: none !important; 
    background: #394551 !important;
    background: -webkit-linear-gradient(top, #394551 0%,#1a32cc 44%,#0052cc 47%,#7e9ecc 100%) !important; /* Chrome10+,Safari5.1+ */
    background: -ms-linear-gradient(top, #394551 0%,#1a32cc 44%,#0052cc 47%,#7e9ecc 100%) !important; /* IE10+ */
    background: linear-gradient(to bottom, #394551 0%,#1a32cc 44%,#0052cc 47%,#7e9ecc 100%) !important; /* W3C */
}

/* Nieuwstitel, topictitel */ 
h1, h2, h1 a, h2 a { 
    color: #3240A7 !important; 
} 

/* Jobs onderdeel uit header */ 
#menu ul:first-child li.last.more div ul li:first-child, 
#menu ul:first-child li.last.more div ul li+li+li+li { display: none !important; } 
#menu ul:first-child li.last.more div ul li+li+li+li+li { display: block !important; } 

/* Pricewatch filters naar rechts */ 
#filterContainer  
    { 
    float: right; 
    margin: 0 0 0 30px; 
    } 

/* messageheader topicstarter posts rood */ 

div.topicstarter div.messageheader {background: #ffcccc !important;} 

/* messageheader ownpost groen */ 

div.message div.ownpost {background-color: rgb(200, 230, 210) !important;}  

/* Post titels donkerder */ 
div.message div.messageheader  
    { 
    background-image: -moz-linear-gradient(center top, rgb(234,235,235), rgb(217, 218, 218)) ; 
    background-image: -webkit-gradient(linear, center top, rgb(234, 235, 235), rgb(217, 218, 218)) ; 
    background-image: -o-linear-gradient(center top, rgb(234, 235, 235), rgb(217, 218, 218)) ; 
    background-image: -ms-linear-gradient(center top, rgb(234, 235, 235), rgb(217, 218, 218)) ; 
    background-image: linear-gradient(center top, rgb(234, 235, 235)m rgb(217, 218, 218)) ; 
    border-bottom: 1px solid rgb(207, 208, 208); 
    } 

/* Posts iets dichterbij in een topic */ 
div.message  
    { 
    margin-bottom: 3px !important; 
    border: 1px solid rgb(195, 195, 195) !important; 
    }     
     
/* Aanpassen kleur postdatum in messageheader */ 
.date a  
    { 
    color: #444444 !important; 
    } 

/* Verkleinen tussenruimte myreact */ 
td, th { 
    padding: 4px 5px!important; 
} 
  
/* Weghalen Quick Reply */  
#quickreply_form 
    { 
    display: none !important; 
    }     
#reageer 
    { 
    display: none !important; 
    }        


/* Tracker aansluiten */
/* OUD */
.trackerPopup.fixed {
    width: 300px;
    position: absolute;
    top: 0;
    left: -336px !important;
    display: block;
    -webkit-box-shadow: -6px 6px 20px -6px black;
}
/* Tracker lettertjes */
.trackerPopup .trackeritem li {
    padding: 1px 0 1px 0;
}


/* NIEUW */

/* body margin 0 */
body.trackerVisible {
margin-left: 0;
}
div#trackerContainer {
    position: relative;
    top: 16px;
    right: -88px;
    background-color: #313232;
}
div#trackerStretch { display: none; }
div.fakeTop { display: none; }
div#tracker { left: 0 !important; background: none;}

/* Verbreden post area */ 
#messages, .topic_actions { 
    width: 956px !important; 
} 
.messagecontent { 
    /* width: 800px !important; */
    width: 640px !important; /* Ik bedoel priveberichten, hoop niet dat ik nu iets anders sloop */
} 
div.post { 
    padding: 6px 0 6px 10px !important; 
} 

/* Weghalen karma in profiel */  
div.karma 
    { 
    display: none !important; 
    }  
     
/*searchbar omhoog*/ 
#search {
    position: absolute;
    top: -90px;
    right: 220px;
} 
/*Forum en website dichter naar menu*/ 
#searchbar { 
    height: 10px !important; 
    background: #313232 !important; 
    position: relative;
    top: 7px;
    z-index: 1;
    border-radius: 0 !important;
}
/* Achtergrondkleur */ 
body{
    margin: 0; /* Trackergedoe weg */
    background: #c6c6c6; /* Old browsers */
    background: -webkit-linear-gradient(top, #c6c6c6 0%,#828893 36%,#2f333d 100%); /* Chrome10+,Safari5.1+ */
    background: -ms-linear-gradient(top, #c6c6c6 0%,#828893 36%,#2f333d 100%); /* IE10+ */
    background: linear-gradient(to bottom, #c6c6c6 0%,#828893 36%,#2f333d 100%); /* W3C */
    background-attachment: fixed;
} 
#contentArea{
    background-color: #E0E6EE;
    padding-left:5px;
    padding-right: 5px;
    padding-top: 0;
    padding-bottom: 70px;
    top: 6px;
}
#searchbar { 
    background:#313232 !important; 
} 
#top { 
     border-bottom: 1px solid #313232 !important; 
} 
div.hr { 
    background:#313232 !important; 
} 

/*topic pagina nummers in myreact zwart en grijs wanneer bezocht.*/ 
td.topic small a:visited { 
    color: rgb(97, 100, 100) !important; 
} 
td.topic small a { 
    color: black !important; 
} 

/*topics in myreact zwart*/ 
td.topic a { 
    color: black !important; 
} 

/*menubar 100% van het scherm*/ 
#menubar { 
    z-index: 2;
    width: 1730px;
    box-shadow: 0 -5px 10px rgba(0, 0, 0, 0.40);
    border: 1px solid rgba(255, 255, 255, 0.15);
    border-bottom: none;
    border-radius: 20px 20px 0 0;
    background: -webkit-linear-gradient(rgba(40, 40, 40, 0.95),rgba(0, 0, 0, 1) 68px);
}

/* Notification fix */
#userbar .newNotificationsCount {
    position: absolute;
    z-index: 999;
}

/*Custom quotes*/ 
div.messagecontent blockquote div.message-quote-div { 
    background-color: #FFFFFF !important; 
    border: 1px solid #555555 !important; 
    color: #111111 !important; 
} 

/*
 * Frontpage
 */

/* Plaatjes op logischere plek */
#fpaItems {
    overflow: hidden;
    margin-bottom: 25px;
    position: absolute;
    right: 87px;
    width: 244px;
    top: 94px;
}

/* Ik bepaal zelf wel wat ik interresant vind */
#highlights {
    display: none;
}
/* Relevante blaat rechts */
.relevancyColumn {
    position: absolute;
    width: 328px;
    margin-left: 24px;
    right: -336px;
    top: 0;
    background-color: #CFCFCF;
    padding: 4px !important;
    /* -webkit-box-shadow: 6px 6px 16px -2px black; Meerdere blokken overlappen lelijk zonder containing div */

}
/* Nieuws meer ruimte geven */
.articleColumn {
    width: 980px;
}
/* Reacties meer ruimte */
#reacties {
    width: 980px;
}


/*
 * Footer
 */

#bottom {
    bottom: -7px;
    height: 80px;
}
/* compacter */
#about {
    height: 40px;
}
#about p:first-child {
    margin: 8px 0 0;
}

/* Blokuitlijningen pricewatch enzo, hoop dat dit niks anders sloopt */  
#listing, #filterContainer, .articleColumn, .relevancyColumn, .mainColumn, .secondColumn 
{ 
    padding-top: 4px !important;
}


/* FP Header */

#header {
    padding-top: 10px;
}



/* FP Reacties */

.reactie {
    background: none;
    border-left: none;
}

.reactieContainer {
    border-left: none !important;
    margin-left: -40px;
    padding-left: 40px;
}

.reactie .topBorder {
    display: none;
}

.reactieHeader {
    padding: 0 0 10px;
}

.eenReactie {
    margin-top: 8px;
    border: 1px solid black;
    padding: 4px;
    -webkit-border-radius: 4px;
    -moz-border-radius: 4px;
    border-radius: 4px;
    background: #e2e2e2; /* Other browsers */
    background: -webkit-linear-gradient(top, #e2e2e2 0%,#dbdbdb 20%,#d1d1d1 21%,#fefefe 100%); /* Chrome10+,Safari5.1+ */
    background: -ms-linear-gradient(top, #e2e2e2 0%,#dbdbdb 20%,#d1d1d1 21%,#fefefe 100%); /* IE10+ */
    background: linear-gradient(to bottom, #e2e2e2 0%,#dbdbdb 20%,#d1d1d1 21%,#fefefe 100%); /* W3C */    
}

.reactieBody.hidden, .reactieBody.informative, .reactieBody.spotlight {
    border-top: 0;
    margin-left: 0;
    padding-left: 0;
}

.reactieBody.informative {
    background-color: #F7FBF6;
    border: 0;
    background: -webkit-radial-gradient(center, ellipse cover,  rgba(144,244,137,0) 0%,rgba(72,242,82,0.12) 50%,rgba(0,239,27,0) 100%); /* Chrome10+,Safari5.1+ */
    background: -ms-radial-gradient(center, ellipse cover,  rgba(144,244,137,0) 0%,rgba(72,242,82,0.12) 50%,rgba(0,239,27,0) 100%); /* IE10+ */
    background: radial-gradient(ellipse at center,  rgba(144,244,137,0) 0%,rgba(72,242,82,0.12) 50%,rgba(0,239,27,0) 100%); /* W3C */
}

.reactieBody.spotlight {
    background-color: #F7FBF6;
    border: 0;
    background: -webkit-radial-gradient(center, ellipse cover,  rgba(144,244,137,0) 0%,rgba(72,242,82,0.28) 50%,rgba(0,239,27,0) 100%); /* Chrome10+,Safari5.1+ */
    background: -ms-radial-gradient(center, ellipse cover,  rgba(144,244,137,0) 0%,rgba(72,242,82,0.28) 50%,rgba(0,239,27,0) 100%); /* IE10+ */
    background: radial-gradient(ellipse at center,  rgba(144,244,137,0) 0%,rgba(72,242,82,0.28) 50%,rgba(0,239,27,0) 100%); /* W3C */
}

}
