css:
@-moz-document domain('tweakers.net') {
    body {background-color:#545454; min-height: 100%; position: relative;}
    #layout {min-width: 1346px; width: 1346px; margin-right: auto; margin-left: auto; background-color:#ffffff;}
    #contentArea { margin: 0 auto !important; left: 155px !important; margin-right: 175px !important; margin-left: 130px !important; }
    #top {min-width: 1346px; width: 1346px;}
    #bottom {min-width: 1346px; width: 1346px; position: relative!important;}
    #menubar {background: linear-gradient(#262828, #1C1E1E) repeat scroll 0 0 #444444; min-width: 1346px; width: 1346px!important;}
    #tweakbase {background: none repeat scroll 0 0 #EDEEEE;}
    .trackerPopup {background: none repeat scroll 0 0 #404040; width:316px !important; left: -335px !important; }
    .fpaItem div.textContainer {background-color:#444444;}
    .fpaItem:hover .imageContainer {opacity:0.85} 
    .reactie, .articleColumn {line-height: 1.2}
    table.highlights td {padding: 3px}
    .reactie .topBorder {background: none}
    .popup trackerPopup {display: block; width: 252px; }
    .mainColumn, #messages { width: 100% !important; }
    .messagecontent { width: 80% !important; }
    .popularProductListing, .secondColumn, .mainColumn #community, .relevancyColumn, #trackerHeader .fancyButton, .trackerPopup.fixed .close, .bulletSeparatedList, .icon.tracker, #true, #sidebar { display: none !important; } 
    #menu, #search, #contentArea, #ticker p, #about { width: auto !important; }
    #userbar { right: 10px; }
    #search .keywordSearch { margin: 0 17px 0 0 !important; float: right; }
    div.resizewarning div { display: none !important; }
    img.resized { border: 1px dashed #B41039 !important; display: block; float: none; }
}
