css:
@-moz-document domain("youtube.com") {

*:not(#logo){
background: black !important;
}


body,button, a{
background: black !important;
color: #ddd !important;
}


a, abbr, acronym, address, applet, b, big, blockquote, button, canvas, caption, center, cite, code, dd, del, dfn, div, dl, dt, em, embed, fieldset, font, form, h1, h2, h3, h4, h5, h6, hr, html, i, iframe, img, ins, kbd, label, legend, li, menu, object, ol, p, pre, q, s, samp, small, span, strike, strong, sub, sup, table, tbody, td, tfoot, th, thead, tr, tt, u, ul, var {
color: #aaa !important;
}


#yt-masthead-container, #watch7-action-buttons, #watch7-action-panels, #watch7-discussion, *{
border: none !important;
}


.yt-horizontal-rule{
display: none !important;
}

*{
text-shadow: none !important;
}

.feed-item-container:hover, .yt-uix-tile.yt-lockup-list.yt-tile-default.yt-grid-box, .guide-count.yt-uix-tooltip, li.subscribable-content-rec.yt-rounded:hover {
background: black !important;
}

#masthead-search-term, textarea{
background: #333 !important;
color: #ddd !important;
}

.overlay
{
 display: none !important;
}
}
