css:
/* Breedte aanpassingen*/ 
#searchbar { 
  margin: 34px auto 0 auto; 
  width: 1020px; 
} 
#bottom div.hr, 
.pageTabsContainer, 
.galleryHeadingContainer { 
    width: 1020px; 
    margin: 0px auto 0 auto; 
} 
#contentArea{ 
    width: 980px; 
    padding: 20px 20px 180px 20px; 
    background-color: #ffffff;  
} 
#menubar { width: 982px; } 
#fp_tabs_container, 
#forum_tabs { 
    margin-left: -20px; 
    margin-right: -20px; 
} 
#footer{ margin-right: 20px; } 

/*Achtergrondkleur minder licht*/ 
#layout { background-color: #dcdddd; } 

/*FP reacties, ruimte correcties, duidelijke header en mod opmaak*/ 
.eenReactie { margin-top: 5px; width: 600px; } 
.reactieContent { padding: 5px 15px 5px 0px; } 
.reactieHeader { 
    padding: 1px 0px 1px 15px; 
    background-color: #E7E8E8; 
    margin-left: -20px; 
} 
.reactieBody.hidden .reactieHeader { 
    padding: 0px 10px; 
    opacity: 0.35; 
} 
.reactieBody.informative,  
.reactieBody.informative .reactieHeader { 
    background-color: #EFFFE2; 
    border-left: 1px solid #CFD4C9; 
} 
.reactieBody.spotlight,  
.reactieBody.spotlight .reactieHeader { 
    background-color: #D8FFC2; 
    border-left: 1px solid #BECFBE; 
} 

/*FP hoogte en kleur nieuwsitems*/ 
table.highlights td { line-height: 0px; padding: 5px 8px; } 
.frontPageLink { color: #212323; text-decoration: none; } 
.frontPageLink:visited { color: #B9133c; } 

/*Forum gedeelte, lage (dunne) regels*/ 
table.listing td,  
table.listing th { padding: 1px 5px; }
