css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url("http://www.baidu.com/index.php") {

a{color: #00DDDD !important;}

div.s-title em, h5.pk-title span, div.bm-video-tip td
{color: #DD0000 !important;}

div.s-select-input-mask, input.cite-url-input, input.cite-name-input, div.s-select-layer-option, td.popContent, div.pop_title{background: -moz-linear-gradient(top, #151515, black) !important;}
p#nv{margin-top: -10px !important;}
body{background: #111111 !important;	}
#lg{background-image: url(http://hiphotos.baidu.com/soinlia/pic/item/c8d19810b31bb0519deb2299367adab448ede0d9.jpg);	background-repeat: no-repeat !important;background-position: center center !important;opacity:0.9 !important;}
#lg img{opacity:0 !important;}
.nv a img {border-radius:5px 5px 0px 0px !important;background:rgba(255,255,255,0.3) !important;	opacity:0.8 !important;}

/* title a */
p#nv a, p#u a{color: #00FFAA !important;text-shadow: -1px -1px 1px black,1px -1px 1px black,-1px 1px 1px black,1px 1px 1px black !important; }

/* title b */
p#nv b{color: #FF8800 !important;text-shadow: -1px -1px 1px black,1px -1px 1px black,-1px 1px 1px black,1px 1px 1px black !important; }

/* title one */
em.s-mod-shortcut, span.s-topsearch-title, em.s-mod-feed{color: #FF0000 !important;text-shadow: -1px -1px 1px black,1px -1px 1px black,-1px 1px 1px black,1px 1px 1px black !important; }

/* title two */
em.sc-dragitem{color: #99FF33 !important;text-shadow: -1px -1px 1px black,1px -1px 1px black,-1px 1px 1px black,1px 1px 1px black !important; padding-right: 10px !important; padding-left: 10px !important; }

li.s-ncf-nav{border-style: none !important; }

div.s-nplus, div.s-content, div.s-title{background-color: #000000 !important; }

input#kw{color: #FFFF00 !important; background-color: #000000 !important; opacity: 0.8 !important; }

/* news */
div.s-content a, div.s-title b{color: #0088FF !important;text-shadow: -1px -1px 1px black,1px -1px 1px black,-1px 1px 1px black,1px 1px 1px black !important; padding-left: 10px !important; padding-right: 10px !important; }

/* right up corner */
a.user-name-top, p#u a, div.s-username-menu a, div.s-ncf-name a
{background-color: black !important; color: #B894FF !important;text-shadow: -1px -1px 1px black,1px -1px 1px black,-1px 1px 1px black,1px 1px 1px black !important; }

form[action] input[name=wd]{background:rgba(255,255,255,0.3) !important;}.btn_wr {background: transparent !important;}.s_btn_wr {background: none !important;}#fm input[name="kw"],#head input[name="kw"] {opacity:0.8 !important;}#fm input[type="button"],#fm input[type="submit"],#head input[type="button"],#head input[type="submit"]{-moz-appearance:none !important;color:transparent !important;margin:0px 32px 0px -6px !important;border:1px solid #bbb !important;border-radius:0px 5px 5px 0px !important;background:url(data:image/png;base64,) no-repeat center center, -moz-linear-gradient(top, rgba(68,68,68,1) 0%, rgba(102,102,102,1) 50%, rgba(60,60,60,1) 51%, rgba(68,68,68,1) 71%, rgba(51,51,51,1) 100%) !important;width: 95px !important;height: 32px !important;}	#fm input[type="button"]:hover,#fm input[type="submit"]:hover,	#head input[type="button"]:hover,#head input[type="submit"]:hover{		box-shadow: 0px 0px 1px 0px gray !important;	}	#fm input[type="button"]:focus,#fm input[type="submit"]:focus,	#head input[type="button"]:focus,#head input[type="submit"]:focus{		box-shadow: 0px 0px 1px 0px gray !important;	}

div[class='mod-sc s-mod']{opacity:0.8 !important;}
div[class='mod-sc s-mod']:hover{opacity:1.0 !important;}
#container table {border-radius:5px 5px 5px 5px !important;background:rgba(255,255,255,0.8) !important;}

div.s-title, div.s-pk-mod h5, div.s-pk-hithot-b, div.s-c-feeditems div.feed-item, div.pk-addhandler, div.pk-caption, div.pk-row, span.pk-anchor-wrap a, div#playerpanel-songinfo, div.hithot-head-b{background: -moz-linear-gradient(top, #222222, #000000) !important;}

div.s-ncf, div#bm-tip a, div.bm-video-tip{background: -moz-linear-gradient(top, #222222, #000000) !important;}

div span.weather-col, div a.weather-set, div.s-feedback a, div.status-alldone
{background: -moz-linear-gradient(top, #222222, #000000) !important;border: 1px solid black !important;}

div.s-ncf-name a{background: none !important;}

DIV#s_modules, div#m{margin-top: -20px !important;}

span.weather-col img, div.s-pk-baidu-fm{opacity: 0.7 !important;}

img{border-radius: 3px 3px 3px 3px !important;}

div.s-ncf{margin-left: -19px !important;padding-left: 30px !important;padding-right: 40px !important;}

div.hithot-head-b
{margin-top: -19px !important;margin-left: -19px !important;padding-left: 20px !important;padding-top: 9px !important;padding-bottom: 7px !important;}

div.feed-item
{margin-left: 0px !important;padding-left: 20px !important;margin-right: 0px !important;}

div.pk-addhandler{margin-top: -10px !important;}

div.s-ncf-name a{font-weight: bolder !important;}

}
