css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tweakers.net") {
	html
	{
		background-color:#222;
	}

	#menubar
	{
		width:100%;
	}

	div#top
	{
		border-bottom:#000 0 solid;
	}

	div#contentArea
	{
		background-color:#333;
		padding-left:20px;
		padding-right:20px;
		border:#c3c6c6 1px solid;
	}

	html #bottom div.hr
	{
		background:none;
		height:0;
	}

	#ticker
	{
		background-color:#212323;
		border-top:1px solid #444;
	}

	#searchbar
	{
		background-color:#222;
		background-image:-moz-linear-gradient(#333,#222);
		background-image:-webkit-linear-gradient(#333,#222);
		background-image:linear-gradient(#333,#222);
	}

	.pageTabs li
	{
		background-color:#444;
		background-image:-moz-linear-gradient(#333,#222);
		background-image:-webkit-linear-gradient(#333,#222);
		background-image:linear-gradient(#333,#222);
	}

	.pageTabs li.active
	{
		background-color:#444;
		background-image:-moz-linear-gradient(#444,#333);
		background-image:-webkit-linear-gradient(#444,#333);
		background-image:linear-gradient(#444,#333);
	}

	.pageTabs li p,.pageTabs li a
	{
		color:#ddd;
	}

	#fp_tabs li.active a
	{
		color:#fff;
	}

	.frontPageLink:visited
	{
		color:#7D3475;
	}

	a
	{
		color:#0483cc;
		text-decoration:none;
	}

	.relatedContent a
	{
		color:#0483cc;
	}

	p.author,p.author a
	{
		color:#c6cccc;
	}

	body
	{
		color:#ddd;
	}

	#pricewatch,#jobs,#poll,#newsletter,#sponsored,#textlink,#social .twitterButton,#social .facebookButton
	{
		border:1px solid #DDDEDE;
		border-radius:1px;
		background:#444;
		background-image:-moz-linear-gradient(#555,#444);
		background-image:-webkit-linear-gradient(#555,#444);
		background-image:linear-gradient(#555,#444);
	}

	.reactie
	{
		background:#333;
	}

	.reactieBody.informative
	{
		background-color:#4b4d4b;
		border:1px solid #CFD4C9;
	}

	.reactieBody.hidden
	{
		background-color:#4c4d4d;
		border:1px solid #D9DBDB;
		cursor:pointer;
	}

	#toggleButtonTxt
	{
		display:inline-block;
		background:none;
	}

	.reviewToc a
	{
		color:#fff;
		text-decoration:none;
	}

	#tweakbase
	{
		background:#222;
	}

	#tabs
	{
		background:#222;
	}

	.commonSpecBlock
	{
		border:1px solid #222;
		border-radius:1px;
		background:#EDF4F6;
		background-image:-moz-linear-gradient(#222,#333);
		background-image:-webkit-linear-gradient(#222,#333);
		background-image:linear-gradient(#222,#333);
	}

	.productEditionListing.commonSpecBlock .showLink,.productEditionListing.commonSpecBlock .hideLink
	{
		cursor:pointer;
		color:#0483cc;
	}

	#filter
	{
		background:#EDEEEE;
		background-image:-moz-linear-gradient(#222,#333);
		background-image:-webkit-linear-gradient(#222,#333);
		background-image:linear-gradient(#222,#333);
	}

	#scorecardView
	{
		background-color:#333;
	}

	h3,h3 a
	{
		color:#FFF;
	}

	#scorecard .scores
	{
		background-color:#222;
	}

	#scorecard .scores .arrow
	{
		display:none;
	}

	.tweakbaseHeader .subtitle .bulletSeparatedList a
	{
		color:#0483cc;
	}

	#breadCrumbListing span
	{
		color:#0483cc;
	}

	a.fancyCheckbox
	{
		color:#0483cc;
	}

	.pageTabs li p,.pageTabs li a:hover
	{
		color:#0483cc;
	}

	.reactie .topBorder
	{
		background:#666;
	}

	.article table
	{
		background-color:#333;
	}
}
