css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/*Appmenu Button*/
#appmenu-button{
    background-color: rgb(43,87,154) !important;
    background-image: none !important;
    border: none !important;
    box-shadow: none !important;
    border-radius: 0 !important;
    text-shadow: none !important;
}

#appmenu-button:hover,
#appmenu-button:hover:active{
    background-color: rgb(62,109,181) !important;
}

/*Tabs*/
.tabs-newtab-button,
.tabbrowser-tab{
    -moz-border-image: none !important;
    border-style: solid !important;
    border-width: 1px 1px 0 1px !important;
    -moz-border-top-colors: none !important;
    -moz-border-left-colors: none !important;
    -moz-border-right-colors: none !important;
    padding: 4px 4px 5px !important;
}

.tabbrowser-tab[selected]{
    background: white !important;
    border-color: rgb(185,185,185) !important;
    color: rgb(43,87,154) !important;
}

.tabs-newtab-button,
.tabbrowser-tab:not([selected]){
    background: transparent !important;
    border-color: transparent !important;
}

.tabbrowser-tab:hover:not([selected]){
    color: rgb(43,87,154) !important;
}

/*Toolbar*/
#addon-bar,
#PersonalToolbar,
#nav-bar{
    background: white !important;
    border-color: rgb(185,185,185) !important;
    border-radius: 0 !important;
}

#nav-bar{
    padding-right: 3px;
}

/*Urlbar*/
#searchbar > .searchbar-textbox,
#urlbar{
    border-color: rgb(217,217,217) !important;
    border-radius: 0 !important;
}

#searchbar:hover:not([focused]) > .searchbar-textbox,
#urlbar:hover:not([focused]){
    border-color: rgb(126, 180, 234) !important;
}

#searchbar > .searchbar-textbox[focused],
#urlbar[focused]{
    border-color: rgb(86, 157, 229) !important;
}

/*Stop, Go, Reload*/
#urlbar-go-button,
#urlbar-reload-button,
#urlbar-stop-button{
border-left: 1px solid rgb(217,217,217) !important;
padding: 3px 4px 4px 6px !important;
}

/*Body*/
#browser-border-start,
#browser-border-end{
    background-color: rgb(185,185,185) !important;
}
