css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("iqiyi.com") {

/* black background and gray text*/
body, div.gui_FlashPlayer, div.g-lay-box, div.look_t, div.qt-cmtBox, div.footer, div.qt-cmtBox-bg, div.qt-cmtBg
{background: black !important;color: #AAAAAA !important;}

/* link color(cyan) */
a{color: #009999 !important;}

/* gray gradient */
div.gui_Header, div.gui_TopInfo, div.gui_scroll, div.title_zy, div.title_zy span, div.hd_zy p, div.hide_l, div.fx_box, div.avideo, div.top10_general, div.bd
{background: -moz-linear-gradient(top, #151515, black) !important;border: 0px !important;}

/* opacity is 0.2 */
div.headBg img, span#topInfoC_add a{opacity: 0.2 !important;}

/* text(green) and bold and colorful shadow */
div.crumb_new h1, p.hd_r a, div.title_zy span
{color: #00BB00 !important;font-weight: bolder !important;text-shadow: -1px -1px 1px black,1px -1px 1px black,-1px 1px 1px black,1px 1px 1px black,-2px -2px 3px #FF00FF,2px -2px 3px #FF00FF,-2px 2px 3px #FF00FF,2px 2px 3px #FF00FF,-3px -3px 3px #0066CC,3px -3px 3px #0066CC,-3px 3px 3px #0066CC,3px 3px 3px #0066CC !important; padding-left: 10px !important; padding-right: 10px !important; }

/* text with red */
p.pLeft a.selected, [style=background]
{color: #FF0000 !important;}

/* text with gray */
div.crumb_new a, p.pLeft a, dl.qt-cmtSection dd, strong, span
{color: #AAAAAA !important;}

/* text with yellow and colorful shadow */
div.headBg li a, div.headBg li span, p.headC span, p.headC a
{color: #FF5500 !important;text-shadow: -1px -1px 1px black,1px -1px 1px black,-1px 1px 1px black,1px 1px 1px black,-2px -2px 3px #00FFAA,2px -2px 3px #00FFAA,-2px 2px 3px #00FFAA,2px 2px 3px #00FFAA,-3px -3px 3px #0066CC,3px -3px 3px #0066CC,-3px 3px 3px #0066CC,3px 3px 3px #0066CC !important; }

}
