css:
/*Autor: skorki */
@namespace url(http://www.w3.org/1999/xhtml); 

@-moz-document domain(strims.pl) {
/*tlo strony*/
#template {
background: -moz-linear-gradient(top,  rgba(0,0,0,0.65) 0%, rgba(0,0,0,0.65) 1%, rgba(0,0,0,0.05) 3%, rgba(0,0,0,0.05) 100%)!important; /* FF3.6+ */
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(0,0,0,0.65)), color-stop(1%,rgba(0,0,0,0.65)), color-stop(3%,rgba(0,0,0,0.05)), color-stop(100%,rgba(0,0,0,0.05)))!important; /* Chrome,Safari4+ */
background: -webkit-linear-gradient(top,  rgba(0,0,0,0.65) 0%,rgba(0,0,0,0.65) 1%,rgba(0,0,0,0.05) 3%,rgba(0,0,0,0.05) 100%)!important; /* Chrome10+,Safari5.1+ */
background: -o-linear-gradient(top,  rgba(0,0,0,0.65) 0%,rgba(0,0,0,0.65) 1%,rgba(0,0,0,0.05) 3%,rgba(0,0,0,0.05) 100%)!important; /* Opera 11.10+ */
background: -ms-linear-gradient(top,  rgba(0,0,0,0.65) 0%,rgba(0,0,0,0.65) 1%,rgba(0,0,0,0.05) 3%,rgba(0,0,0,0.05) 100%)!important; /* IE10+ */
background: linear-gradient(to bottom,  rgba(0,0,0,0.65) 0%,rgba(0,0,0,0.65) 1%,rgba(0,0,0,0.05) 3%,rgba(0,0,0,0.05) 100%)!important; /* W3C */
}
/*góra*/
#top_bar {
background-color: #A71313 !important;  
background-image: none !important; 

}
#top_bar_left {
background: #a70404 !important;
}
ul.light {
background-color: #A71313 !important;  
background-image: none !important; 
}
#top_bar a { 
color: #f6f6f6 !important;
}
#top_bar_more {
background: #A71313 !important;
}
#top_inner {
background: #3e3e3e !important;
border-bottom-color: #d21010 !important;
height : 20px !important;
min-height: 40px !important;
}
#top div.template_wrapper {
max-width: 100% !important;
background: #3e3e3e !important;
}
#top_bookmarks a {
color: #b2b2b2 !important;
}
#top_section a {
color: #b2b2b2 !important;
}
#top_bookmarks li a {
background-color: #535353 !important;
}
#top_bookmarks li a:hover{
background: #630909 !important;
}
#top_bookmarks li.selected a { 
background-color: #343434 !important;
font-weight : 900 !important;
padding-bottom : 2px !important;
}
.user_link {
font-weight : bold !important;
}
#top_inner_right a {
color: #b2b2b2 !important;
}
.background_gray {
background: #3e3e3e !important;
}
/*logo*/
#logo img {
min-width: 140px !important;
border: none !important;
margin-top : -32px !important;
}
/*góra, reputacja*/
.reputation.size_11.color_green {
color: #72f45c !important;
}
/*środek*/
a.visited { 
color: #f68500 !important;
}
h2.content_header {
font-size: 17px !important;
margin: 0px 5px 0px 0px !important;
}
.content_header a {
color: #3c3c3c !important;
}
.content_header a:hover {
color: #d21010 !important;
}
.color_gray_light { 
color: #7A7A7A !important;
}
div.markdown q {
color: #424242 !important;
}
/*miniatury znalezisk po prawej*/
a.content_image {
float : right !important;
}
img {
border: 1px solid #bababa !important;
padding: 2px !important;
}
/*zdjęcie znaleziska*/
#content_page div.content_wrapper div.content a.content_image img {
width: 120px !important;
}
/*wielkość wideo i zdjęć w znaleziskach */
.embed.video { 
max-width: 890px !important;
}
.embed.image { 
max-width: 890px !important;
}
/*głosy*/
div.content_voting {
width: 40px !important;
}
.content_vote_count {
font-size : 14px !important;
}
.content_vote.like.selected.color_green {
background: #CBF783 !important; 
}
.content_vote.like.selected.color_orange {
background: #CBF783 !important; 
}
.color_orange {
color : #2E9B2D !important; 
}
.content_vote.dislike.selected.color_red {
background: #FEAEB3 !important;
}
a.content_comment_vote.like.color_green.selected {
background: #CBF783 !important; 
}
a.content_comment_vote.dislike.selected.color_red {
background: #FEAEB3 !important;
}
a.entry_vote.like.color_green.selected {
background: #CBF783 !important; 
}
a.entry_vote.dislike.color_red.selected {
background: #FEAEB3 !important;
}
a.content_link_vote.like.selected  {
background: #CBF783 !important; 
}
.button.selected.green {
background: #CBF783 !important; 
}
/*prawa kolumna kolor linków*/
h3 {
color: #a70404 !important;
}
.column.right a {
color: #3c3c3c !important;
}
.column.right a:hover {
color: #a70404 !important;
}
/*kolory użytkowników*/
.user_name.level_5.admin   a {
color: #000000 !important;
}
.user_name.level_5.advanced.admin   a {
color: #000000 !important;
}
.user_name.level_6.advanced.admin   a {
color: #000000 !important;
}
.user_name.level_6.admin   a {
color: #000000 !important;
}
.user_name.level_10.advanced    a{
color: #ba1010 !important;
}
.user_name.level_9.advanced    a{
color: #ba1010 !important;
}
.user_name.level_8.advanced    a{
color: #ba1010 !important;
}
.user_name.level_7.advanced    a{
color: #ba1010 !important;
}
.user_name.level_6.advanced     a{
color: #ba1010 !important;
}
.user_name.level_6     a{
color: #ba1010 !important;
}
.user_name.level_5.advanced    a{
color: #e73100 !important;
}  
.user_name.level_5   a{
color: #e73100 !important;
} 
.user_name.level_4.advanced    a{
color: #e76700 !important;
}
.user_name.level_4  a{
color: #e76700 !important;
}   
.user_name.level_3   a {
color: #e76700 !important;
}
.user_name.level_2   a {
color: #e1af00 !important;
}
.user_name.level_1     a{
color: #049d00 !important;
}
.user_name.level_0 a {
color: #39d235 !important;
}
.user_name.level_0.new  a {
color: #39d235 !important;
}
/*Stopka*/
#footer {
background-color: #3e3e3e !important; 
background-image: none !important; 
border-top-color: #d21010 !important;
}
#footer a {
color : #ffffff !important; 
}
#footer a:hover {
color : #d21010 !important; 
}
}
