css:
@namespace url(http://www.w3.org/1999/xhtml);

/* Themed by F-Man */

@-moz-document url(http://www.soniconthe.net/),
               url-prefix(http://www.soniconthe.net/),
               domain(soniconthe.net)
{

/*
  The original SonicOnThe.Net theme.
*/


 /* General page style. */
body {
	background-color: #004E98 !important;
}

/* General font families for common tags */
font,th,td,p { font-family: Verdana, Arial, Helvetica, sans-serif }
a:link,a:active,a:visited { color : #000000 !important; }
a:hover		{ color : #CC0000 !important; text-decoration: none !important; }
span a { text-decoration: none !important; }
span.postbody a { font-weight: bold !important; }
hr	{ height: 0px !important; border: solid rgb(0, 85, 164) 0px !important; border-top-width: 1px !important;}


/* This is the border line & background colour round the entire page */
.bodyline	{ background-color: #F5F4EA !important; border: 1px #98AAB1 solid !important; }

/* This is the outline round the main forum tables */
.forumline	{ background-color: rgb(0, 85, 164) !important; border-width: 0 !important; }


/* Main table cell colours and backgrounds */
td.row1	{ background-color: #dCd9c8 !important; }
td.row2	{ background-color: #dCd9c8 !important; }
td.row3	{ background-color: #dCd9c8 !important; }


/*
  This is for the table cell above the Topics, Post & Last posts on the index.php page
  By default this is the fading out gradiated silver background.
  However, you could replace this with a bitmap specific for each forum
*/
td.rowpic {
		background-color: rgb(236, 233, 216) !important;
		background-image: none !important;
}

/* Header cells - the blue and silver gradient backgrounds */
th	{
	color: #FFFFFF !important; font-size: 11px !important; font-weight: bold !important;
	background-color: rgb(49, 106, 197) !important; height: 25px !important;
	background-image: none !important;
}

td.cat,td.catHead,td.catSides,td.catLeft,td.catRight,td.catBottom {
			background-color: rgb(236, 233, 216) !important; border: #FFFFFF !important; border-style: solid !important; height: 28px !important;
			background-image: none !important;
}


/*
  Setting additional nice inner borders for the main table cells.
  The names indicate which sides the border will be on.
  Don't worry if you don't understand this, just ignore it :-)
*/
td.cat,td.catHead,td.catBottom {
	height: 29px !important;
	border-width: 0px 0px 0px 0px !important;
}
th.thHead,th.thSides,th.thTop,th.thLeft,th.thRight,th.thBottom,th.thCornerL,th.thCornerR {
	font-weight: bold !important; border: 0 none !important; height: 28px !important; }
td.row3Right {
	background-color: #dCd9c8 !important; }
td.spaceRow {
	background-color: rgb(236, 233, 216) !important; }

th.thHead,td.catHead { font-size: 12px !important; border-width: 1px 1px 0px 1px !important; }
th.thSides,td.catSides,td.spaceRow	 { border-width: 0px 1px 0px 1px !important; }
th.thRight,td.catRight,td.row3Right	 { border-width: 0px 1px 0px 0px !important; }
th.thLeft,td.catLeft	  { border-width: 0px 0px 0px 1px !important; }
th.thBottom,td.catBottom  { border-width: 0px 1px 1px 1px !important; }
th.thTop	 { border-width: 1px 0px 0px 0px !important; }
th.thCornerL { border-width: 1px 0px 0px 1px !important; }
th.thCornerR { border-width: 1px 1px 0px 0px !important; }


/* The largest text used in the index page title and toptic title etc. */
.maintitle,h1,h2	{
			font-weight: bold !important; font-size: 22px !important; font-family: "Trebuchet MS",Verdana, Arial, Helvetica, sans-serif !important;
			text-decoration: none !important; line-height : 120% !important; color : #000000 !important;
}


/* General text */
.gen { font-size : 12px !important; }
.genmed { font-size : 11px !important; }
.gensmall { font-size : 10px !important; }
.gen,.genmed,.gensmall { color : #000000 !important; }
a.gen,a.genmed,a.gensmall { color: #000000 !important; text-decoration: none !important; }
a.gen:hover,a.genmed:hover,a.gensmall:hover	{ color: #CC0000 !important; text-decoration: underline !important; }


/* The register, login, search etc links at the top of the page */
.mainmenu		{ font-size : 11px !important; color : #000000 }
a.mainmenu		{ text-decoration: none !important; color : #000000 !important;  }
a.mainmenu:hover{ color : #CC0000 !important; text-decoration: none !important; }


/* Forum category titles */
.cattitle		{ font-weight: bold !important; font-size: 12px  !important; letter-spacing: 1px !important; color : #006699}
a.cattitle		{ text-decoration: none !important; color : #000000 !important; }
a.cattitle:hover{ color: #CC0000 !important; text-decoration: none !important; }


/* Forum title: Text and link to the forums used in: index.php */
.forumlink		{ font-weight: bold !important; font-size: 12px !important; color : #006699 !important; }
a.forumlink 	{ text-decoration: none !important; color : #000000 !important; }
a.forumlink:hover{ color : #CC0000 !important; text-decoration: none !important; }


/* Used for the navigation text, (Page 1,2,3 etc) and the navigation bar when in a forum */
.nav			{ font-weight: bold !important; font-size: 11px !important; color : #000000 !important;}
a.nav			{ text-decoration: none !important; color : #000000 !important; }
a.nav:hover		{ text-decoration: underline !important; }


/* titles for the topics: could specify viewed link colour too */
.topictitle			{ font-weight: bold !important; font-size: 11px !important; color : #000000 !important; }
a.topictitle:link   { text-decoration: none !important; color : #000000 !important; }
a.topictitle:visited { text-decoration: none !important; color : #222222 !important; }
a.topictitle:hover	{ text-decoration: none !important; color : #CC0000 !important; }


/* Name of poster in viewmsg.php and viewtopic.php and other places */
.name			{ font-size : 11px !important; color : #000000 !important;}

/* Location, number of posts, post date etc */
.postdetails		{ font-size : 10px !important; color : #000000 !important; }


/* The content of the posts (body of text) */
.postbody { font-size : 12px !important;}
a.postlink:link	{ text-decoration: none !important; color : #006699 }
a.postlink:visited { text-decoration: none !important; color : #5493B4 !important; }
a.postlink:hover { text-decoration: none !important; color : #DD6900}


/* Quote & Code blocks */
.code {
	font-family: Courier, 'Courier New', sans-serif !important; font-size: 11px !important; color: #006600 !important;
	background-color: #FAFAFA !important; border: #D1D7DC !important; border-style: solid !important;
	border-left-width: 1px !important; border-top-width: 1px !important; border-right-width: 1px !important; border-bottom-width: 1px
}

.quote {
	font-family: Verdana, Arial, Helvetica, sans-serif !important; font-size: 11px !important; color: #444444 !important; line-height: 125% !important;
	background-color: #FAFAFA !important; border: #D1D7DC !important; border-style: solid !important;
	border-left-width: 1px !important; border-top-width: 1px !important; border-right-width: 1px !important; border-bottom-width: 1px
}


/* Copyright and bottom info */
.copyright		{ font-size: 10px !important; font-family: Verdana, Arial, Helvetica, sans-serif !important; color: #444444 !important; letter-spacing: -1px !important;}
a.copyright		{ color: #444444 !important; text-decoration: none !important;}
a.copyright:hover { color: #000000 !important; text-decoration: none !important;}


/* Form elements */
input,textarea, select {
	color : #000000 !important;
	font: normal 11px Verdana, Arial, Helvetica, sans-serif !important;
	border-color : #000000 !important;
}

/* The text input fields background colour */
input.post, textarea.post, select {
	background-color : #FFFFFF !important;
}

input { text-indent : 2px !important; }

/* The buttons used for bbCode styling in message post */
input.button {
	background-color : #EFEFEF !important;
	color : #000000 !important;
	font-size: 11px !important; font-family: Verdana, Arial, Helvetica, sans-serif !important;
}

/* The main submit button option */
input.mainoption {
	background-color : #FAFAFA !important;
	font-weight : bold !important;
}

/* None-bold submit button */
input.liteoption {
	background-color : #FAFAFA !important;
	font-weight : normal !important;
}

/* This is the line in the posting page which shows the rollover
  help line. This is actually a text box, but if set to be the same
  colour as the background no one will know ;)
*/
.helpline { background-color: #dCd9c8 !important; border-style: none !important; }

.seed, .leech						{ font-size : 12px !important; }
.seedmed, .leechmed			{ font-size : 11px !important; }
.seedsmall, .leechsmall	{ font-size : 10px !important; }

.seed, .seedmed, .seedsmall			{ color: #006600 !important; }
.leech, .leechmed, .leechsmall	{ color: #800000 !important; }

.dlWill			{ color: #003366 !important; }
.dlDown			{ color: #800000 !important; }
.dlComplete	{ color: #006600 !important; }
.dlCancel		{ color: #666666 !important; }

.dlBgr1		{ background-color: #ADBAC6 !important; }
.dlBgr2		{ background-color: #D6DBDE !important; }
.dlBgr3		{ background-color: #D6DBDE !important; }

.tDL		{ color: #000000 !important; font-weight: bold !important; }
.tDLDown	{ color: #800000 !important; font-weight: bold !important; }
.tDLCmpl	{ color: #006600 !important; font-weight: bold !important; }

.prow1	{ background-color: #ECECEC !important; }
.prow2	{ background-color: #E7E7E7 !important; }
.prow3	{ background-color: #DEE3E7 !important; }

a.seed:link, a.seed:visited, a.seed:hover, a.seed:active, a.seedmed:link, a.seedmed:visited, a.seedmed:hover, a.seedmed:active, a.seedsmall:link, a.seedsmall:visited, a.seedsmall:hover, a.seedsmall:active {
	color: #006600 !important; text-decoration: none !important; }

a.leech:link, a.leech:visited, a.leech:hover, a.leech:active, a.leechmed:link, a.leechmed:visited, a.leechmed:hover, a.leechmed:active, a.leechsmall:link, a.leechsmall:visited, a.leechsmall:hover, a.leechsmall:active {
	color: #800000 !important; text-decoration: none !important; }

a.dlWill:link, a.dlWill:visited, a.dlWill:hover, a.dlWill:active {
	color: #003366 !important; text-decoration: none !important; }

a.dlDown:link, a.dlDown:visited, a.dlDown:hover, a.dlDown:active {
	color: #800000 !important; text-decoration: none !important; }

a.dlComplete:link, a.dlComplete:visited, a.dlComplete:hover, a.dlComplete:active {
	color: #006600 !important; text-decoration: none !important; }

a.dlCancel:link, a.dlCancel:visited, a.dlCancel:hover, a.dlCancel:active {
	color: #666666 !important; text-decoration: none !important; }

a.seed:hover, a.seedmed:hover, a.seedsmall:hover, a.leech:hover, a.leechmed:hover, a.leechsmall:hover, a.dlWill:hover, a.dlDown:hover, a.dlComplete:hover, a.dlCancel:hover  {
	text-decoration: underline !important; }

input.pOrdSel {
	background-color: #EFEFEF !important;
	border: 0px solid #EFEFEF !important;
	color: #333333 !important;
	font-size: 12px !important;
	cursor: pointer !important;
	font-family:Arial, Helvetica, sans-serif !important;
	overflow:hidden !important;
	width: 18px !important;
}

optgroup, option, fieldset, legend {
	font-size: 11px !important; font-family: Verdana,Arial,Helvetica,sans-serif !important; }

optgroup { font-style: normal !important; font-weight: bold !important; background-color: #ECECEC !important; }
option { background-color: #FFFFFF !important; font-size: 11px !important; }

legend { color: #000066 !important; }

.bold		{ font-weight: bold !important; }
.normal	{ font-weight: normal !important; }
.opened { color: #778899 !important; }

.btTbl	{ background-color: #98AAB1 !important; border: 0px #98AAB1 solid !important; }
.btHead { background-color: #D1D7DC !important; font-size: 11px !important; font-weight: bold !important; text-align: center !important; padding: 1px 0px 1px 0px !important; }

}
