css:
@-moz-document domain('tweakers.net') {

/* -------------------------------------- */
/* ------                          ------ */
/* ------      Website layout      ------ */
/* ------                          ------ */
/* -------------------------------------- */

.date a  {color: #444444 !important;} 

td, th {padding: 4px 4px !important;}

td.topic small a:visited {color: rgb(97, 100, 100) !important;}

td.topic small a {color: black !important;}

td.topic a {color: black !important;}

body {background-color:#666;}

h1, h2, h3, p , #reacties {
	text-align:justify !important;
	text-justify:inter-word !important;
}

div.hr {background:#313232 !important;}

#top {border-bottom: 1px solid #313232 !important;}

#contentArea{
	background-color:#FFFFFF;
	padding-left:5px;
	padding-right: 5px;
	padding-top: 0;
	top: 6px;
}

p.pagecounter {text-align: right !important;}

/* -------------------------------------- */
/* ------                          ------ */
/* ------      Menubar layout      ------ */
/* ------                          ------ */
/* -------------------------------------- */
	
#menubar {width: 100% !important;}

#menu #userbar li:first-child{
	display: block !important;
}

#menu ul:first-child li.last.more div ul li:first-child, #menu ul:first-child li.last.more div ul li+li+li+li {display: none !important;}


#menu ul:first-child li.last.more div ul li+li+li+li+li {display: block !important;}

/* -------------------------------------- */
/* ------                          ------ */
/* ------       Forum layout       ------ */
/* ------                          ------ */
/* -------------------------------------- */
	
div.topicstarter div.messageheader {background: #FCC !important;}

div.message div.ownpost {background-color: #CFD !important;}

div.message div.messageheader {
	background-image: -moz-linear-gradient(center top, rgb(234,235,235), rgb(217, 218, 218));
	background-image: -webkit-gradient(linear, center top, rgb(234, 235, 235), rgb(217, 218, 218));
	background-image: -o-linear-gradient(center top, rgb(234, 235, 235), rgb(217, 218, 218));
	background-image: -ms-linear-gradient(center top, rgb(234, 235, 235), rgb(217, 218, 218));
	background-image: linear-gradient(center top, rgb(234, 235, 235)m rgb(217, 218, 218));
	border-bottom: 1px solid rgb(207, 208, 208);
}

.spoiler, div.messagecontent div.spoiler a, div.messagecontent div.message-quote-div div.spoiler a, div.messagecontent div.spoiler a:hover, div.messagecontent div.message-quote-div div.spoiler a:hover {background-color: #999;}

div.message  {
	margin-bottom: 3px !important;
	border: 1px solid #AAA !important;
}

div.spoiler:hover {color: white !important;}

div.messagecontent blockquote div.message-quote-div {
	background-color: #FFF !important;
	border: 1px solid #666 !important;
	color: #333 !important;
}

#messages, .topic_actions {width: 980px !important;}

/* -------------------------------------- */
/* ------                          ------ */
/* ------      Tracker layout      ------ */
/* ------                          ------ */
/* -------------------------------------- */

#menu #userbar li.icon.tracker{display: none !important;}

.trackerPopup.fixed {
    width: 300px;
    position: absolute;
    top: 0;
    left: -336px !important;
    display: block;
}

.popup.trackerPopup.fixed span.fancyButton.close{
	display: none !important;
}

a.fancyButton {
	display: none !important;
}

.trackerPopup .trackeritem li {padding: 1px 0 1px 0;}

/* -------------------------------------- */
/* ------                          ------ */
/* ------    Nieuwspost layout     ------ */
/* ------                          ------ */
/* -------------------------------------- */

.reactionRatingFilterPopup {
    margin: 4px !important; padding: 0px;
    background: #444;
    padding-left: 10px;
    padding-top: 7px;
    border: 1px solid #777;
}

.modFilter span.reactionCount.score-1 {
    margin: 4px !important; padding: 3px;
}

.modFilter span.reactionCount.score0 {
    margin: 4px !important; padding: 3px;
}

.modFilter span.reactionCount.score1 {
    margin: 4px !important; padding: 3px;
}

.modFilter span.reactionCount.score2 {
    margin: 4px !important; padding: 3px;
}

.modFilter span.reactionCount.score3 {
    margin: 4px !important; padding: 3px;
}

.reactieHeader .usericonsmall { display: none; }
.reactieHeader br { display: none; }
.reactieHeader a { font-size: 12px !important; margin-right: 10px; }

.minmaxreacties {
    float: left; display: inline-block;
    position: relative; top: 2px;
    width: 12px; height: 12px;
    background: #fff; color: #888 !important;
    line-height: 11px; font-weight: normal !important;
    text-align: center;
    margin: 0 8px 0 0 !important; padding: 0;
    border: 1px solid #ccc !important;
}

.minmaxreacties:hover { background: #efefef; text-decoration: none !important; color: #000 !important; }

.reactie .topBorder { 
    margin-left: -20px !important; 
    background: #666!important; 
} 

.reactie.last .topBorder { 
    margin-left: -20px !important; 
    background: #666!important; 
} 

.reactie { 
    background: #212323 !important; 
    color: #EDEEEE !important; 
    border-left: 1px solid #666!important; 
}

.reactie.last > .reactieContainer { 
    border-left: 0px solid #666 !important;
    margin-left: -39px !important; 
}

.reactieBody.informative { 
    background: #eee !important; 
    border: 1px solid #666!important; 
}

.reactieBody.spotlight { 
    background: #ccc !important; 
    border: 1px solid #666!important; 
    margin-left: -20px !important; 
}

/* -------------------------------------- */
/* ------   Random uitlijningen,   ------ */
/* ------  aanvullingen en andere  ------ */
/* ------          opties          ------ */
/* -------------------------------------- */
	
#listing, #filterContainer, .articleColumn, .relevancyColumn, .mainColumn, .secondColumn  {padding-top: 4px !important;}

#filterContainer  {
	float: right;
	margin: 0 0 0 30px;
}

#mynotepad textarea {
	height: 99px !important;
	width: 450px !important;
}


/* -------------------------------------- */
/* ------                          ------ */
/* ------        PM layout         ------ */
/* ------                          ------ */
/* -------------------------------------- */

.messagecontent {width: 640px !important;}

div.post {padding: 6px 0 6px 10px !important;}

/* -------------------------------------- */
/* ------                          ------ */
/* ------       Search layout      ------ */
/* ------                          ------ */
/* -------------------------------------- */

#search {
	position:absolute;
	top:-10px;
	right:220px;
} 

#searchbar {
	height: 10px !important;
	background:#313232 !important;
}

/* -------------------------------------- */
/* ------                          ------ */
/* ------     Disabled layouts     ------ */
/* ------                          ------ */
/* -------------------------------------- */

#fp_tabs_container, fieldset.modbreak, #pageTabs, #fpaItems, #menu li:first-child, #true, #sidebar, #newsletter, #jobs, #social, #highlights, #ticker, #b_tb, #b_ac, #b_ad, #about, #trackerFixateLink, div.karma, a#streamone, #bottom {display: none !important;}

}
