css:
/*
Theme Name:		Musée des Beaus-Arts de Caen CSS-Hacking
Author: 		Xavier Aurey
Author URI:		http://www.fondamentaux.org
*/

/* =Reset
-------------------------------------------------------------- */

html, body, div, span, applet, object, iframe,h1, h2, h3, h4, h5, h6, p, blockquote, pre,
a, abbr, acronym, address, big, cite, code,del, dfn, em, font, ins, kbd, q, s, samp,
small, strike, strong, sub, sup, tt, var,dl, dt, dd, ol, ul, li,
fieldset, form, label, legend,table, caption, tbody, tfoot, thead, tr, th, td {
	border: 0;font-family: inherit;font-size: 100%;font-style: inherit;font-weight: inherit;
	margin: 0;outline: 0;padding: 0;vertical-align: baseline;}
html {font-size: 62.5%;overflow-y: scroll;-webkit-text-size-adjust: 100%;-ms-text-size-adjust: 100%;}
body {background: #fff;}
article,aside,details,figcaption,figure,footer,header,hgroup,nav,section {display: block;}
ol, ul {list-style: none;}
table {border-collapse: separate;border-spacing: 0;}
caption, th, td {font-weight: normal;text-align: left;}
blockquote:before, blockquote:after, q:before, q:after {content: "";}
blockquote, q {quotes: "" "";}
a:focus {outline: thin dotted #333;outline: 5px auto -webkit-focus-ring-color;outline-offset: -2px;}
a:hover,a:active {outline: 0;}
a img {border: 0;}
img {vertical-align: middle;border: 0;-ms-interpolation-mode: bicubic;}
button,input,select,textarea {margin: 0;font-size: 100%;vertical-align: middle;}
button,input {*overflow: visible;line-height: normal;}
button::-moz-focus-inner,input::-moz-focus-inner {padding: 0;border: 0;}
button,input[type="button"],input[type="reset"],input[type="submit"] {cursor: pointer;-webkit-appearance: button;}
input[type="search"] {-webkit-box-sizing: content-box;-moz-box-sizing: content-box;box-sizing: content-box;-webkit-appearance: textfield;}
input[type="search"]::-webkit-search-decoration,input[type="search"]::-webkit-search-cancel-button {-webkit-appearance: none;}
textarea {overflow: auto;vertical-align: top;}

/* Normal */

body {
	margin: 0;font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;font-size: 13px;line-height: 18px;
	background-color:#32465f;color: #333333;padding-top: 20px;padding-bottom: 40px;}
a {color: #0088cc;text-decoration: none;}
a:hover {color: #005580;text-decoration: underline;}

b, strong {font-weight: bold}

div#container, div#milieu, div#header {background-color:transparent !important;}

div#container {border: none !important;width: 960px !important;}
#container {
	position:relative;background-color:none;z-index:10;
	margin-right: auto;margin-left: auto;*zoom: 1;}
#container:before,#container:after {display: table;content: "";}
#container:after {clear: both;}

div#header {height: 450px !important; background-image:none !important;text-align:right !important;}
#header {position:relative;background-color:#333333;width:240px;z-index:1}
#header a[href="http://www.mba.caen.fr"]:before {content: "Musée des Beaux Arts de Caen"}
#header a[href="http://www.mba.caen.fr"] {color:#FFF;font-size:80px;line-height:74px;}
#header img {display:none}

#MenuBar1 {float:right;}

ul.MenuBarHorizontal {padding: 0;margin: 30px 0;width:240px;font-size:18px;}
ul.MenuBarActive {z-index: 1002;}
ul.MenuBarHorizontal li {margin: 0;padding: 0;position: relative;float: right;text-align: right;}
ul.MenuBarHorizontal ul {display:none !important;}

ul.MenuBarHorizontal a {
	display: block;	cursor: pointer;
	background-color: transparent; padding: 0.3em 0;
	color: #fff;border: none;
}

ul.MenuBarHorizontal a.MenuBarItemHover {
	background-color: #beb4aa; color: #000;}

ul.MenuBarHorizontal a.MenuBarItemSubmenu {
	background-image: none;}


div#milieu {position:absolute;top:0;left:250px;width:400px !important;}
#milieu table {}
#milieu tr {}
#milieu td[width="188"] {vertical-align: top;padding:0;margin:0}
#milieu table table {}
#milieu table table tr {}
#milieu table table td {}

#milieu td{
	background-color: transparent !important;
	width:auto !important;
}


#milieu td[bgcolor="#76bcd1"]{
	background-color: transparent !important;
	margin-top: 2px;
}

#milieu td[bgcolor="#dbb7b7"] table[width="100%"] td[bgcolor="#dbb7b7"] {
	position:absolute;top:435px;left:0px;width:188px !important;
	background-color: #32465f !important;color:#FFF;
	padding-top:90px;
}
#milieu td[bgcolor="#dbb7b7"] table[width="100%"] td[bgcolor="#dbb7b7"] p {
	margin-left:10px;
}
#milieu td[bgcolor="#dbb7b7"] table[width="100%"] td[bgcolor="#dbb7b7"] p strong {
	text-transform: uppercase;
}

#milieu td[bgcolor="#dbb7b7"] table[width="100%"] td[bgcolor="#dbb7b7"] img {
	position: absolute; top: 0px ;left:-5px;width:188px !important;height:100px;
}

#milieu td[width="50%"][align="center"]{
	position:relative; top:0px;left:0px;
	width: 300px !important;
	float:left;
	background-color: #FFF !important;
	margin: 15px 15px 0 13px;
}
#milieu td[width="50%"][align="center"]:first-of-type{
	margin-top: 35px;
}
#milieu td[width="50%"][align="center"]:first-of-type p{
	font-size: 18px;
}
#milieu td[width="50%"][align="center"]:first-of-type strong{
	font-size: 36px;line-height:30px
}
#milieu td[width="50%"][align="center"]:last-of-type strong:first-of-type{
	font-size: 36px;line-height:30px;padding-left:45px;
}
#milieu td[width="50%"][align="center"]:last-of-type p{
	padding-left:10px;margin-bottom:10px
}
#milieu td[width="50%"][align="center"]:last-of-type img{
	margin-left:0px
}
#milieu td[width="50%"][align="center"] img{
	float:left;
	-moz-box-shadow:3px 3px 10px 3px #777777;
	-webkit-box-shadow:3px 3px 10px 3px #777777;
	box-shadow:3px 3px 10px 3px #777777;
	margin:10px 15px 10px 10px;
}
#milieu td[width="50%"][align="center"] a.liennoir, #milieu td[width="50%"][align="center"] a.liennoir:hover{
	color:#32465f;
}

#milieu table[width="100%"] tr:last-of-type td[valign="top"][align="center"]:last-of-type img{
	float:left;
	-moz-box-shadow:3px 3px 10px 3px #777777;
	-webkit-box-shadow:3px 3px 10px 3px #777777;
	box-shadow:3px 3px 10px 3px #777777;
	margin:0px 15px 15px 0px;
}

#milieu table[width="100%"] tr:last-of-type td[valign="top"][align="center"]:last-of-type{
	background-color: #FFF !important;
	float: left; position: absolute; left:520px; top :35px
}

#milieu table[width="100%"] tr:last-of-type td[valign="top"][align="center"]:last-of-type p{
	font-size: 18px;margin: 15px;
}
#milieu table[width="100%"] tr:last-of-type > td:first-child{
	color: #FFF;
}
#milieu td [align="right"] [bgcolor="#dbdb70"]{
	color: #FFF;
	position: relative; top:-30px;left:40px;
}

#milieu img[src="imgtest/gris.gif"]{display:none}
#milieu a[href="savourer.html"]{display:none}
a.liennoir {color:#FFF;}
a.liennoir:hover {text-decoration: underline;color:#FFF;}

img#imgvisus {margin: 2px 0 !important;padding: 0 !important;}

ul#MenuBar3 {float: left;position: absolute; top:0px; left:200px; width: 600px;border:none !important;}
ul#MenuBar3 li {float: left;width: 120px !important;border:none !important;}
ul#MenuBar3 li a {color:#32465f;}
ul#MenuBar3 ul {display:none;}

/* */
@media (min-width: 768px) and (max-width: 979px) {
	ul#MenuBar3 li {clear:both;width:250px}
	
	#milieu td[width="50%"][align="center"]:first-of-type{
		margin-top: 155px;
	}

	#milieu table[width="100%"] tr:last-of-type td[valign="top"][align="center"]:last-of-type{
		background-color: #FFF !important;
		float: left; position: absolute; left:200px; top :800px
	}
	#milieu td [align="right"] [bgcolor="#dbdb70"]{
		color: #FFF;
		position: absolute; top:900px;left:200px;
	}
}
