/*

 Musée des beaux arts Nantes

 http://www.museedesbeauxarts.nantes.fr/

 Responsive Museum Weeck

 Par fabien townsend 
 http://fabientownsend.com
 
*/


@-moz-document url-prefix("http://www.museedesbeauxarts.nantes.fr/") {
* { 
	-webkit-box-sizing: border-box;
	-moz-box-sizing: border-box;
	box-sizing: border-box;	
}

#page { position: relative; padding: 0; line-height: 1.5em;}
#page.pages { padding: 0; }
.nav_sec { margin-bottom: 1.5em; }
.nav_secc { position: relative; top: 0; }
.nav li { width: 50%; }

h1{ clear: both !important; float: none !important;}

.logo, .nav { padding: 5px 2%; margin: 0; }

.pages .visuel img { max-width: 100%; margin: 0 auto; }

.carrousel ul li img { max-width: 100%; }
#center { min-height: 0; }

.left_bis { border-top: 1px solid #696969; padding-top: 15px; margin-top: 15px; }
.pages .expo .left_bis h2 { margin: 0.5em 0 }
		
.right img { margin: 15px; }



	/*-----------------------------------------------------------------------------
		MOBILE 0 -> 500px * 3 Columns   
	----------------------------------------------------------------------------- */
    @media screen and (min-width: 0px) and (max-width: 500px) {

		#page, #page.pages { width: 100%;}
		
		.acces_rapide ul li { float: left; width: 33%; }

		.acces_rapide ul li img { display: block; margin: 0 auto; }
		.acces_rapide { width: 100%; margin: 0; padding: 0; background-color: rgba(245,245,245,50); position: absolute;}
		.acces_rapide fieldset { text-align: center; padding: 5px 0;}
		.acces_rapide fieldset input { border: 1px solid black; }
		.acces_rapide fieldset input:first-child { border: none; }


		img.logo { margin-top: 50px; }
		.accueil .logo{ margin-top: 5px; }
		
		img.logo_bis{ visibility: hidden; }
		.nav ul { width:100%; padding: 0;}

		.pages #center h1 { clear: both; }
		.pages #center { padding: 0 2%; }

		.pages div.left  { clear: both; width: 100%;}
		.pages div.right  {clear: both; width: 100%;}

		.nav_sec { height: auto; }
		.nav_ssec { top: 3em; width: 320px; position: static; }
		.nav_ssec li { float: left; } 



	}

	/* -----------------------------------------------------------------------------
	Tablette 501 -> 959 px  * 6 Columns
    -----------------------------------------------------------------------------     */
    @media screen and (min-width: 501px) and (max-width: 959px) {
		#page, #page.pages { width: 100%; }
    	
		.acces_rapide ul, .acces_rapide fieldset{ width: 20%; display: inline; }
		.acces_rapide ul li { float: left; width: 25%; }

    	.acces_rapide ul li img { display: block; margin: 0 auto; }
		.acces_rapide { width: 100%; margin: 0; padding: 0; background-color: rgba(245,245,245,50); position: absolute;}
		.acces_rapide fieldset { text-align: center; padding: 5px 0;}
		.acces_rapide fieldset input { border: 1px solid black; }
		.acces_rapide fieldset input:first-child { border: none; }

		img.logo_bis{ visibility: hidden; }

		img.logo { margin: 50px 0 15px 0; }
		.accueil .logo{ margin-top: 55px; }

		.nav { margin-top: 46px; }
		.accueil .nav { margin-top: 0; }		
		#center h1 { margin-left: 2%; }
		.nav_sec { height: auto; margin-left: 2%; }
		.nav_ssec li { float: left; } 
		.nav_ssec { top: 3em; width: auto; position: static; }

		.pages div.left  { width: 50%; padding: 0 2%; }
		.pages div.right  { width: 50%; padding: 0 2%;}
		
    }

    /* -----------------------------------------------------------------------------
	Desktop 960 
	----------------------------------------------------------------------------- */
    @media screen and (min-width: 960px) {

    	.accueil .nav, .nav { margin-top: 20px; }
    	.logo { margin-top: 10px; }
		#page { width: 960px; }
		#center { margin-left: 2%; }
		.pages #center h1 { clear: both; }
		.nav_sec { height: auto; }
		.nav_ssec li { float: left; } 
		.nav_ssec { top: 3em; width: auto; position: static; }
	}
}
