css:
/*
 * KompjoeFriek's custom CSS for Tweakers 7
 *              Subtle Grey
 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain('tweakers.net') 
{

body {
    color: #000000;
    text-shadow: 1px 1px #EFEFEF;
    background-color: #DFDFDF;
}

/* Frontpage */
#contentArea {
    background-color: #DFDFDF !important;
}

#menubar {
    width: 100% !important;
}

#searchbar {
    background: linear-gradient(#EFEFEF, #E8E8E8) repeat scroll 0 0 #E8E8E8 !important;
    border-radius: 0 !important;
}    

#cookieBar {
    text-shadow: none !important;
}

body.hasTracker {
    margin-left: 0px !important;
    margin-right: 0px !important;
}

#tracker .fakeTop {
    height: 49px !important; /* 68 - 19 to overlap other dark bar */
}

#trackerFixedLink {
    display: none !important;
}

#trackerPositionLink {
    display: none !important;
}

#tracker {
    text-shadow: none !important;
    position: absolute !important;
    float: left !important;
}

#tracker .fakeTop > div {
    background: url("http://i49.tinypic.com/23rk96a.png") no-repeat scroll 0 0 transparent !important;
}

.fpaItem {
    text-shadow: none !important;
}

.dropdown ul li:hover, #menu .dropdown ul li:hover {
    text-shadow: none !important;
}

#pricewatch, #jobs, #poll, #newsletter, #sponsored, #textlink, #social .twitterButton, #social .facebookButton {
    background: linear-gradient(#EFEFEF, #E8E8E8) repeat scroll 0 0 #E8E8E8 !important;
}

.nextPrevLinks .arrowUp, .nextPrevLinks .arrowDown {
    color: #626565 !important; /* reset sites default color */
}

.reactie {
    background: none repeat scroll 0 0 #DFDFDF !important;
    border-left: 1px solid #BBBBBB !important;
}

.reacties > .reactie > .reactieContainer > .eenReactie .topBorder, .reacties > .reactie > .reactieContainer > div > .eenReactie .topBorder {
    background-position: 0 -27px !important;
}

.reacties > .reactie > .reactieContainer > .eenReactie .topBorder.informative, .reacties > .reactie > .reactieContainer > div > .eenReactie .topBorder.informative { /* +2 green */
    background-position: 0 -36px !important;
}

.reacties > .reactie > .reactieContainer > .eenReactie .topBorder.spotlight, .reacties > .reactie > .reactieContainer > div > .eenReactie .topBorder.spotlight {/* +3 green */
    background-position: 0 -45px !important;
}

.reactie .topBorder {
    background: url("http://i47.tinypic.com/2u6hfr6.png") no-repeat scroll 0 0 #E8E8E8 !important;
}

.reactie .topBorder.informative {
    /* no background-color. use sites default */
    background-position: 0 -9px !important;
}

.reactie .topBorder.spotlight {
    /* no background-color. use sites default */
    background-position: 0 -18px !important;
}

.reactie.last > .reactieContainer {
    border-left: 1px solid #DFDFDF !important;
}

.reactieBody {
    background-color: #E8E8E8 !important;
    border: 1px solid #BBBBBB;
    border-top: 0 none;
    margin-left: -20px !important;
    padding-left: 19px !important;
}

.reactieBody.hidden { /* hidden (collapsed) */
    background-color: #DFDFDF !important;
    border: 1px solid #BBBBBB !important;
    border-top: 0 none !important;
}

.reactieBody.informative { /* +2 green */
    background-color: #E8EFE8 !important;
    border: 1px solid #BBC9BB !important;
    border-top: 0 none !important;
}

.reactieBody.spotlight { /* +3 green */
    background-color: #E8F8E8 !important;
    /* border: 1px solid #BECFBE !important; */
    /* border-top: 0 none !important; */
}

.reactieBody .reactieFooter {
    margin-bottom: 0px !important;
    padding-bottom: 10px !important;
}

#toggleButtonTxt {
    background: url("http://i48.tinypic.com/6nr24j.png") no-repeat scroll right -15px #DFDFDF !important;
    text-shadow: -1px -1px #EFEFEF;
    color: #BFBFBF !important;
}
   
#toggleButtons:hover #toggleButtonTxt {
    background-position: right 0 !important;
    text-shadow: 1px 1px #F4F4F4;
    color: #6E6E6E !important;
}

.noShow #toggleButtonTxt {
    background-image: none !important;
}

.popupContent {
    text-shadow: none !important;
}

#modPopup {
    text-shadow: none !important;
}

#bottom {
    text-shadow: none !important;
}  

#bottom div.hr {
    background-color: #EFEFEF !important;
    border-radius: 0 !important;
}

.article img {
    border: 1px solid #BBBBBB;
}

/* thumbs */
li > .thumb, .vathumb > .thumb, .thumb.product, .thumb.video {
    background: none repeat scroll 0 0 #EFEFEF !important;
}

.tweakbaseHeader > .thumb, .usericon {
    background: none repeat scroll 0 0 #FAFAFA !important;
}

/* itknowledgebase */
.bar {
    text-shadow: none !important;
}

.itknowledgebasewrapper {
    background: none repeat scroll 0 0 #E8E8E8 !important;
}

/* jobs */
.listing .replies {
    text-shadow: none !important;
}

/* v&a */
.searchAanbod #nearbyAdForm {
    background: none repeat scroll 0 0 #E8E8E8 !important;
}

.more.active a {
    text-shadow: none !important;
}

.replyWarning:empty {
    margin: 0 !important;
}

table.highlights {
    border-top: 1px solid #EFEFEF !important;
}

table.highlights td {
    border-bottom: 1px solid #EFEFEF !important;
}

/* pricewatch */
.BBGUnderPic {
    text-shadow: none !important;
}

/* got */    
.commentCount {
    text-shadow: none !important;
}

div.resizewarning div {
    text-shadow: none !important;
    background-image: url("http://i50.tinypic.com/25ssk5z.png") !important;
}

div.info, div.message, div.faq, form.form1, form.form2, #folders, #myprofilecontainer, table.info {    
    border: 1px solid #C7C7C7 !important;
}

.messagecontent h1, .messagecontent h2, .messagecontent h3, .messagecontent h4 {
    text-shadow: none !important;
}

table.shop-listing tr:first-child td, table.listing tr:first-child td {
    border-top: 1px solid #CFCFCF !important;
}

table.shop-listing td, table.listing td {
    border-bottom: 1px solid #CFCFCF !important;
}

table.listing td.last {
    border-right: 1px solid #CFCFCF !important;
}

table.listing tr.alt1 > td {
    background: none repeat scroll 0 0 #E8E8E8 !important;
}

table.listing tr.alt2 > td {
    background: none repeat scroll 0 0 #EFEFEF !important;
}

#myprofilecontainer h2 {
    background: none repeat scroll 0 0 #DFDFDF !important;
    border-bottom: 1px solid #C7C7C7 !important;
}

#myprofilecontainer {
    border-top: none !important;
}

p.pagecounter b { /* Note: this b element should be strong? */
    text-shadow: none !important;
}

.topicstarter .messageheader .poster .user { /* underline topic starter */
    text-decoration: underline !important;
}

/* image viewer */
#imageViewer {
    text-shadow: none !important;
}

/* gallery */
ul.galleryProfile li {
    background-position: 0 3px !important; 
}

th {
    text-shadow: none !important;
}

.userReview {
    background: #E8E8E8 !important;
    border: 1px solid #BBBBBB !important;
}

.galleryInnerTable td {
    border-bottom: 1px solid #EFEFEF !important;
}

table.pwListTable td {
    border-top: 1px solid #EFEFEF !important;
}

table.pwListTable tr.first td {
    border-top: none !important;
}

#userBoxContent {
    text-shadow: none !important;
}

} /* end */
