css:
@-moz-document domain("mysleepbot.com") {

body {
  background-color: #222 !important;
  background-image: none !important;
  color: #ddd !important;
  }

h1, h2, h3, h4, h5, h6 {
  color: #eee !important;
  background-color: #333 !important;
  text-shadow: 0 1px 0 #444!important;
  }

a {
  color: #eee !important;
  }

a:hover {
  color: #888 !important;
  }






#sidebar {
  color: #bbb !important;
  /* font-size: 100% !important; */
  text-shadow: 0 1px 0 #bbb !important;
  background-color: none !important;
  }


#sidebar a {
  color: #eee !important;
  text-shadow: 0 1px 0 #555 !important;
  }

#sidebar a:hover {
  color: #888 !important;
  text-shadow: 0 1px 0 #777 !important;
  }







#user_email {
  background-color: #555 !important;
  font-size: 100% !important;
  }


#sidebar .user_box {
  border: 1px solid #444 !important;
  }

body #sidebar .user_box {
  background-color: #555 !important;
  }


body .grad_grey, #sidebar ul#accordion > li, #nav_top ul li a, #nav_top ul li ul.dropdown li, .content_accordion h3.bar, .box .no_padding .dataTables_wrapper .ui-corner-tl, .dataTables_scrollHead, .list_slider .slider-nav, .slider-content .title, .list_slider .slide-up, .list_slider .slide-down, #sidebar ul#side_links li a, div.main_container .tabs ul.tab_header li a, div.main_container .tabs ul.tab_sider li a, .fc .fc-agenda-slots tr th, .wizard .wizard_steps ul li.current {
  background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#555), color-stop(99%,#333)) !important;
  }


#sidebar ul#accordion li a, #sidebar ul#side_links li a {
  background-color: #444 !important;
  background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#555), color-stop(99%,#333)) !important;

  border-top: 0px solid #444 !important;
  border-bottom: 1px solid #272727 !important;
  }



#sidebar ul#accordion, #sidebar ul#side_links {
  border: 1px solid #272727 !important;
  }

#sidebar ul#accordion, #sidebar ul#side_links {
  border: 1px solid #272727 !important;
  }





.DataTables_sort_wrapper {
  color: #eee !important;
  text-shadow: 0 1px 0 #777 !important;
  }


.DataTables_sort_wrapper:hover {
  color: #888 !important;
  text-shadow: 0 1px 0 #777 !important;
  }




.box .no_padding .dataTables_wrapper .ui-corner-tl {
  color: #eee !important;
  text-shadow: none !important;
  }

button {
  color: #eee !important;
  }







#sidebar ul#accordion li a, #sidebar ul#side_links li a {
  line-height: 37px !important;
  }




}
