css:
@-moz-document domain("tweakers.net") {
html {overflow-x:hidden;}
#contentArea {padding-top: 10px !important; padding-bottom: 0px !important; padding-right: 50px !important; margin: 0px 10px !important}
#layout.content-grid {grid-column: 1/ -1 !important;}
.headline {padding-bottom: 0.0rem !important; padding-top: 0.2rem !important;}
table.highlights td {line-height: 30px !important; padding: 0px 0px 0px 0px !important;}
.article {font-size: 12px !important; text-align: justify !important;}
#menu {width: 1450px !important; margin: 0px 130px !important;}
#menubottombar .menubottom {margin: 0px 13px !important; width: 985px !important;}
#searchbar {height: 40px !important;}
.pageTabs {margin: 0px 40px !important; width: 925px !important;}
.entityHeader {margin: 0px 0px 0px 0px !important; width: 925px !important;}
#entity > div {margin: 0px 40px !important; width: 100% !important; min-width: 600px; padding: 15px 0px 10px; border-top: 1px solid rgb(221, 222, 222);}
#filterBackButton {position: absolute !important; border: 0px !important; left: 40px !important; top: 72px !important; border-radius: 7px !important;}
#trackerContainer {background: linear-gradient(rgb(38, 40, 40), rgb(28, 30, 30)) repeat scroll 0px 0px rgb(33, 35, 35);}
#tracker .fakeTop > div {opacity: 0.05 !important;}
#tracker .fakeTop > div:hover {opacity: 1.0 !important;}
#streamone {position: absolute !important; left: 850px; top: 71px;}
#true {position: absolute !important; left: 950px; top: 4px;}
#search .keywordSearch {position: absolute !important; left: 245px !important; top: 7px !important; border-radius: 7px !important; height: 25px !important; width: 518px !important; z-index: 0 !important;}
#search input.text {position: absolute !important; top: 0px !important; margin: 1px 0px !important; font-size: 12px !important; width: 340px !important; height: 24px !important;}
.keywordSearch .keyword{margin-left: 10px !important}
#search input.submit {top: 0px !important; right: 10px !important; margin: 0px 0px !important; height: 23px !important; width: 24px !important;}
#search .options {margin: -2px 7px !important;}
#tracker .trackeritem li {padding: 1px 0px 1px 0px !important;}
#reacties .commentLink {margin: -5px -2px !important; background-color: #EBF3F5; border: 1px solid #D9DDDF; border-radius: 5px; color: #616464 !important; display: block; font-size: 11px; padding: 1px 7px; width: 42px;}
#reacties .commentLink:hover {background: #0CA3D4; border: 1px solid #0B94BF; color: #fff !important; text-decoration: none !important;}
#about {height: 120px !important; padding-top: 23px !important; position: relative !important; margin-left: 80px !important;}
#about .wvhj {top: 7px !important;}
table.listing td {padding: 2px 0px !important;}
#notifications {width: 960px; margin: 0px 30px;}
#about .bulletSeparatedList {margin: 0px 100px !important;}
#streamone {position: absolute !important; top: 5px !important; left: 800px !important;}
}
