css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tweakers.net") {

#fpaItems {
   float:right;
   width:245px;
   padding-bottom: 0px;
}
.fpaItem {
   width: 245px !important;
   height: 120px !important;
   float: left;
   overflow: hidden;
}
.fpaItem .imageContainer {
   width: 245px !important;
   height: 80px;
   overflow: hidden;
}
.secondColumn {
   width: 245px !important;
   float: right !important;
}
.mainColumn {
   width: 710px !important;
   margin-right: 24px;
   float: left;
}
#community li {
   float: left;
   width: 295px !important;
   margin-right: 30px;
   border-bottom: 1px solid rgb(232, 234, 234);
   padding: 5px 0px !important;
}
#social {
   display: none;
}
#jobs{
   display: none;
}
#pricewatch{
   display: none;
}

#pricewatch input.text {
   width: 156px !important;
}
h1, h2, h3, p {
   margin: 0px 0px 5px !important;
}
#pricewatch, #jobs, #poll, #newsletter, #sponsored, #textlink, #social .twitterButton, #social .facebookButton {
   padding: 5px 15px 5px !important;
   border: 1px solid rgb(221, 222, 222);
   border-radius: 1px 1px 1px 1px;
   background: -moz-linear-gradient(rgb(245, 246, 246), rgb(237, 238, 238)) repeat scroll 0% 0% rgb(237, 238, 238);
   box-shadow: 0px 0px 0px 1px rgba(255, 255, 255, 0.33) inset;
}
.fpaItem div.textContainer {
   overflow: hidden;
   height: 20px !important;
   padding: 5px 18px !important;
   font-size: 14px;
   line-height: 21px;
   color: rgb(200, 203, 203);
   background-color: rgb(33, 35, 35);
   border-right: 1px solid rgb(163, 166, 166);
}
table.highlights td {
   line-height: 19px !important;
   border-bottom: 1px solid rgb(232, 234, 234);
   padding: 0px 0px !important;
}
table.highlights td a:hover {
   text-decoration: none !important;
}

table.highlights tbody :hover {
   background-color:#eee;
}

table.highlights {
   margin-bottom: 5px!important;
   width: 100%;
   table-layout: fixed;
}
#menubar {
   margin: 0px auto;
   height: 32px;
   width: 100% !important;
   position: relative;
   top: 51px;
   background: -moz-linear-gradient(rgb(38, 40, 40), rgb(28, 30, 30)) repeat scroll 0px 0px rgb(33, 35, 35);
   border-top: 1px solid rgb(28, 30, 30);
   border-bottom: 1px solid rgb(28, 30, 30);
   border-radius: 1px 1px 1px 1px;
   box-shadow: 0px 0px 0px 1px rgba(255, 255, 255, 0.15), 0px -10px 15px rgba(255, 180, 38, 0.12);
}
#searchbar {
   margin-top: 34px;
   height: 16px !important;
   background: -moz-linear-gradient(rgb(245, 246, 246), rgb(237, 238, 238)) repeat scroll 0% 0% rgb(237, 238, 238);
   border-radius: 6px 6px 0px 0px;
}
#search {
   padding-top: 33px !important;
}
#search #true {
   display:none;
   float: right;
   margin-top: 3px;
}
#search .keywordSearch {
   position: absolute;
   height: 28px !important;
   width: 265px !important;
   margin: -48px 0px 0px 734px !important;
   z-index: 10 !important;
}
#search input.text {
   font-size: 14px;
   width: 210px !important;
   height: 19px;
}
.keywordSearch input {
   float: left;
}
#socialButtons {
   display:none;
   height: 28px;
   margin-bottom: 14px;
}
.filterBox {
   overflow: hidden;
   margin-bottom: 5px !important;
}
#ticker {
   display:none;
   height: 34px;
   overflow: hidden;
   border-bottom: 1px solid rgb(60, 60, 60);
}
#about {
   padding-top:5px;
   height: 20px !important;
   overflow: hidden;
   position: relative;
}
#bottom {
   height: 50px !important;
   width: 100%;
   min-width: 1000px;
   position: absolute;
   bottom: 0px;
   background: -moz-linear-gradient(rgb(38, 40, 40), rgb(28, 30, 30)) repeat scroll 0px 0px rgb(33, 35, 35);
}
.thumb.community {
   width: 36px !important;
   height: 36px !important;
}
.thumb.community img{
   width: 36px !important;
   height: 36px !important;
}
#community p {
   color: rgb(130, 133, 133);
   line-height: 12px !important;
}
#community .score2 {
   position:absolute;
   margin-top:-36px;
   margin-left:184px;
   background: -moz-linear-gradient(rgb(131, 212, 50), rgb(101, 202, 1)) repeat scroll 0% 0% rgb(116, 207, 24);
   border-color: rgb(96, 191, 0);
}
#community > div {
   width: 710px !important;
}
#community li {
   float: left;
   width: 325px !important;
   margin-right: 30px;
   border-bottom: 1px solid rgb(232, 234, 234);
   padding: 15px 0px;
}
#layout {
   min-width: 1000px;
   background-color:#464649;
}
#contentArea{
   width: 1020px;
   padding: 8px 20px 0px 20px !important;
   margin: 0px auto;
   border: 1px solid rgb(221, 222, 222);
   border-width: 0px 1px 0px 1px;
   border-radius: 0px 1px 0px 1px;
   background-color: #fff;
   padding-top: 14px !important;
   padding-bottom: 60px !important;
}
#reacties {
   width: 980px !important;
}
#bottom div.hr {
   width: 1022px;
   margin: 0px auto;
   background-color: rgb(255, 255, 255);
   height: 6px;
   border-radius: 0px 0px 6px 6px;
}
.relatedBlock {
   display:none;
   margin-bottom: 25px;
   padding-top: 15px;
   border: 1px solid rgb(221, 222, 222);
   border-radius: 1px 1px 1px 1px;
   background: linear-gradient(rgb(245, 246, 246), rgb(237, 238, 238)) repeat scroll 0% 0% rgb(237, 238, 238);
   box-shadow: 0px 0px 0px 1px rgba(255, 255, 255, 0.33) inset;
}
#search #streamone {
   display:none;
   float: right;
   margin: 4px 20px 0px 0px;
}
.reactie .topBorder {
   background: url("http://www.evior.net/filedepo/reply_top.png") no-repeat scroll 0% 0% transparent !important;
   height: 8px;
   width: auto;
   margin-left: -40px;
}
#userbar {
    position: absolute;
    right: -18px !important;
    top: -42px !important;
}
/*
#menu li:hover,#logo:hover+.sitename{
    text-shadow:#272828 1px 1px 1px;
    border:0px !important;
    background:transparent !important;
    background-image:url() !important;
}*/
#fp_tabs_container {
   position:absolute;
   width: 22px;
   margin-top:-10px;
   overflow: hidden;
   height: 220px !important;
   margin-bottom: 20px;
   margin-left: -43px;
}
#fp_tabs_container:hover{
   position:absolute;
   width: 152px;
   margin-top:-10px;
   overflow: hidden;
   height: 220px !important;
   margin-bottom: 20px;
   margin-left: -173px;
}
#forumheading {
   position:absolute;
   width: 22px;
   margin-top:-10px;
   overflow: hidden;
   height: 220px !important;
   margin-bottom: 20px;
   margin-left: -43px;
}
#forumheading:hover{
   position:absolute;
   width: 152px;
   margin-top:-10px;
   overflow: hidden;
   height: 220px !important;
   margin-bottom: 20px;
   margin-left: -173px;
}
.pageTabsContainer {
   background-color:transparent !important;
   position:absolute;
   width: 20px;
   left:50%;
   margin-top:10px;
   overflow: hidden;
   height: 220px !important;
   margin-bottom: 20px;
   margin-left: -530px;
}
.pageTabsContainer:hover{
   position:absolute;
   width: 152px;
   left:50%;
   margin-top:10px;
   overflow: hidden;
   height: 220px !important;
   margin-bottom: 20px;
   margin-left: -662px;
}
#tabs {
   background-color:transparent !important;
   position:absolute;
   width: 20px;
   left:50%;
   margin-top:10px;
   overflow: hidden;
   height: 220px !important;
   margin-bottom: 20px;
   margin-left: -530px;
}
#tabs:hover{
   position:absolute;
   width: 152px;
   left:50%;
   margin-top:10px;
   overflow: hidden;
   height: 220px !important;
   margin-bottom: 20px;
   margin-left: -662px;
}
#title{
   display:none;
}
.pageTabs {
    width: 52px !important;
    margin: 0px 0px 0px 0px !important;
}
#fp_tabs {
    padding-top: 6px;
    padding-left: 10px;
    border-bottom: 0px !important;
}
#forum_tabs {
    padding-top: 6px;
    padding-left: 10px;
    border-bottom: 0px !important;
}
.pageTabs li { 
    clear:both !important;
    width: 150px;
    height: 28px;
    margin-top: 4px !important;
    margin-right: 1px !important;
    border-width: 1px 1px 1px 1px !important;
    border-style: solid !important;
    border-color: rgb(216, 216, 216) !important;
    border-image: none;
    background: linear-gradient(rgb(255, 255, 255), rgb(250, 250, 250)) repeat scroll 0% 0% rgb(255, 255, 255);
}
.pageTabs li.active {
    width: 151px;
    height: 30px;
    margin-top: 0px;
    border-color: rgb(195, 198, 198);
    border-width: 1px 0px 1px 1px !important;
    background: none repeat scroll 0% 0% rgb(255, 255, 255);
}

/*Reacties*/
.reactieHeader {
    line-height: 20px;
    padding: 3px 0px 5px !important;
}
.reactieContent {
    overflow: hidden;
    padding-bottom: 4px !important;
    padding-right: 15px;
}
.reactieBody{
    position:relative;
}
.eenReactie {
    margin-top: 0px !important;
}
.reactieHeader .usericonsmall {
    width:20px !important;
    height:20px !important;
    float: left;
    border-radius: 2px 2px 2px 2px;
    margin-top: 4px;
    margin-right: 10px;
}
.scoreButton.large {
    width: 20px !important;
    height: 20px !important;
    font-size: 14px;
    font-weight: bold;
    line-height: 20px !important;
    border-radius: 2px 2px 2px 2px;
}
.reactieHeader .scoreButton {
    float: left !important;
    margin-top: 4px;
    margin-right: 5px !important;
}
.reactieHeader a {
    line-height:30px;
    font-size:15px !important;
    font-weight: bold;
    color:#666 !important;
    text-decoration: none;
}
.reactieHeader .date {
    position:absolute;
    right:60px;
    margin-top:-31px;
    color: rgb(140, 140, 140) !important;
    font-size: 10px !important;
    font-weight: normal;
}
.modButtons{
    display:none;
}
.reactieFooter {
    position:absolute;
    right:-16px;
    top:7px;
    width:50px;
    overflow: hidden;
    font-size: 10px !important;
    padding-bottom: 0px !important;
    padding-right: 15px;
}
.modFilter {
    float: left;
    width: 355px;
    line-height: 15px;
    margin-bottom: 0px !important;
}
.layoutFilter {
    float: right;
    margin-bottom: 0px !important;
}
#reacties > .spacer {
    border-top: 1px solid rgb(215, 223, 221);
    margin: 8px 0px 8px !important;
}

/*Forum*/
.galleryHeadingContainer {
    background-color: rgb(237, 238, 238);
    padding-bottom: 5px !important;
}
.galleryHeadingContainer > div, #tweakbase > div {
    margin: 0px auto;
    width: 90%;
    min-width: 980px;
    padding: 5px 0px 0px !important;
    border-top: 1px solid rgb(221, 222, 222);
}
.galleryInnerTable td {
    border-bottom: 1px solid rgb(215, 223, 221);
    padding: 3px !important;
}

/*Products*/
table.listing td {
    padding: 0px 0px !important;
}
.thumb {
    background: none repeat scroll 0% 0% rgb(255, 255, 255);
    padding: 5px;
    display: block;
    border: 1px solid rgb(232, 234, 234);
    border-width: 0px 1px 0px 1px !important;
    border-radius: 3px 3px 3px 3px;
    width: 60px;
}
table.listing .compare input, table.listing .compareEdition input {
    margin-top: 20px !important;
}
#tweakbase{
    display:none;
}

}
