@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("feedly.com") {

/*  Blocked */
DIV#sponsorsModule_part.sideAreaModule,
DIV#flickrModule_part.sideAreaModule,
DIV#youtubeModule_part.sideAreaModule,
DIV#tweetsModule_part.sideAreaModule,
DIV#fusionModule_part.sideAreaModule,
DIV#mobileModule_part.sideAreaModule,
DIV#portfolioModule_part.sideAreaModule,
DIV#newsfeedModule_part.sideAreaModule,
DIV#timelineModule_part.sideAreaModule
    {display: none !important;}


/* 100% width */
DIV#feedlyFrame
    {width: 100% !important;}
    
    /* zooien met DIV#mainArea. 100%? */
    

/* Black BG */
BODY.home,
.entryHolder,
.inlineFrame
/* .area */
    {background: #111 !important; color: #111 !important; background-color: #111 !important; }


/* Dark Grey BG */
DIV#feedlyPart0.area,
DIV#floatingBar,
DIV#contentArea
    {background: #222 !important;}


/*  Light Grey Bg */
div.pageActionBar
{background: #444 !important;}


/* light grey font */
h1,
#floatingBar h1,
.unread
    {color: #444 !important;}

    
/* Light Grey Font */
.summary,
#sideArea .feedIndexTitle
    {color: #666 !important;}


/* Borders */
.inlineFrame
    {border: 2px solid #333 !important;}

  
}
