css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("oboi.ws") {

body {
    overflow-x:hidden!important;
}
body, td, th {
    font:10pt/12pt 'Trebuchet MS',Tahoma,sans-serif
}
.head .cont {
    overflow:visible!important;
    height:26px!important;
    width:100%!important;
}
.head .logo {
    top:0!important;
    left:0!important;
    color:transparent!important;
}
.head .logo img {
    width:86px!important;
}
.head .menu {
    top:-8px!important;
    left:110px!important;
    z-index:5!important;
    
}
.please {
    background: #012730;
    background: -webkit-linear-gradient(top,  #012730 0%,#001114 100%)!important;
    background: linear-gradient(to bottom,  #012730 0%,#001114 100%)!important;
    box-shadow:0 0 12px -2px #000A0D,0 0 14px -6px #004E66!important;
    border-radius:3px;
    height: 65px!important;
    padding: 10px 10px!important;
    right: 20px!important;
    top: 41px!important;
    width: 135px!important;
    opacity:0.08;
}
.please:before {
    position:absolute;
    top:-15px;
    left:25px;
    content:"";
    width:0;
    height:0;
    border-top:0;
    border-right:12px solid transparent;
    border-bottom:15px solid #012730;
    border-left:12px solid transparent;
}
.head:hover .please {
    opacity:1;
}
.head .secondRow {
    top:-10px!important;
    left:auto!important;
    right:0px!important;
    opacity:0.5!important;
    width:150px!important;
}
.head .secondRow:hover {
    opacity:1!important;
}
.body .cont {
    position:static!important;
}
.left {
    position:relative!important;
    padding:10px 0!important;
    z-index:1!important;
}
.right {
    padding:9px 0!important;
}
.right .block .h4{
    padding-top:10px!important;
    padding-bottom:2px!important;
}
.right .block {
    padding-bottom:10px!important;
}
.right .block:first-child {
    padding:0px!important;
}
.block {
    padding-top:0!important;
    padding-bottom:0!important;
    margin-top:0!important;
    margin-bottom:0!important;
    border-bottom: 1px solid #B2DCE1!important;
}
.block>h1 {
    padding:0px!important;
    font-size:15pt!important;
}
[colspan="3"] {
    padding:0px!important;
}
.block.pageselector {
    padding-top:10px!important;
    padding-bottom:10px!important;
}
.block.share-links {
    padding-top:10px!important;
    padding-bottom:20px!important;
}
#vk_comments {
    padding-top:10px!important;
}
ins, .block+.block+.block>h1, .block>h1>span, .block>h2, .block>h3,
.left .block p,
[style="width:609px;height:250px;overflow:hidden;"],
#togmenu, #togline {
    display:none!important;
}
.prewiev {
    overflow:visible!important;
}
.prewiev .name {
    left:auto!important;
    right:80px!important;
    padding-left:100px!important;
}
.min.prewiev {
    overflow:hidden!important;
}
.min.prewiev .name {
    left:10px!important;
    right:auto!important;
    padding-left:0px!important;
}
.min.prewiev .panel {
    margin-bottom:0px!important;
}
.panel {
    margin-left:-20px!important;
    width:649px!important;
    margin-bottom:-1px!important;
}
.min.prewiev .rating {
    right:-1px!important;
}
.rating {
    right:-19px!important;
}
.block-blue {
    padding:0 20px 5px!important;
}
.block-blue.nospace {
    color:#5C8D95!important
}
.block-blue.nospace h4{
    display:none!important;
}
.download-link-wrapper {
    color:rgba(255,255,255,0.4)!important
}
.download-links {
    position:relative!important;
}
.download-links a, .category-link, .tag-link, .download-original-link a{
    text-decoration:none!important;
}
.download-links a:hover, .category-link:hover, .tag-link:hover, .download-original-link a:hover {
    text-decoration:underline!important;
}
.download-original-link {
    font-size:1em!important;
}
.download-original-link a{
    font-size:1.5em!important;
}
.download-link.user-resolution {
    position:absolute!important;
    top:-70px!important;
    left:-10px!important;
    z-index:100!important;
    background: none repeat scroll 0 0 #84B200!important;
    box-shadow:0 -8px 8px 2px #4F8417 inset!important;
}
.download-link.user-resolution:hover {
    text-decoration:none!important;
}
.download-link.user-resolution:hover {
    box-shadow:0 8px 8px 2px #508B00 inset!important;
}
.block-blue .download-link-wrapper {
    padding:0px!important;
}
.block-blue .basket_add{
    opacity:0.07!important;
}
.block-blue .download-link-wrapper:hover .basket_add{
    opacity:1!important;
}
.palette {
    padding:0!important;
    position:fixed!important;
    top:36px!important;
    left:0!important;
    width:105px!important;
    border-radius:3px!important;
    box-shadow:0 0 12px -2px #000A0D,0 0 14px -6px #004E66!important;
    opacity:0.1!important;
    z-index:1001;
}
.palette:hover {
    opacity:1!important;
}
.palette a {
    border-radius:0!important;
    padding:0!important;
    margin:0!important;
}


}
