css:
#header-bottom-right .user{margin-left:0!important}
#mail{margin:7px 10px}
#resaccountswitchericon .downarrow{display:inline-block}
#resshortcuts.sr-bar{font-size:13px!important}
#sr-header-area{font-size:13px!important;top:2px}
.moderator #moderation_tools .content ul{font-size:13px}
div#resshortcuts,div#resshortcuts a,div#resshortcuts,div#resshortcuts a:hover{display:block!important;font-size:18px!important;line-height:24px!important}
.loggedin div#header-bottom-right {padding-left:15px!important}
div.sr-list a:hover,body .sr-bar a:hover,#resshortcuts a:hover,#resstaticshortcuts a:hover{font-size:18px!important}
div#userbarToggle.userbarHide, div#userbarToggle.userbarShow {padding-right:10px}
#RESAccountSwitcherIcon .downArrow {display:inline-block}
#RESShortcuts {font-size:18px!important}
div#RESShortcutsViewport a {margin:3px!important;display:block!important}
div#RESShortcutsViewport a:hover {margin:3px!important;font-size:18px!important}
div#header-bottom-right, body div#header-bottom-right { bottom:10%!important}
#RESAccountSwitcherIcon {top:0;position:absolute!important;right:0;}
#mail.havemail:after {content:"unread messages";}
.nohavemail {display:none!important;}
#modmail.havemail:before {content:"unread "; }
#mail, #modmail{display:block!important;background:none!important;margin:5px!important; padding:1px!important;text-indent:0!important;margin: 10px 0px!important; font-size:16px; width:auto!important;height:auto!important;overflow: visible}
.loggedin div#header-bottom-right { float:none}
#modmail.nohavemail {display:none!important}
.commentarea .buttons, .profile-page .noncollapsed .buttons {display:inline!important;}
