@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("freak.no") {
/*Fjerner marger ol. */
#siteWrapper{
padding: 0px !important;
}
/*Fjerner marger ol. */
html{
height: 100% !important;
margin: 0px !important;
padding: 0px !important;
overflow-x: hidden !important;
overflow-y: scroll !important;
}
/*Forsikring at høyden er på plass*/
body{
height: 100% !important;
}
/*Setter sidebredde*/  
html, body, table, .page, #siteWrapper, #browserWidth{
max-width: 100% !important;
min-width: 150px !important;
}
/*Justerer søklisting inkl tråder under hurtiglenker*/
.contentWrapper #inlinemodform .tborder tbody tr:nth-child(2) td:nth-child(3){
width:100% !important;  
}
.contentWrapper #inlinemodform .tborder tbody tr td:nth-child(7){
display:none !important;
}
/* Justerer brukernavn for å få fast bredde på poster */
#posts div[align="center"] .page div[align="left"] div .tborder tbody tr:nth-child(1) td:nth-child(1) {
 padding-bottom:60px !important;
 font-size: 17px !important;
}
#posts div[align="center"] .page div[align="left"] div .tborder tbody tr:nth-child(2) td:nth-child(1) div:nth-child(1) {
 position:absolute !important;
 margin-top: -6.5% !important;
}
#posts div[align="center"] .page div[align="left"] div .tborder tbody tr:nth-child(2) td:nth-child(1) div:nth-child(1) a {
 color: white !important; 
}
/*Justerer sidenavigasjon i tråder*/
.pagenav .tborder tbody tr .vbmenu_control{
display:none !important;
}
.pagenav{
width:1px !important;
display:table !important;
margin-left:auto !important;
margin-right:auto !important;
}
/*Fjerner sist redigert*/
#posts div .page div div .tborder tbody tr:nth-child(4) .alt1 .smallfont
{
display: none !important;
}
/*Fikser lange lenker*/

#posts div .page div div .tborder tbody tr:nth-child(2) .alt1 div a
{
word-break: break-all !important;
}
/* -######- Fjerner mendger av ting !! -######- */
/*Right Column*/div[align="center"]>.page>div[align="left"][style="padding:0px 0px 0px 0px"]>.page>tbody>tr[valign="top"]>td[style="padding-left: 6px"],
/*Ad below navbar*/.adBelowNavbar,
/*Hele header untatt logo*/#headerWrapper>table>tbody>tr td:nth-child(2),
/*Forside spacer */#collapseobj_module_5 tr:nth-child(1),
/*Forside spacer */#collapseobj_module_5 tr td:nth-child(1),
/*Forside siste innlegg */#collapseobj_module_5 tr td:nth-child(3),
/*Forside svar */#collapseobj_module_5 tr td:nth-child(4),
/*Forside visninger */#collapseobj_module_5 tr td:nth-child(5),
/*Forside forum */#collapseobj_module_5 tr td:nth-child(6),
/*Chat*/.tborder table tbody tr[align="center"] td:nth-child(2),
/*Kontrollpanel*/.tborder table tbody tr[align="center"] td:nth-child(3),
/*Dagens Innlegg*/.tborder table tbody tr[align="center"] td:nth-child(4),
/*KP*/.tborder table tbody tr[align="center"] td:nth-child(6),
/*Hurtiglenker*/.tborder table tbody tr[align="center"] td:nth-child(5),
/*Bilde First New (i liste over tråder, venstre)*/img[src="http://static3.freak.no/forum/images/buttons/firstnew.gif"],
/*Navbar/underforumtekst*/td[class="navbarbg alt1"],
/*Tråd Svar knapp*/img[src="http://static3.freak.no/forum/images/buttons/reply.gif"],
/*Tråd gammel post */img[src="http://static3.freak.no/forum/images/statusicon/post_old.gif"],
/*Tråd ny post */img[src="http://static3.freak.no/forum/images/statusicon/post_new.gif"],
/*Tråd hurtigsvar */img[src="http://static3.freak.no/forum/images/buttons/quickreply.gif"],
/*Tråd multisiter */img[src="http://static3.freak.no/forum/images/buttons/multiquote_off.gif"],
/*Tråd meldingskboks verktøy*/ #vB_Editor_QR_controls,
/*Tråd meldingsboks valg */ #collapseobj_quickreply tr .panelsurround .panel div fieldset[style="margin:3px 0px 0px 0px"],
/*Tråd avansert modus*/ input[id="qr_preview"][class="button"][value="Gå til avansert modus"],
/*Tråd leser nå */ div[align="center"]>.page>div>table[class="tborder"]:nth-child(15),
/*Forum tråd trådstarter etc startside*/div[align="center"] .page>div[align="left"][style="padding:0px 0px 0px 0px"]>.contentWrapper>.tborder>thead>tr[align="center"]>td[class="thead"]:nth-child(1),
/*Forum tråd trådstarter etc startside*/div[align="center"] .page>div[align="left"][style="padding:0px 0px 0px 0px"]>.contentWrapper>.tborder>thead>tr[align="center"]>td[class="thead"]:nth-child(3),
/*Forum tråd trådstarter etc startside*/div[align="center"] .page>div[align="left"][style="padding:0px 0px 0px 0px"]>.contentWrapper>.tborder>thead>tr[align="center"]>td[class="thead"]:nth-child(4),
/*Forum tråd trådstarter etc startside*/div[align="center"] .page>div[align="left"][style="padding:0px 0px 0px 0px"]>.contentWrapper>.tborder>thead>tr[align="center"]>td[class="thead"]:nth-child(5),
/*Forum Extra info siste innlegg*/ .contentWrapper .tborder tbody[style=""] tr[valign="top"][align="center"] .alt2,
/*Forum Extra info tråder innlegg*/ .contentWrapper .tborder tbody[style=""] tr[valign="top"][align="center"] .alt1,
/*Forum forumbeskrivelser*/ div[align="center"]>.page>div[style="padding:0px 0px 0px 0px"]>.contentWrapper>.tborder>tbody[style=""]>tr>td:nth-child(2) div[class="smallfont"][style="margin: 10px 10px 0px 10px"],
/*Forum forumkategorier*/ div[align="center"]>.page>div[style="padding:0px 0px 0px 0px"]>.contentWrapper>.tborder>tbody>tr>.tcat,
/*Forum aktive brukere*/ #collapseobj_forumhome_activeusers,
/*Ad Below navbar*/div[class="alt1 adBelowNavbar"],
/*Forum underforum trådliste*/ #threadslist tbody tr td:nth-child(1),
/*Forum underforum trådliste*/ #threadslist tbody tr td:nth-child(2),
/*Forum underforum trådliste*/ #threadslist tbody tr td:nth-child(4),
/*Forum underforum trådliste*/ #threadslist tbody tr td:nth-child(5),
/*Forum underforum trådliste*/ #threadslist tbody tr td:nth-child(6),
/*Forum underforum aktive brukere*/ div[align="center"]>.page>div>.contentWrapper>form[action="forumdisplay.php"]>.tborder>tbody>tr>td:nth-child(2),
/*Forum underforum moderatorer*/ div[align="center"]>.page>div>.contentWrapper>form[action="forumdisplay.php"]>.tborder>tbody>tr>td:nth-child(3),
/*Bilde multipage*/ img[src="http://static3.freak.no/forum/images/misc/multipage.gif"],
/*Tråder i dette forum tekst*/ #inlinemodform table[class="tborder"][style="border-bottom-width:0px"] tbody tr .tcat,
/*Rapporter regelbrudd*/ .page div[style="padding:0px 0px 0px 0px"] div[style="padding:0px 0px 6px 0px"] .tborder tbody tr:nth-child(4) .alt2 div a,
/*Signatur*/ .page div[style="padding:0px 0px 0px 0px"] div[style="padding:0px 0px 6px 0px"] .tborder tbody tr:nth-child(3) .alt1 div,
/*Registreringsdato*/ .page div[style="padding:0px 0px 0px 0px"] div[style="padding:0px 0px 6px 0px"] .tborder tbody tr:nth-child(2) .alt2 .smallfont div:nth-child(2),
/*Forumbeskrivelser*/ #contentWrapper .tborder:nth-child(1) tbody[style=""] tr .alt1Active div[class="smallfont"],
/*Hurtigsvar valgmeny*/ #qrform .tborder #collapseobj_quickreply tr .panelsurround div div[align="left"] .fieldset,
/*Trådverktøy*/ .contentWrapper .tborder tbody tr #threadtools,
/*Trådsøk*/ .contentWrapper .tborder tbody tr #threadsearch
{
display:none !important;
}
/* -######- Endrer tekststørrelse !! -######- */
td,
#navbar_username,
#navbar_password,
.button,
/*Tekst i loginbox*/.panelsurround .panel blockquote p:nth-child(2),
/*Forum Sorteringsbokser*/ .smallfont select[id="sel_sort"][name="sort"],
/*Forum Sorteringsbokser*/ .smallfont select[id="sel_sort"][name="sort"] option,
/*Forum Sorteringsbokser*/ .smallfont select[id="sel_order"][name="order"],
