/*Forum Sorteringsbokser*/ .smallfont select[id="sel_daysprune"][name="daysprune"],
/*Forum Sorteringsbokser*/ .smallfont select[id="sel_daysprune"][name="daysprune"] option
{
font-size: 30px !important;
}
.smallfont,
.quotetop,
.fieldset legend,
/*Linker i loginbox*/.panel div div ul li,
/*Liste brukerer KP*/.page div div[style="padding:0px 0px 6px 0px"] .tborder tbody tr[valign="top"] .alt1 div[style="padding: 7px; margin-bottom: 5px; font-size: 10px ! important; font-weight: normal;"],
/*Regler*/td[class="alt2"][align="left"] .normalfont div[style="padding: 10px 20px 10px 20px;"] p,
/*Regler*/td[class="alt2"][align="left"] .normalfont div[style="padding: 10px 20px 10px 20px;"] ul li,
/*Regler*/td[class="alt2"][align="left"] .normalfont div[style="padding: 10px 20px 10px 20px;"] ol li,
/*søkeboks*/ .vbmenu_option form[action="search.php?do=process"] div .bginput,
/*Tråd Tekstfelt */ .controlbar fieldset #vB_Editor_QR_textarea,
/*Avansert Tekstfelt*/ .controlbar #vB_Editor_001_textarea,
/*Lister i tekst*/ .page div[style="padding:0px 0px 0px 0px"] div[style="padding:0px 0px 6px 0px"] .tborder tbody tr:nth-child(2) td:nth-child(2) div ul li
{
font-size: 30px !important;
}
.navbar strong, .bigusername {
font-size: 40px !important;
}
/*Navigering i tråder*/.pagenav .tborder tbody tr .smallfont{
font-size: 50px !important;          
}
