/*Justerer freaken logo*/
#headerWrapper>table{
width:100px !important;
position:absolute !important;
top: 10px !important;
left: 5px !important;
z-index: 55 !important;
}
/*Justerer login / logget inn tekst*/
.page>div table[class="tborder"]:nth-child(2)
,
div[align="center"]>.page>div[align="left"][style="padding:0px 0px 0px 0px"]>#navbarWrapper>.tborder:nth-child(1){
background: none repeat scroll 0 0 #EBEBEB !important;
