css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("drexeldemocrats.blogspot.com") {

body {
width: 100% !important;
padding: 0 !important;
margin: 0 !important;
}

#content {
width: 100% !important;
padding: 0 !important;
margin: auto !important;
position: relative !important;
left: 0 !important;
}

#main{
width: 70% !important;
padding: 0 !important;
margin: 0 !important;
float: left !important;
position: relative !important;
font-size: 11pt !important;
}

#sidebar{
width: 30% !important;
padding: 0 !important;
margin: 0 !important;
float: right !important;
position: relative !important;
}

#header {
width: 100% !important;
padding: 0 !important;
margin: 0 !important;
position: relative !important;
}

#header div {
width: 100% !important;
padding: 0 !important;
margin: auto !important;
position: relative !important;
text-align: center !important;
background-position: top center !important;
}

#main h2, #main div {
padding: 10px !important;
}

#sidebar ul {
padding: 10px !important;
font-size: 11pt !important;
}

#sidebar #description {
width: 100% !important;
padding: 0 !important;
margin: 0 !important;
position: relative !important;
}

#space-for-ie {
display: none !important;
}

}
