css:
body.main_body {
background: #B5B4B2 url(http://i49.tinypic.com/xgl9p5.png) repeat-x;
background-attachment: fixed !important;
background-image: -moz-radial-gradient(center, ellipse cover,  rgba(0,0,0,0) 0%, rgba(0,0,0,0.65) 100%); 
background-image: -webkit-gradient(radial, center center, 0px, center center, 100%, color-stop(0%,rgba(0,0,0,0)), color-stop(100%,rgba(0,0,0,0.65))); 
background-image: -webkit-radial-gradient(center, ellipse cover,  rgba(0,0,0,0) 0%,rgba(0,0,0,0.65) 100%); 
background-image: -o-radial-gradient(center, ellipse cover,  rgba(0,0,0,0) 0%,rgba(0,0,0,0.65) 100%); 
background-image: -ms-radial-gradient(center, ellipse cover,  rgba(0,0,0,0) 0%,rgba(0,0,0,0.65) 100%); 
background-image: -radial-gradient(center, ellipse cover,  rgba(0,0,0,0) 0%,rgba(0,0,0,0.65) 100%); 
filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#00000000', endColorstr='#a6000000',GradientType=0 );
}

#logo {
background: transparent url(http://i47.tinypic.com/14e510x.png) no-repeat!important;
}

#topmenu li.selected a, #topmenu li.selected a:hover {
background-color: #555;
}

#topmenu li a{
color: #555;
}

#topmenu li a:hover {
color: #8F8F8F;
}

#tabmenu li.active a {
background: #555555;
}

#tabmenu li a {
background: #8F8F8F;
}

#tabmenu li a:hover {
background: #C0C0C0;
}

h1, h2, h3, h4, h5 {
color: #545350;
}

.wittetekst h4{
color: white;
}

.wittetekst h4:hover{
color: #808080;
}

a, #SUBreplies a {
color: #000000;
}

a:hover, #SUBreplies a:hover {
color: #8F8F8F;
}

#submenucontainer {
background: transparent url(http://i46.tinypic.com/2wreycx.png);
}

th.bluerow {
background: url(http://i46.tinypic.com/2wreycx.png);
}

.info, .question {
background: #E3E3E3 url(http://bierdopje.com/g/if/icons_png/information.png) no-repeat 10px 3px;
border: 1px solid #B6B5B2;
}

.block .header .wrap {
background: #808080 url(http://i49.tinypic.com/2lu57nn.jpg) repeat-x left -48px;
}

#nav, #nav ul {
background-color: #FFFFFF;
}

.container_16 .grid_15 {
width: 980px;
}

.searchblock {
margin-top: -32px;
}

.headband {
background-color: #6B6A66;
}

#submenu ul li a:hover {
color:#fff;
background:transparent url(http://i49.tinypic.com/2wdc4ty.png) no-repeat top right;
}


th.forumkop, #SUBreplies th.forumkop {
background-image: url(http://i50.tinypic.com/25ivhmw.png);
}

#pagination .active {
background: #808080;
border: 1px #B6B5B2;
}

#pagination .spacer {
background: #B5B5B2;
border: 1px #B6B5B2;
}

#pagination a:link, #pagination a:visited {
color: #0E1722;
}

#pagination a {
color: #555;
border: solid 1px #808080;
}

#pagination a:hover {
color: #8F8F8F;
border: solid 1px #555;
}

.markItUpContainer {
border: 1px solid #CDCDCD;
}

.markItUpEditor {
border: 3px solid #808080;
}

.button {
background: url(http://i48.tinypic.com/15mhfk7.png) no-repeat left top;
}

.button a {
background: url(http://i50.tinypic.com/mljs3o.png) no-repeat right top;
}

.button a:hover {
color: #FFFFFF;
}

table.kalender td, table.kalender th {
background: url(http://i1221.photobucket.com/albums/dd472/5xt/cells.png);
}

#footer {
background: #B5B4B2 url(http://i49.tinypic.com/xgl9p5.png) repeat-x;
}

.grid_16 p {
color: #E4E4E4;
}
