css:
@-moz-document domain("bierdopje.com") {
body.main_body {
background: #607A9F url(http://i1221.photobucket.com/albums/dd472/5xt/bgr-footer.png) repeat-x;
background-attachment: fixed !important;
background-image: -moz-radial-gradient(center, ellipse cover,  rgba(0,0,0,0) 0%, rgba(0,0,0,0.65) 100%); 
background-image: -webkit-gradient(radial, center center, 0px, center center, 100%, color-stop(0%,rgba(0,0,0,0)), color-stop(100%,rgba(0,0,0,0.65))); 
background-image: -webkit-radial-gradient(center, ellipse cover,  rgba(0,0,0,0) 0%,rgba(0,0,0,0.65) 100%); 
background-image: -o-radial-gradient(center, ellipse cover,  rgba(0,0,0,0) 0%,rgba(0,0,0,0.65) 100%); 
background-image: -ms-radial-gradient(center, ellipse cover,  rgba(0,0,0,0) 0%,rgba(0,0,0,0.65) 100%); 
background-image: -radial-gradient(center, ellipse cover,  rgba(0,0,0,0) 0%,rgba(0,0,0,0.65) 100%); 
filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#00000000', endColorstr='#a6000000',GradientType=0 );
}

#logo {
background: transparent url(http://i47.tinypic.com/14e510x.png) no-repeat!important;
}

#topmenu li.selected a {
background-color: #607A9F;
}

#topmenu li a{
color: #1C65C2;
}

#topmenu li a:hover {
color: #87AEE0;
}

#tabmenu li.active a {
background: #607A9F;
}

#tabmenu li a {
background: #ACBACF;
}

#tabmenu li a:hover {
background: #9EBAE0;
}

h1, h2, h3, h4, h5 {
color: #545350;
}

.wittetekst h4, #userbox_loggedin .fr a, #login_link, #register_link {
color: white;
}

.wittetekst h4:hover, #userbox_loggedin .fr a:hover, #login_link:hover, #register_link:hover {
color: #1C65C2;
}

.wittetekst a:hover {
color: #1C65C2;
}

a, #SUBreplies a, .fr a {
color: #1C65C2;
}

a:hover, #SUBreplies a:hover, .fr a:hover {
color: #87AEE0;
}

a.imgbutton {
color: #FFFFFF;
}

a.imgbutton:hover {
color: #4566A0;
}

.lijstitem a {
color: #555;
}

.lijstitem a:hover{
color: #555;
}

a.premsub:hover {
color: #000000;
}

#submenucontainer {
background: transparent url(http://i1221.photobucket.com/albums/dd472/5xt/subnav_bg-deselect.png);
}

th.bluerow {
background: url(http://i1221.photobucket.com/albums/dd472/5xt/subnav_bg-deselect.png);
}

.message {
background: #D8DEE7 url(http://bierdopje.com/g/if/icons_png/email.png) no-repeat 10px 3px;
border: 1px solid #0741BD;
}

.info {
background: #D8DEE7 url(http://bierdopje.com/g/if/icons_png/information.png) no-repeat 10px 3px;
border: 1px solid #0741BD;
}

.question {
background: #D8DEE7 url(http://bierdopje.com/g/if/question.png) no-repeat 10px 3px;
border: 1px solid #0741BD;
}

.block .header .wrap {
background: #607A9F url(http://i1221.photobucket.com/albums/dd472/5xt/submenubg.png) repeat-x left -48px;
}

#nav, #nav ul {
background-color: #FFFFFF;
}

.container_16 .grid_15 {
width: 980px;
}

.searchblock {
margin-top: -32px;
}

.headband {
background-color: #536EA7;
}

#submenu ul li a:hover {
color:#fff;
background:transparent url(http://i1221.photobucket.com/albums/dd472/5xt/submenu_over.png) no-repeat top right;
}

#topmenu li.selected a, #topmenu li.selected a:hover {
background-color: #607A9F;
}

th.forumkop, #SUBreplies th.forumkop {
background-image: url(http://i1221.photobucket.com/albums/dd472/5xt/header-r.png);
}

#pagination .active {
background: #607A9F;
border: 1px #0741BD;
}

#pagination .spacer {
background: #D8DEE7;
border: 1px #0741BD;
}

#pagination a:link, #pagination a:visited {
color: #4566A0;
}

#pagination a {
color: #555;
border: solid 1px #808080;
}

#pagination a:hover {
color: #0741BD;
border: solid 1px #555;
}

.markItUpContainer {
border: 1px solid #CDCDCD;
}

.markItUpEditor {
border: 3px solid #808080;
}

.button {
background: url(http://i1221.photobucket.com/albums/dd472/5xt/button-l.png) no-repeat left top;
}

.button a {
background: url(http://i1221.photobucket.com/albums/dd472/5xt/button-r.png) no-repeat right top;
}

.button a:hover {
color: #FFFFFF;
}

.loginbox .button {
background: transparent url(http://i1221.photobucket.com/albums/dd472/5xt/btn-lblue.png) no-repeat 0 0;
}

table.kalender td, table.kalender th {
background: url(http://i1221.photobucket.com/albums/dd472/5xt/cells-1.png);
}

#footer {
background: #D8DEE7 url(http://i1221.photobucket.com/albums/dd472/5xt/bgr-footer.png) repeat-x;
}

.grid_16 p {
color: #808080;
}

#footer, #footer a {
color: #1C65C2;
}

#footer, #footer a:hover {
color: #87AEE0;
}
}
