css:
@-moz-document domain("teltarif.de") {
body, div, input, td, p, h1, h2, h3, h4, #lxnews, a, .arial1, .arial2, .arial3, .arial35, li, .roman4, .roman3, .roman2, .roman1 {font-family: lucida sans, sans-serif !important;}
p {color: #444 !important; font-size: 11pt !important; line-height: 18pt;}
h1 {font-size: 24pt !important; text-align: left !important;}
h2 {font-size: 20pt !important; text-align: left !important;}
h3 {font-size: 18pt !important; text-align: left !important;}
h4 {font-size: 14pt !important; text-align: left !important;}
a, a:link {color: #872 !important; text-decoration: none !important;}
a:hover {color: #650 !important;}

table[width*="760"] {width: 900px !important; }
td[width*="77"] {width: 260px !important; color: #555; background: #fff !important; font-weight: bold !important;}
table#lxheader, tr[bgcolor*="#FFCC00"], td[bgcolor*="#FFCC00"] {background: #fe7 !important;}

td.gemi {background: #fe7 !important;}
td.gemi a {color: #222 !important;}

td.grli, td.gr1, td.gr2, td.gr3 {background: #eee !important; }
td.grli:hover, td.gr1:hover, td.gr2:hover, td.gr3:hover {background: #ddd !important; }
td.grli a, td.gr1 a, td.gr2 a, td.gr3 a {color: #666 !important; }
td.grli a:hover, td.gr1 a:hover, td.gr2 a:hover, td.gr3 a:hover {color: #222 !important; }

.teabox, .teaboxh, .teaboxe i {background: #eee !important; background-image: none !important;}
.teabox a {color: #666 !important;}
.teabox a:hover {color: #222 !important;}
.teaboxh {padding: 10px 0px;}
.teaboxc, .teaboxe b {border: none !important; background: #fafafa !important; }
}
