css:
@-moz-document domain("bierdopje.com") {
body.main_body {
background: #607A9F url(http://i1221.photobucket.com/albums/dd472/5xt/bgr-footer.png) repeat-x !important;
background-attachment: fixed !important;
background-image: -moz-radial-gradient(center, ellipse cover,  rgba(0,0,0,0) 0%, rgba(0,0,0,0.65) 100%) !important; 
background-image: -webkit-gradient(radial, center center, 0px, center center, 100%, color-stop(0%,rgba(0,0,0,0)), color-stop(100%,rgba(0,0,0,0.65))) !important; 
background-image: -webkit-radial-gradient(center, ellipse cover,  rgba(0,0,0,0) 0%,rgba(0,0,0,0.65) 100%) !important; 
background-image: -o-radial-gradient(center, ellipse cover,  rgba(0,0,0,0) 0%,rgba(0,0,0,0.65) 100%) !important; 
background-image: -ms-radial-gradient(center, ellipse cover,  rgba(0,0,0,0) 0%,rgba(0,0,0,0.65) 100%) !important; 
background-image: -radial-gradient(center, ellipse cover,  rgba(0,0,0,0) 0%,rgba(0,0,0,0.65) 100%) !important; 
filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#00000000', endColorstr='#a6000000',GradientType=0 ) !important;
}

#logo {
background: transparent url(http://i47.tinypic.com/14e510x.png) no-repeat !important;
}

#topmenu li.selected a {
background-color: #607A9F !important;
}

#topmenu li a{
color: #1C65C2 !important;
}

#topmenu li a:hover {
color: #87AEE0 !important;
}

#tabmenu li.active a {
color: #000000 !important;
background: #607A9F !important;
}

#tabmenu li a {
background: #ACBACF !important;
}

#tabmenu li a:hover {
background: #9EBAE0 !important;
}

.headband div.headband_filler h1, h1.block_title {
color: #FFFFFF !important;
}

h1, h2, h4 {
color: #545350 !important;
}

h3, h5 {
color: #000000 !important;
}

h3.forumHeader {
color: #545350 !important;
}

#submenu ul li a, #topmenu li.selected a, #topmenu li.selected a:hover {
    color: #FFFFFF !important;
}

#submenu ul li a.selected, #submenu ul li a.selected:hover {
color: #000000 !important;
}

.wittetekst h4, .wittetekst, #userbox_loggedin .fr a, #login_link, #register_link {
color: white !important;
}

.wittetekst h4:hover, #userbox_loggedin .fr a:hover, #login_link:hover, #register_link:hover {
color: #1C65C2 !important;
}

.wittetekst a:hover, .fr a:hover {
color: #1C65C2 !important;
}

a, #SUBreplies a, .fr a {
color: #1C65C2 !important;
}

a:hover, #SUBreplies a:hover, .fr a:hover {
color: #96AEE0 !important;
}

a.imgbutton {
color: #FFFFFF !important;
}

a.imgbutton:hover {
color: #4566A0 !important;
}

a.user, a.user:hover, #subreplies a.user, #subreplies a.user:hover {
color: #5C5850 !important;
}

a.crew_mod, a.crew_mod:hover, #subreplies a.crew_mod, #subreplies a.crew_mod:hover {
color: #639A3E !important;
}

a.vertaler, a.vertaler:hover, #subreplies a.vertaler, #subreplies a.vertaler:hover {
color: #47BF0B !important;
}

a.syncer, a.syncer:hover, #subreplies a.syncer, #subreplies a.syncer:hover {
color: #B100CF!important;
}

a.crew_manager, a.crew_manager:hover, #subreplies a.crew_manager, #subreplies a.crew_manager:hover {
color: #F20000 !important;
}

a.crew_admin, a.crew_admin:hover, #subreplies a.crew_admin, #subreplies a.crew_admin:hover {
color: #04B9ED !important;
}

a.premsub {
color: red !important;
}

a.premsub:hover {
color: #000000 !important;
}

#submenucontainer {
background: transparent url(http://i1221.photobucket.com/albums/dd472/5xt/subnav_bg-deselect.png) !important;
}

th.bluerow {
background: url(http://i1221.photobucket.com/albums/dd472/5xt/subnav_bg-deselect.png) !important;
}

.message {
background: #D8DEE7 url(http://bierdopje.com/g/if/icons_png/email.png) no-repeat 10px 3px !important;
border: 1px solid #0741BD !important;
}

.info {
background: #D8DEE7 url(http://bierdopje.com/g/if/icons_png/information.png) no-repeat 10px 3px !important;
border: 1px solid #0741BD !important;
}

.question {
background: #D8DEE7 url(http://bierdopje.com/g/if/question.png) no-repeat 10px 3px !important;
border: 1px solid #0741BD !important;
}

.block .header .wrap {
background: #607A9F url(http://i1221.photobucket.com/albums/dd472/5xt/submenubg.png) repeat-x left -48px !important;
}

#nav, #nav ul {
background-color: #FFFFFF !important;
}

.container_16 .grid_15 {
width: 980px !important;
}

.searchblock {
margin-top: -32px !important;
}

.headband {
background-color: #536EA7 !important;
}

#submenu ul li a:hover {
color:#fff !important;
background:transparent url(http://i1221.photobucket.com/albums/dd472/5xt/submenu_over.png) no-repeat top right !important;
}

#topmenu li.selected a, #topmenu li.selected a:hover {
background-color: #607A9F !important;
}

th.forumkop, #SUBreplies th.forumkop {
background-image: url(http://i1221.photobucket.com/albums/dd472/5xt/header-r.png) !important;
}

#pagination .active {
background: #607A9F !important;
border: 1px #0741BD !important;
}

#pagination .spacer {
background: #D8DEE7 !important;
border: 1px #0741BD !important;
}

#pagination a:link, #pagination a:visited {
color: #4566A0 !important;
}

#pagination a {
color: #555 !important;
border: solid 1px #808080 !important;
}

#pagination a:hover {
color: #0741BD !important;
border: solid 1px #555 !important;
}

.markItUpContainer {
border: 1px solid #CDCDCD !important;
}

.markItUpEditor {
border: 3px solid #808080 !important;
}

.button {
background: url(http://i1221.photobucket.com/albums/dd472/5xt/button-l.png) no-repeat left top !important;
}

.button a {
color: #FFFFFF !important;
background: url(http://i1221.photobucket.com/albums/dd472/5xt/button-r.png) no-repeat right top !important;
}

.button a:hover {
color: #FFFFFF !important;
}

.loginbox .button {
background: transparent url(http://i1221.photobucket.com/albums/dd472/5xt/btn-lblue.png) no-repeat 0 0 !important;
}

table.kalender td, table.kalender th {
background: url(http://i1221.photobucket.com/albums/dd472/5xt/cells-1.png) !important;
}

table.kalender td:hover, table.kalender th:hover {
background-position: 0 -81px !important;
}

table.kalender td.date_has_event {
background-position: 162px 0 !important;
}

table.kalender td.date_has_event:hover {
background-position: 162px -81px !important;
}

table.kalender td.today {
background-position: 81px 0 !important;
}

table.kalender td.today:hover {
background-position: 81px -81px !important;
}

table.kalender td.padding {
background: url(http://www.bierdopje.com/g/if/calendar/calpad.jpg) !important;
}

#footer {
background: #D8DEE7 url(http://i1221.photobucket.com/albums/dd472/5xt/bgr-footer.png) repeat-x !important;
}

.grid_16 p {
color: #808080 !important;
}

#footer, #footer a {
color: #1C65C2 !important;
}

#footer, #footer a:hover {
color: #87AEE0 !important;
}

.lijstitem a {
color: #555 !important;
}

.lijstitem a:hover {
color: #555 !important;
}

.lijstitem a:active, .lijstitem a:visited {
color: #8F8F8F !important;
}
}
