css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("u-cursos.cl") {

h1#ucursos {background-color: transparent  !important;background-image: url( "http://imageshack.us/a/img525/9048/escudouniversidaddechil.png" ) !important;border-bottom: 5px solid #018fbe !important;}
h1.modulo {color: #018fbe !important;border-bottom: 1px solid #018fbe !important;}
h2.ucursos {color: #018fbe !important;border-bottom: 5px solid #018fbe !important;background-image: url("https://www.u-cursos.cl/icei/diseno/images/logos/ucursos3.png") !important;}

ul.modulo li {border-right: 1px solid #018fbe !important;}
ul.modulo li a {color: rgb(149, 149, 149) !important;}
ul.modulos li.sel, ul.modulos li.sel:hover {border-color: #018fbe !important;}
ul.modulo li.sel a {background: #018fbe !important;color: rgb(255,255,255) !important;}

a {color: #333399 !important;}
a:hover {color: #5969FC !important;}

div#izquierda div ul li a {color: rgb(133, 133, 133) !important;}
div#izquierda div h1 a {color: rgb(203, 154, 45) !important;}
div#izquierda div.off h1 a {color: rgb(165, 165, 165) !important;}

}
