css:
@-moz-document domain("irccloud.com") {

::selection{
    background:#9a9a9a;
    color:#ccc
}
.viewport{
    background: #121212;
    font-family:monospace
}
.viewport *{
    color:#ffffd7
}
.viewport .self{
    background:#353535!important
}
body.app,#headercell *,.viewport,div.shim,td.nickcell a.nick,td.inputcell textarea,div.log div.messageRow,div.log span.author,div.log div.me a.user,input,textarea,button,select{
    font-family:"Ubuntu Mono",Consolas,monospace
}
body.app,#headercell *,.viewport{
    background:#121212!important;
    color:#ffffd7!important;
    border-color:#444!important
}
h1#title span.logo,h1#title span.beta{
    display:none
}
ul.memberList,div.memberwrapper{
    background:#121212;
    border-width:0
}
ul.memberList li h2,ul.memberList li h2 span.memberCount{
    color:#ffffd7!important;
    border-width:0
}
ul.memberList li.owner ul,ul.memberList li.owner h2{
    background:#454545
}
ul.memberList li.admin ul,ul.memberList li.admin h2{
    background:#343434
}
ul.memberList li.ops ul,ul.memberList li.ops h2{
    background:#232323
}
ul.memberList li.halfops ul,ul.memberList li.halfops h2{
    background:#121212
}
ul.memberList li.voiced ul,ul.memberList li.voiced h2{
    background:#010101
}
ul.memberList li.members ul,ul.memberList li.members h2{
    background:#121212
}
ul.memberList li ul{
    border-color:#555!important
}
ul.memberList li h2{
    font-weight:bold
}
.bufferHead{
    background-color:#333!important;
    color:#ffffd7!important;
    border-width:0
}
td.statuscell div.status h2.bufferHeading span.label,#buffers .active a.buffer{
    color:#efefef
}
td.statuscell div.status h2.bufferHeading{
    color:#eee
}
ul.memberList a{
    color:#ffffd7
}
div.log div.status{
    background:#565656
}
div.log span.message a.bufferLink.user{
    color:#dedede
}
div.log span.message a.bufferLink.user:hover{
    color:#ededed
}
div.log div.lastSeen span.text,div.log div.bottomSeen span,div.log div.focusSeen span,div.log div.fetch span.text{
    background:#111;
    color:#ffffd7
}
div.log div.highlight{
    background:#af005f !important;
}
div.log div.groupedJoinPart{
    background:#494949;
    border-color:#767676
}
div.log div.groupedJoinPart:hover,div.log div.groupedJoinPart.expanded{
    background:#767676;
    border-color:#878787
}
div.log div.groupedJoinPart.expanded:hover{
    background:#878787;
    border-color:#8a8a8a
}
div.log span.prefix{
    color:#bbb;
    font-family:arial,sans-serif
}
div.log div.dateChange p,div.dateWrapper table,div.dateWrapper td{
    border-width:0;
    color:#212121;
    background:#bababa
}
div.log div.dateChange p{
    padding-bottom:5px
}
td.bufferinputcell{
    background:#010101
}
td.nickcell{
    background:#000;
    border-width:0
}
td.nickcell a.nick{
    color:#ffffd7
}
div.nickinputwrapper{
    border-width:0;
    background:#121212
}
table.nickinput{
    border-radius:0;
    -webkit-border-radius:0;
    -moz-border-radius:0
}
::-webkit-scrollbar-thumb{
    
}
#channelContextMenu ul.modeList{
    color:#777
}
*{
    border-radius:0!important;
    -webkit-border-radius:0!important;
    -moz-border-radius:0!important;
    box-shadow:none;
    -webkit-box-shadow:none
}
.extras span.extrasBar{
    background:#005faf;
    color:#fafafa
}
.extras span.extrasBar:hover{
    background:#2020d0;
    color:#dcdcdc
}
.extras span.extrasDismiss{
    background:#0055ff
}
.extras span.extrasDismiss:hover{
    background:#0090ff
}
td.inputcell,td.inputcell textarea{
    background:#121212;
    color:#ffffd7
}
#buffers h2 a.join{
    background:url("data:image/png;base64,") center center no-repeat;
    opacity:0.8
}
#buffers h2 a.join:hover{
    background-position:center center;
    opacity:1
}
div.log div.groupedJoinPart span.mode{
    color:#9e9e9e
}
#buffers h2 a.buffer{
    background-color:#464646;
    background-image:none;
    color:#eee;
    font-weight:bold;
    border-width:0
}
#buffers h2 a.buffer:hover{
    background-color:#373737
}
table.buffer{
    border-width:0
}
::-webkit-scrollbar-thumb{
    background:#313131;
    border-width:0;
    border-radius:0;
    -webkit-border-radius:0
}
::-webkit-scrollbar-track-piece{
    background:#646464;
    border-width:0;
    border-radius:0;
    -webkit-border-radius:0
}
::-webkit-scrollbar-thumb:inactive{
    background:#535353
}
::-webkit-scrollbar-track-piece:inactive{
    background:#868686
}
.sidebarIndicator,.sidebarIndicatorBadged{
    background:#005faf;
    border-width:0
}
.sidebarIndicatorBadged{
    background:#f00000
}
#buffers .selected a.buffer,#buffers .selected a.buffer:hover,#buffers .active.selected a.buffer,#buffers .active.selected a.buffer:hover{
    background-color:#13354a;
    border-width:0
}
#buffers li.connection{
    background:#121212
}
#buffers a.buffer{
    color:#eee
}
#buffers a.buffer:hover{
    background-color:#757575
}
#buffers ul.buffers .active.unread a.buffer,#buffers h2.active.unread{
    border-left-color:#13354a
}
#buffers p.archiveToggle a:hover{
    background:#858585;
    color:#ffffd7
}
p#addNetwork a,p#addNetwork a:hover,p#addNetwork.selected a,p#addNetwork.selected a:hover{
    background:transparent;
    padding:0 4px;
    margin:0;
    border-width:0;
    color:#ffffd7
}
p#addNetwork.selected a,p#addNetwork.selected a:hover{
    background:#333
}
#footer,#headercell a,h1#title a,#footer a{
    color:#ffffd7
}
p#addNetwork a:hover,#headercell a:hover,h1#title a:hover,#footer a:hover{
    color:#bbb
}
div.log span.author a,div.log div.me span.message a.author,td.nickcell a.nick{
    color:#ddd
}
button b.bullet{
    color:#eee
}
td.statuscell div.status p.buttons button span,.overlaycontainer button.confirm span,.overlaycontainer button.close span,button.action span,button span,button a,td.statuscell div.status p.buttons button.active span,.overlaycontainer button.confirm.active span,.overlaycontainer button.close.active span,button.action.active span,button span:hover,button a:hover,button:hover span,button:hover a,button.action:hover span,button.action:hover a{
    border-color:#121212;
    background:#121212;
    color:#ffffd7;
    box-shadow:none!important;
    -webkit-box-shadow:none!important
}
#sidebar #limits{
    background:#333;
    color:#efefef
}
#limits p#upgradeLink a{
    background:#85de85;
    color:#121212
}
#limits p#upgradeLink a:hover{
    background:#90ef90;
    color:#121212
}
#buffers ul.archives{
    background:#565656
}
#buffers ul.archives a:hover{
    background:#777;
    color:#ccc
}
#buffers p.openArchiveToggle a,#buffers p.openArchiveToggle a:hover{
    background:#777;
    color:#eee;
    font-weight:bold
}
.viewport .overlaycontainer{
    background:#555;
    border-width:0
}
div.log div.notice,div.log div.joinPartGroup.expanded div.messageRow, div.log div.warning{
    background:#444
}
div.log div.groupedLines{
    background:#262626 !important;
	color:#bcbcbc!important;
    border-width:0
}
div.log div.chat a,div.log div.status a,div.log div.notice a,div.log div.groupedLines a,div.log div.warning a,div.log div.error a,div.log div.table a,div.log div.me a{
    color:#afd75f;
}
form.addNetworkForm,.mainContainer{
    background:#121212;
    border-color:#121212
}
#inviteForm h1,.explanation,.mainForm,.mainContent{
    color:#ffffd7
}
body.disconnected,body.disconnected td#headercell *{
    background:orange!important
}
.overlayTable tr.even td{
    background:#767676
}
.overlay table thead th,.overlay table thead td{
    background:#424242
}
.overlay .heading h2{
    font-weight:bold
}
.overlay table td,.overlay table th{
    border-left-color:#252525
}
#sidebar .info{
    background:#212121
}
#sidebar .info p.button,#sidebar .info p.more{
    background:#343434
}
#sidebar .info p.form .input,#sidebar .info p.form .input:focus{
    border-color:#555
}
form.addNetworkForm h2 span,form.addNetworkForm h2 a{
    background-color:#121212
}
#memberContextMenu div.userContext{
    color:#121212
}
.form .input{
    color:#121212
}
.connection.disconnected{
    opacity:0.75
}
body> pre{
    -webkit-user-select:text
}

a.bufferLink {
	color: #005faf !important;
}

div.row.messageRow.me span.message {
	color: #ff8700 !important;
}

}
