css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.bonjourmadame.fr") {

#header, .banner { float: right; }
#header { z-index: 1 !important; }
#page { padding-top: 10px !important; }

#banner { width: 180px; }
.banner { width: 180px; margin-top: 0 !important; }
.banner a:not(:first-child) { display: none; }

.inner { text-indent: -800px; border-bottom: 0 none !important; }
.inner > font { display: none; }
.inner > ul { text-indent: -0; }

#content { margin-right:40px !important; }
#blog-info > .description > div:last-child { width:250px !important; }

body { width:800px; transform:scale(1.7); transform-origin:0 0 }

}
