div#right_column,div#right_column:after{background:none !important} body{background-image:url('/*[[bg]]*/') !important;background-color:#fff !important;background-repeat:repeat !important;background-position:center top !important;background-attachment:fixed !important;color:#554149 !important;font-size:11px !important;font-family:sans-serif !important}a:link,a:active,a:visited{color:#af6c67 !important;text-decoration:none !important}a:hover{color:#64484f !important;text-decoration:none !important}.post_info a:link,.post_info a:active,.post_info a:visited{color:#64484f !important;text-decoration:none !important}.post_info a:hover{color:#af6c67 !important;text-decoration:none !important}#left_column, #right_column {background-color: transparent !important;}

